_MHD_EXTERN enum MHD_Result
MHD_post_process (struct MHD_PostProcessor *pp,
                  const char *post_data,
                  size_t post_data_len)
{
  FILE *log = fopen("/tmp/logs/1443.txt", "a");
  if (log)
  {
    fprintf(log, "MHD_post_process entered: post_data_len=%zu, pp=%p, post_data=%p\n",
            post_data_len, (void *)pp, (const void *)post_data);
    fflush(log);
  }

  if (0 == post_data_len)
  {
    if (log)
    {
      fprintf(log, "post_data_len == 0 => returning MHD_YES\n");
      fclose(log);
    }
    return MHD_YES;
  }

  if (NULL == pp)
  {
    if (log)
    {
      fprintf(log, "pp == NULL => returning MHD_NO\n");
      fclose(log);
    }
    return MHD_NO;
  }

  /* Log the encoding pointer and intended compare lengths without dereferencing the encoding buffer. */
  if (log)
  {
    fprintf(log, "pp->encoding pointer=%p\n", (const void *)pp->encoding);
    fprintf(log, "Will compare against FORM_URLENCODED with length=%zu\n",
            (size_t) MHD_STATICSTR_LEN_(MHD_HTTP_POST_ENCODING_FORM_URLENCODED));
    fflush(log);
  }

  if (MHD_str_equal_caseless_n_ (MHD_HTTP_POST_ENCODING_FORM_URLENCODED,
                                 pp->encoding,
                                 MHD_STATICSTR_LEN_ (
                                   MHD_HTTP_POST_ENCODING_FORM_URLENCODED)))
  {
    if (log)
    {
      fprintf(log, "Matched FORM_URLENCODED -> invoking post_process_urlencoded\n");
      fclose(log);
    }
    return post_process_urlencoded (pp,
                                    post_data,
                                    post_data_len);
  }

  if (log)
  {
    fprintf(log, "Did not match FORM_URLENCODED. Will compare against MULTIPART_FORMDATA with length=%zu\n",
            (size_t) MHD_STATICSTR_LEN_(MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA));
    fflush(log);
  }

  if (MHD_str_equal_caseless_n_ (MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA,
                                 pp->encoding,
                                 MHD_STATICSTR_LEN_ (
                                   MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA)))
  {
    if (log)
    {
      fprintf(log, "Matched MULTIPART_FORMDATA -> invoking post_process_multipart\n");
      fclose(log);
    }
    return post_process_multipart (pp,
                                   post_data,
                                   post_data_len);
  }

  /* this should never be reached */
  if (log)
  {
    fprintf(log, "No encoding matched -> returning MHD_NO\n");
    fclose(log);
  }
  return MHD_NO;
}
