_MHD_EXTERN enum MHD_Result
MHD_post_process (struct MHD_PostProcessor *pp,
                  const char *post_data,
                  size_t post_data_len)
{
  /* Open log file for probes. Use append mode so multiple calls accumulate evidence. */
  FILE *log = fopen("/tmp/logs/1445.txt", "a");
  if (log)
    {
      fprintf(log, "ENTER MHD_post_process: pp=%p post_data=%p post_data_len=%zu\n",
              (void *) pp, (const void *) post_data, post_data_len);
      /* Log brief view of encoding string if available (guard against NULL). */
      if (pp && pp->encoding)
        fprintf(log, "  pp->encoding='%s'\n", pp->encoding);
      else if (pp)
        fprintf(log, "  pp->encoding=NULL\n");
      fflush(log);
    }

  if (0 == post_data_len)
    {
      if (log)
        {
          fprintf(log, "  post_data_len == 0 -> returning MHD_YES\n\n");
          fclose(log);
        }
      return MHD_YES;
    }

  if (NULL == pp)
    {
      if (log)
        {
          fprintf(log, "  pp == NULL -> returning MHD_NO\n\n");
          fclose(log);
        }
      return MHD_NO;
    }

  /* Note for auditors: in this function post_data is declared as 'const char *'.
     If post_process_urlencoded is declared to accept 'char *' (non-const), then
     passing post_data here will discard the const qualifier at the call site
     (a compile-time qualifier mismatch). We record the call site and arguments
     to provide evidence of that scenario. */
  if (log)
    {
      fprintf(log, "  NOTE: local post_data type is 'const char *' (address %p)\n",
              (const void *) post_data);
      fflush(log);
    }

  if (MHD_str_equal_caseless_n_ (MHD_HTTP_POST_ENCODING_FORM_URLENCODED,
                                 pp->encoding,
                                 MHD_STATICSTR_LEN_ (
                                   MHD_HTTP_POST_ENCODING_FORM_URLENCODED)))
    {
      if (log)
        {
          fprintf(log, "  Branch: form-urlencoded -> calling post_process_urlencoded(pp, post_data, post_data_len)\n");
          /* For additional evidence, log the first up-to-64 bytes of post_data if non-NULL. */
          if (post_data && post_data_len > 0)
            {
              size_t dump_len = post_data_len < 64U ? post_data_len : 64U;
              /* Print as a C string safely by ensuring a terminating NUL in a buffer. */
              char buf[65];
              size_t i;
              for (i = 0U; i < dump_len; ++i)
                buf[i] = post_data[i];
              buf[dump_len] = '\0';
              fprintf(log, "    post_data[0..%zu] (as string-safe)='%s'\n", dump_len, buf);
            }
          else
            fprintf(log, "    post_data is NULL or empty\n");
          fflush(log);
        }

      /* Preserve original behavior: call the handler and log its result. */
      enum MHD_Result res = post_process_urlencoded (pp,
                                                     post_data,
                                                     post_data_len);
      if (log)
        {
          fprintf(log, "  post_process_urlencoded returned %d\n\n", (int) res);
          fclose(log);
        }
      return res;
    }

  if (MHD_str_equal_caseless_n_ (MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA,
                                 pp->encoding,
                                 MHD_STATICSTR_LEN_ (
                                   MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA)))
    {
      if (log)
        {
          fprintf(log, "  Branch: multipart/form-data -> calling post_process_multipart(pp, post_data, post_data_len)\n");
          fflush(log);
        }

      enum MHD_Result res = post_process_multipart (pp,
                                                    post_data,
                                                    post_data_len);
      if (log)
        {
          fprintf(log, "  post_process_multipart returned %d\n\n", (int) res);
          fclose(log);
        }
      return res;
    }

  /* this should never be reached */
  if (log)
    {
      fprintf(log, "  Unexpected encoding -> returning MHD_NO\n\n");
      fclose(log);
    }
  return MHD_NO;
}
