_MHD_EXTERN enum MHD_Result
MHD_post_process (struct MHD_PostProcessor *pp,
                  const char *post_data,
                  size_t post_data_len)
{
  FILE *log = fopen("/tmp/logs/1445.txt", "a");
  if (log)
  {
    fprintf(log, "MHD_post_process entered: post_data_len=%zu, pp=%p\n",
            post_data_len, (void *)pp);
    if (pp != NULL)
    {
      /* Log encoding pointer and a safe prefix of the string if available */
      if (pp->encoding != NULL)
      {
        /* print up to 256 characters to avoid very long output or reading invalid memory */
        fprintf(log, "pp->encoding=%p, encoding=\"%.256s\"\n",
                (void *)pp->encoding, pp->encoding);
      }
      else
      {
        fprintf(log, "pp->encoding=NULL\n");
      }
    }
    else
    {
      fprintf(log, "pp is NULL\n");
    }
    fflush(log);
  }

  if (0 == post_data_len)
  {
    if (log)
    {
      fprintf(log, "post_data_len == 0 -> returning MHD_YES\n");
      fclose(log);
    }
    return MHD_YES;
  }
  if (NULL == pp)
  {
    if (log)
    {
      fprintf(log, "pp == NULL -> returning MHD_NO\n");
      fclose(log);
    }
    return MHD_NO;
  }

  /* Check for application/x-www-form-urlencoded */
  {
    int match = MHD_str_equal_caseless_n_ (MHD_HTTP_POST_ENCODING_FORM_URLENCODED,
                                           pp->encoding,
                                           MHD_STATICSTR_LEN_ (
                                             MHD_HTTP_POST_ENCODING_FORM_URLENCODED));
    if (log)
    {
      fprintf(log, "compared against FORM_URLENCODED -> match=%d\n", match);
      fflush(log);
    }
    if (match)
    {
      enum MHD_Result res = post_process_urlencoded (pp,
                                                     post_data,
                                                     post_data_len);
      if (log)
      {
        fprintf(log, "post_process_urlencoded returned=%d\n", (int)res);
        fclose(log);
      }
      return res;
    }
  }

  /* Check for multipart/form-data */
  {
    int match = MHD_str_equal_caseless_n_ (MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA,
                                           pp->encoding,
                                           MHD_STATICSTR_LEN_ (
                                             MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA));
    if (log)
    {
      fprintf(log, "compared against MULTIPART_FORMDATA -> match=%d\n", match);
      fflush(log);
    }
    if (match)
    {
      enum MHD_Result res = post_process_multipart (pp,
                                                    post_data,
                                                    post_data_len);
      if (log)
      {
        fprintf(log, "post_process_multipart returned=%d\n", (int)res);
        fclose(log);
      }
      return res;
    }
  }

  /* this should never be reached */
  if (log)
  {
    fprintf(log, "no known encoding matched -> returning MHD_NO\n");
    fclose(log);
  }
  return MHD_NO;
}
