_MHD_EXTERN enum MHD_Result
MHD_queue_auth_required_response3 (struct MHD_Connection *connection,
                                   const char *realm,
                                   const char *opaque,
                                   const char *domain,
                                   struct MHD_Response *response,
                                   int signal_stale,
                                   enum MHD_DigestAuthMultiQOP mqop,
                                   enum MHD_DigestAuthMultiAlgo3 malgo3,
                                   int userhash_support,
                                   int prefer_utf8)
{
  struct DigestAlgorithm da;
  char *buf_ptr;
  enum MHD_Result ret;

  /* Log entry and parameter state to help reproduce/trace the reported violation. */
  {
    FILE *logf = fopen("/tmp/logs/3715.txt", "a");
    if (logf)
    {
      fprintf(logf,
              "ENTER MHD_queue_auth_required_response3: connection=%p realm=%p opaque=%p domain=%p response=%p signal_stale=%d mqop=%d malgo3=%d userhash_support=%d prefer_utf8=%d\n",
              (void *) connection,
              (const void *) realm,
              (const void *) opaque,
              (const void *) domain,
              (void *) response,
              signal_stale,
              (int) mqop,
              (int) malgo3,
              userhash_support,
              prefer_utf8);
      fclose(logf);
    }
  }

  buf_ptr = NULL;
  digest_setup_zero (&da);
  ret = queue_auth_required_response3_inner (connection,
                                             realm,
                                             opaque,
                                             domain,
                                             response,
                                             signal_stale,
                                             mqop,
                                             malgo3,
                                             userhash_support,
                                             prefer_utf8,
                                             &buf_ptr,
                                             &da);

  /* Log result and intermediate state (buf_ptr) after calling inner function. */
  {
    FILE *logf = fopen("/tmp/logs/3715.txt", "a");
    if (logf)
    {
      fprintf(logf,
              "AFTER inner: ret=%d buf_ptr=%p\n",
              (int) ret,
              (void *) buf_ptr);
      fclose(logf);
    }
  }

  digest_deinit (&da);

  if (NULL != buf_ptr)
  {
    /* Log the free action for buf_ptr so tests can verify ownership and lifetime. */
    FILE *logf = fopen("/tmp/logs/3715.txt", "a");
    if (logf)
    {
      fprintf(logf, "FREE buf_ptr: %p\n", (void *) buf_ptr);
      fclose(logf);
    }
    free (buf_ptr);
  }

  return ret;
}
