_MHD_EXTERN enum MHD_Result
MHD_queue_response (struct MHD_Connection *connection,
                    unsigned int status_code,
                    struct MHD_Response *response)
{
  struct MHD_Daemon *daemon;
  bool reply_icy;
  FILE *logf = NULL;

  /* Open log file early to capture as much information as possible. */
  logf = fopen("/tmp/logs/1075.txt", "a");
  if (logf)
  {
    fprintf(logf, "ENTER MHD_queue_response: connection=%p response=%p status_code=%u\n",
            (void *)connection, (void *)response, status_code);
    fflush(logf);
  }

  if ((NULL == connection) || (NULL == response))
  {
    if (logf)
    {
      fprintf(logf, "EARLY RETURN: NULL parameter(s): connection=%p response=%p\n",
              (void *)connection, (void *)response);
      fclose(logf);
    }
    return MHD_NO;
  }

  daemon = connection->daemon;
  if ((! connection->in_access_handler) && (! connection->suspended) &&
      MHD_D_IS_USING_THREADS_ (daemon))
  {
    if (logf)
    {
      fprintf(logf, "EARLY RETURN: not in access handler, not suspended, using threads\n");
      fclose(logf);
    }
    return MHD_NO;
  }

  reply_icy = (0 != (status_code & MHD_ICY_FLAG));
  status_code &= ~MHD_ICY_FLAG;

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if ( (! connection->suspended) &&
       MHD_D_IS_USING_THREADS_ (daemon) &&
       (! MHD_thread_handle_ID_is_current_thread_ (connection->tid)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Attempted to queue response on wrong thread!\n"));
#endif
    if (logf)
    {
      fprintf(logf, "EARLY RETURN: attempted to queue response on wrong thread (tid mismatch)\n");
      fclose(logf);
    }
    return MHD_NO;
  }
#endif

  if (NULL != connection->rp.response)
  {
    if (logf)
    {
      fprintf(logf, "EARLY RETURN: response already set on connection (rp.response=%p)\n",
              (void *)connection->rp.response);
      fclose(logf);
    }
    return MHD_NO; /* The response was already set */
  }

  if ( (MHD_CONNECTION_HEADERS_PROCESSED != connection->state) &&
       (MHD_CONNECTION_FULL_REQ_RECEIVED != connection->state) )
  {
    if (logf)
    {
      fprintf(logf, "EARLY RETURN: wrong connection state (%d)\n", connection->state);
      fclose(logf);
    }
    return MHD_NO; /* Wrong connection state */
  }

  if (daemon->shutdown)
  {
    if (logf)
    {
      fprintf(logf, "EARLY RETURN: daemon is shutting down\n");
      fclose(logf);
    }
    return MHD_NO;
  }

#ifdef UPGRADE_SUPPORT
  if (NULL != response->upgrade_handler)
  {
    struct MHD_HTTP_Res_Header *conn_header;
    if (logf)
    {
      fprintf(logf, "Entering UPGRADE_SUPPORT block: response->upgrade_handler=%p\n",
              (void *)response->upgrade_handler);
      fflush(logf);
    }
    if (0 == (daemon->options & MHD_ALLOW_UPGRADE))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Attempted 'upgrade' connection on daemon without" \
                   " MHD_ALLOW_UPGRADE option!\n"));
#endif
      if (logf)
      {
        fprintf(logf, "EARLY RETURN: daemon does not allow upgrade (options=0x%08x)\n",
                daemon->options);
        fclose(logf);
      }
      return MHD_NO;
    }
    if (MHD_HTTP_SWITCHING_PROTOCOLS != status_code)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid status code for" \
                   " 'upgrade' response!\n"));
#endif
      if (logf)
      {
        fprintf(logf, "EARLY RETURN: invalid status code for upgrade: %u\n", status_code);
        fclose(logf);
      }
      return MHD_NO;
    }
    if (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid response" \
                   " without \"Connection\" header!\n"));
#endif
      if (logf)
      {
        fprintf(logf, "EARLY RETURN: response lacks Connection header (flags_auto=0x%08x)\n",
                response->flags_auto);
        fclose(logf);
      }
      return MHD_NO;
    }
    /* Assignment under scrutiny for MISRA Rule 8.13: used read-only afterwards */
    conn_header = response->first_header;
    if (logf)
    {
      fprintf(logf, "Assigned conn_header = response->first_header -> %p\n",
              (void *)conn_header);
      if (conn_header)
      {
        fprintf(logf, "conn_header->header = %s\n",
                (conn_header->header) ? conn_header->header : "(null)");
        fprintf(logf, "conn_header->value  = %s\n",
                (conn_header->value) ? conn_header->value : "(null)");
      }
      fflush(logf);
    }
    mhd_assert (NULL != conn_header);
    mhd_assert (MHD_str_equal_caseless_ (conn_header->header,
                                         MHD_HTTP_HEADER_CONNECTION));
    if (! MHD_str_has_s_token_caseless_ (conn_header->value,
                                         "upgrade"))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid response" \
                   " without \"upgrade\" token in" \
                   " \"Connection\" header!\n"));
#endif
      if (logf)
      {
        fprintf(logf, "EARLY RETURN: Connection header does not contain token 'upgrade' (value=%s)\n",
                (conn_header && conn_header->value) ? conn_header->value : "(null)");
        fclose(logf);
      }
      return MHD_NO;
    }
    if (! MHD_IS_HTTP_VER_1_1_COMPAT (connection->rq.http_ver))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Connection \"Upgrade\" can be used only " \
                   "with HTTP/1.1 connections!\n"));
#endif
      if (logf)
      {
        fprintf(logf, "EARLY RETURN: Upgrade only allowed on HTTP/1.1 (http_ver=%d)\n",
                connection->rq.http_ver);
        fclose(logf);
      }
      return MHD_NO;
    }
  }
#endif /* UPGRADE_SUPPORT */
  if (MHD_HTTP_SWITCHING_PROTOCOLS == status_code)
  {
#ifdef UPGRADE_SUPPORT
    if (NULL == response->upgrade_handler)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used status code 101 \"Switching Protocols\" " \
                   "with non-'upgrade' response!\n"));
#endif /* HAVE_MESSAGES */
      if (logf)
      {
        fprintf(logf, "EARLY RETURN: 101 used without upgrade_handler\n");
        fclose(logf);
      }
      return MHD_NO;
    }
#else  /* ! UPGRADE_SUPPORT */
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Application used status code 101 \"Switching Protocols\", " \
                 "but this MHD was built without \"Upgrade\" support!\n"));
#endif /* HAVE_MESSAGES */
    if (logf)
    {
      fprintf(logf, "EARLY RETURN: 101 used but UPGRADE_SUPPORT not enabled in build\n");
      fclose(logf);
    }
    return MHD_NO;
#endif /* ! UPGRADE_SUPPORT */
  }
  if ( (100 > status_code) ||
       (999 < status_code) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Refused wrong status code (%u). " \
                 "HTTP requires three digits status code!\n"),
              status_code);
#endif
    if (logf)
    {
      fprintf(logf, "EARLY RETURN: refused wrong status code (%u)\n", status_code);
      fclose(logf);
    }
    return MHD_NO;
  }
  if (200 > status_code)
  {
    if (MHD_HTTP_VER_1_0 == connection->rq.http_ver)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Wrong status code (%u) refused. " \
                   "HTTP/1.0 clients do not support 1xx status codes!\n"),
                (status_code));
#endif
      if (logf)
      {
        fprintf(logf, "EARLY RETURN: HTTP/1.0 client does not support 1xx (http_ver=%d status=%u)\n",
                connection->rq.http_ver, status_code);
        fclose(logf);
      }
      return MHD_NO;
    }
    if (0 != (response->flags & (MHD_RF_HTTP_1_0_COMPATIBLE_STRICT
                                 | MHD_RF_HTTP_1_0_SERVER)))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Wrong status code (%u) refused. " \
                   "HTTP/1.0 reply mode does not support 1xx status codes!\n"),
                (status_code));
#endif
      if (logf)
      {
        fprintf(logf, "EARLY RETURN: response flags indicate HTTP/1.0 reply mode incompatible with 1xx (response->flags=0x%08x)\n",
                response->flags);
        fclose(logf);
      }
      return MHD_NO;
    }
  }
  if ( (MHD_HTTP_MTHD_CONNECT == connection->rq.http_mthd) &&
       (2 == status_code / 100) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Successful (%u) response code cannot be used to answer " \
                 "\"CONNECT\" request!\n"),
              (status_code));
#endif
    if (logf)
    {
      fprintf(logf, "EARLY RETURN: CONNECT method cannot be answered with 2xx (method=%d status=%u)\n",
              connection->rq.http_mthd, status_code);
      fclose(logf);
    }
    return MHD_NO;
  }

  if ( (0 != (MHD_RF_HEAD_ONLY_RESPONSE & response->flags)) &&
       (RP_BODY_HEADERS_ONLY < is_reply_body_needed (connection, status_code)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("HEAD-only response cannot be used when the request requires "
                 "reply body to be sent!\n"));
#endif
    if (logf)
    {
      fprintf(logf, "EARLY RETURN: HEAD-only response not allowed when body required (response->flags=0x%08x)\n",
              response->flags);
      fclose(logf);
    }
    return MHD_NO;
  }

#ifdef HAVE_MESSAGES
  if ( (0 != (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) &&
       (0 != (MHD_RAF_HAS_CONTENT_LENGTH & response->flags_auto)) )
  {
    MHD_DLOG (daemon,
              _ ("The response has application-defined \"Content-Length\" " \
                 "header. The reply to the request will be not " \
                 "HTTP-compliant and may result in hung connection or " \
                 "other problems!\n"));
  }
#endif

  MHD_increment_response_rc (response);
  if (logf)
  {
    fprintf(logf, "After increment: response refcount incremented\n");
    fflush(logf);
  }
  connection->rp.response = response;
  connection->rp.responseCode = status_code;
  connection->rp.responseIcy = reply_icy;
#if defined(_MHD_HAVE_SENDFILE)
  if ( (response->fd == -1) ||
       (response->is_pipe) ||
       (0 != (connection->daemon->options & MHD_USE_TLS))
#if defined(MHD_SEND_SPIPE_SUPPRESS_NEEDED) && \
       defined(MHD_SEND_SPIPE_SUPPRESS_POSSIBLE)
       || (! daemon->sigpipe_blocked && ! connection->sk_spipe_suppress)
#endif /* MHD_SEND_SPIPE_SUPPRESS_NEEDED &&
          MHD_SEND_SPIPE_SUPPRESS_POSSIBLE */
       )
    connection->rp.resp_sender = MHD_resp_sender_std;
  else
    connection->rp.resp_sender = MHD_resp_sender_sendfile;
#endif /* _MHD_HAVE_SENDFILE */
  /* FIXME: if 'is_pipe' is set, TLS is off, and we have *splice*, we could use splice()
     to avoid two user-space copies... */

  if ( (MHD_HTTP_MTHD_HEAD == connection->rq.http_mthd) ||
       (MHD_HTTP_OK > status_code) ||
       (MHD_HTTP_NO_CONTENT == status_code) ||
       (MHD_HTTP_NOT_MODIFIED == status_code) )
  {
    /* if this is a "HEAD" request, or a status code for
       which a body is not allowed, pretend that we
       have already sent the full message body. */
    /* TODO: remove the next assignment, use 'rp_props.send_reply_body' in
     * checks */
    connection->rp.rsp_write_position = response->total_size;
    if (logf)
    {
      fprintf(logf, "Adjusted rsp_write_position to response->total_size=%llu\n",
              (unsigned long long) response->total_size);
      fflush(logf);
    }
  }
  if (MHD_CONNECTION_HEADERS_PROCESSED == connection->state)
  {
    /* response was queued "early", refuse to read body / footers or
       further requests! */
    connection->discard_request = true;
    connection->state = MHD_CONNECTION_START_REPLY;
    connection->rq.remaining_upload_size = 0;
    if (logf)
    {
      fprintf(logf, "Queued early: set discard_request=true, state=MHD_CONNECTION_START_REPLY\n");
      fflush(logf);
    }
  }
  if (! connection->in_idle)
    (void) MHD_connection_handle_idle (connection);
  MHD_update_last_activity_ (connection);

  if (logf)
  {
    fprintf(logf, "EXIT MHD_queue_response: queued response=%p status=%u success\n",
            (void *)response, status_code);
    fclose(logf);
  }
  return MHD_YES;
}
