_MHD_EXTERN enum MHD_Result
MHD_queue_response (struct MHD_Connection *connection,
                    unsigned int status_code,
                    struct MHD_Response *response)
{
  struct MHD_Daemon *daemon;
  bool reply_icy;
  FILE *logf = fopen("/tmp/logs/1075.txt", "a");

  if (logf)
  {
    fprintf(logf, "MHD_queue_response: ENTRY\n");
    fprintf(logf, "  connection=%p, response=%p, initial_status_code=%u\n",
            (void *) connection, (void *) response, status_code);
    fflush(logf);
  }

  if ((NULL == connection) || (NULL == response))
  {
    if (logf)
    {
      fprintf(logf, "  early RETURN: NULL parameter(s)\n");
      fclose(logf);
    }
    return MHD_NO;
  }

  daemon = connection->daemon;
  if (logf)
  {
    fprintf(logf, "  daemon=%p\n", (void *) daemon);
    if (daemon)
    {
      /* log a few daemon fields that are relevant to the violation scenario */
      fprintf(logf, "  daemon->shutdown=%d, daemon->options=0x%08x",
              (int) daemon->shutdown, (unsigned) daemon->options);
#ifdef _MSC_VER
      fprintf(logf, ", daemon->sigpipe_blocked=%d\n", (int) daemon->sigpipe_blocked);
#else
      fprintf(logf, ", daemon->sigpipe_blocked=%d\n", (int) daemon->sigpipe_blocked);
#endif
    }
    else
    {
      fprintf(logf, "  daemon is NULL\n");
    }
    fflush(logf);
  }

  if ((! connection->in_access_handler) && (! connection->suspended) &&
      MHD_D_IS_USING_THREADS_ (daemon))
  {
    if (logf)
    {
      fprintf(logf, "  CONDITION: not in_access_handler && not suspended && using_threads -> RETURN MHD_NO\n");
      fclose(logf);
    }
    return MHD_NO;
  }

  reply_icy = (0 != (status_code & MHD_ICY_FLAG));
  status_code &= ~MHD_ICY_FLAG;

  if (logf)
  {
    fprintf(logf, "  reply_icy=%d, masked_status_code=%u\n", (int) reply_icy, status_code);
    fflush(logf);
  }

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if ( (! connection->suspended) &&
       MHD_D_IS_USING_THREADS_ (daemon) &&
       (! MHD_thread_handle_ID_is_current_thread_ (connection->tid)) )
  {
    if (logf)
    {
      fprintf(logf, "  THREAD CHECK failed: suspended=%d, using_threads=%d, current_thread=%d -> RETURN MHD_NO\n",
              (int) connection->suspended, (int) MHD_D_IS_USING_THREADS_ (daemon),
              (int) MHD_thread_handle_ID_is_current_thread_ (connection->tid));
      fclose(logf);
    }
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Attempted to queue response on wrong thread!\n"));
#endif
    return MHD_NO;
  }
#endif

  if (NULL != connection->rp.response)
  {
    if (logf)
    {
      fprintf(logf, "  RESPONSE ALREADY SET -> RETURN MHD_NO\n");
      fclose(logf);
    }
    return MHD_NO; /* The response was already set */
  }

  if ( (MHD_CONNECTION_HEADERS_PROCESSED != connection->state) &&
       (MHD_CONNECTION_FULL_REQ_RECEIVED != connection->state) )
  {
    if (logf)
    {
      fprintf(logf, "  WRONG CONNECTION STATE: state=%d -> RETURN MHD_NO\n", (int) connection->state);
      fclose(logf);
    }
    return MHD_NO; /* Wrong connection state */
  }

  if (daemon->shutdown)
  {
    if (logf)
    {
      fprintf(logf, "  daemon->shutdown true -> RETURN MHD_NO\n");
      fclose(logf);
    }
    return MHD_NO;
  }

#ifdef UPGRADE_SUPPORT
  if (NULL != response->upgrade_handler)
  {
    struct MHD_HTTP_Res_Header *conn_header;
    if (0 == (daemon->options & MHD_ALLOW_UPGRADE))
    {
      if (logf)
      {
        fprintf(logf, "  upgrade_handler present but daemon lacks MHD_ALLOW_UPGRADE -> RETURN MHD_NO\n");
        fclose(logf);
      }
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Attempted 'upgrade' connection on daemon without" \
                   " MHD_ALLOW_UPGRADE option!\n"));
#endif
      return MHD_NO;
    }
    if (MHD_HTTP_SWITCHING_PROTOCOLS != status_code)
    {
      if (logf)
      {
        fprintf(logf, "  upgrade_handler present but status_code != 101 (%u) -> RETURN MHD_NO\n", status_code);
        fclose(logf);
      }
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid status code for" \
                   " 'upgrade' response!\n"));
#endif
      return MHD_NO;
    }
    if (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
    {
      if (logf)
      {
        fprintf(logf, "  upgrade_handler present but response lacks Connection header -> RETURN MHD_NO\n");
        fclose(logf);
      }
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid response" \
                   " without \"Connection\" header!\n"));
#endif
      return MHD_NO;
    }
    conn_header = response->first_header;
    mhd_assert (NULL != conn_header);
    mhd_assert (MHD_str_equal_caseless_ (conn_header->header,
                                         MHD_HTTP_HEADER_CONNECTION));
    if (! MHD_str_has_s_token_caseless_ (conn_header->value,
                                         "upgrade"))
    {
      if (logf)
      {
        fprintf(logf, "  Connection header missing 'upgrade' token -> RETURN MHD_NO\n");
        fclose(logf);
      }
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid response" \
                   " without \"upgrade\" token in" \
                   " \"Connection\" header!\n"));
#endif
      return MHD_NO;
    }
    if (! MHD_IS_HTTP_VER_1_1_COMPAT (connection->rq.http_ver))
    {
      if (logf)
      {
        fprintf(logf, "  Upgrade only allowed with HTTP/1.1 compat -> RETURN MHD_NO\n");
        fclose(logf);
      }
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Connection \"Upgrade\" can be used only " \
                   "with HTTP/1.1 connections!\n"));
#endif
      return MHD_NO;
    }
  }
#endif /* UPGRADE_SUPPORT */
  if (MHD_HTTP_SWITCHING_PROTOCOLS == status_code)
  {
#ifdef UPGRADE_SUPPORT
    if (NULL == response->upgrade_handler)
    {
      if (logf)
      {
        fprintf(logf, "  status 101 but no upgrade_handler -> RETURN MHD_NO\n");
        fclose(logf);
      }
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used status code 101 \"Switching Protocols\" " \
                   "with non-'upgrade' response!\n"));
#endif /* HAVE_MESSAGES */
      return MHD_NO;
    }
#else  /* ! UPGRADE_SUPPORT */
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Application used status code 101 \"Switching Protocols\", " \
                 "but this MHD was built without \"Upgrade\" support!\n"));
#endif /* HAVE_MESSAGES */
    if (logf)
    {
      fprintf(logf, "  status 101 but UPGRADE_SUPPORT disabled -> RETURN MHD_NO\n");
      fclose(logf);
    }
    return MHD_NO;
#endif /* ! UPGRADE_SUPPORT */
  }
  if ( (100 > status_code) ||
       (999 < status_code) )
  {
    if (logf)
    {
      fprintf(logf, "  invalid status_code=%u -> RETURN MHD_NO\n", status_code);
      fclose(logf);
    }
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Refused wrong status code (%u). " \
                 "HTTP requires three digits status code!\n"),
              status_code);
#endif
    return MHD_NO;
  }
  if (200 > status_code)
  {
    if (MHD_HTTP_VER_1_0 == connection->rq.http_ver)
    {
      if (logf)
      {
        fprintf(logf, "  1xx status code refused for HTTP/1.0 client (status=%u) -> RETURN MHD_NO\n", status_code);
        fclose(logf);
      }
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Wrong status code (%u) refused. " \
                   "HTTP/1.0 clients do not support 1xx status codes!\n"),
                (status_code));
#endif
      return MHD_NO;
    }
    if (0 != (response->flags & (MHD_RF_HTTP_1_0_COMPATIBLE_STRICT
                                 | MHD_RF_HTTP_1_0_SERVER)))
    {
      if (logf)
      {
        fprintf(logf, "  1xx status code refused for HTTP/1.0 reply mode (status=%u) -> RETURN MHD_NO\n", status_code);
        fclose(logf);
      }
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Wrong status code (%u) refused. " \
                   "HTTP/1.0 reply mode does not support 1xx status codes!\n"),
                (status_code));
#endif
      return MHD_NO;
    }
  }
  if ( (MHD_HTTP_MTHD_CONNECT == connection->rq.http_mthd) &&
       (2 == status_code / 100) )
  {
    if (logf)
    {
      fprintf(logf, "  CONNECT request cannot be answered with 2xx (status=%u) -> RETURN MHD_NO\n", status_code);
      fclose(logf);
    }
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Successful (%u) response code cannot be used to answer " \
                 "\"CONNECT\" request!\n"),
              (status_code));
#endif
    return MHD_NO;
  }

  if ( (0 != (MHD_RF_HEAD_ONLY_RESPONSE & response->flags)) &&
       (RP_BODY_HEADERS_ONLY < is_reply_body_needed (connection, status_code)) )
  {
    if (logf)
    {
      fprintf(logf, "  HEAD-only response cannot be used when body needed -> RETURN MHD_NO\n");
      fclose(logf);
    }
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("HEAD-only response cannot be used when the request requires "
                 "reply body to be sent!\n"));
#endif
    return MHD_NO;
  }

#ifdef HAVE_MESSAGES
  if ( (0 != (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) &&
       (0 != (MHD_RAF_HAS_CONTENT_LENGTH & response->flags_auto)) )
  {
    MHD_DLOG (daemon,
              _ ("The response has application-defined \"Content-Length\" " \
                 "header. The reply to the request will be not " \
                 "HTTP-compliant and may result in hung connection or " \
                 "other problems!\n"));
  }
#endif

  MHD_increment_response_rc (response);
  if (logf)
  {
    fprintf(logf, "  incremented response ref count\n");
    fflush(logf);
  }
  connection->rp.response = response;
  connection->rp.responseCode = status_code;
  connection->rp.responseIcy = reply_icy;
#if defined(_MHD_HAVE_SENDFILE)
  if ( (response->fd == -1) ||
       (response->is_pipe) ||
       (0 != (connection->daemon->options & MHD_USE_TLS))
#if defined(MHD_SEND_SPIPE_SUPPRESS_NEEDED) && \
       defined(MHD_SEND_SPIPE_SUPPRESS_POSSIBLE)
       || (! daemon->sigpipe_blocked && ! connection->sk_spipe_suppress)
#endif /* MHD_SEND_SPIPE_SUPPRESS_NEEDED &&
          MHD_SEND_SPIPE_SUPPRESS_POSSIBLE */
       )
    connection->rp.resp_sender = MHD_resp_sender_std;
  else
    connection->rp.resp_sender = MHD_resp_sender_sendfile;
#endif /* _MHD_HAVE_SENDFILE */
  if (logf)
  {
    fprintf(logf, "  set connection->rp fields: response=%p, responseCode=%u, responseIcy=%d\n",
            (void *) connection->rp.response, connection->rp.responseCode, (int) connection->rp.responseIcy);
#ifdef _MHD_HAVE_SENDFILE
    fprintf(logf, "  resp_sender=%p\n", (void *) connection->rp.resp_sender);
#endif
    fflush(logf);
  }
  /* FIXME: if 'is_pipe' is set, TLS is off, and we have *splice*, we could use splice()
     to avoid two user-space copies... */

  if ( (MHD_HTTP_MTHD_HEAD == connection->rq.http_mthd) ||
       (MHD_HTTP_OK > status_code) ||
       (MHD_HTTP_NO_CONTENT == status_code) ||
       (MHD_HTTP_NOT_MODIFIED == status_code) )
  {
    /* if this is a "HEAD" request, or a status code for
       which a body is not allowed, pretend that we
       have already sent the full message body. */
    /* TODO: remove the next assignment, use 'rp_props.send_reply_body' in
     * checks */
    connection->rp.rsp_write_position = response->total_size;
    if (logf)
    {
      fprintf(logf, "  set rsp_write_position to total_size=%zu\n", (size_t) response->total_size);
      fflush(logf);
    }
  }
  if (MHD_CONNECTION_HEADERS_PROCESSED == connection->state)
  {
    /* response was queued "early", refuse to read body / footers or
       further requests! */
    connection->discard_request = true;
    connection->state = MHD_CONNECTION_START_REPLY;
    connection->rq.remaining_upload_size = 0;
    if (logf)
    {
      fprintf(logf, "  headers already processed: set discard_request=true, state=START_REPLY\n");
      fflush(logf);
    }
  }
  if (! connection->in_idle)
    (void) MHD_connection_handle_idle (connection);
  MHD_update_last_activity_ (connection);

  if (logf)
  {
    fprintf(logf, "MHD_queue_response: EXIT -> RETURN MHD_YES\n");
    fclose(logf);
  }
  return MHD_YES;
}
