_MHD_EXTERN enum MHD_Result
MHD_run_from_select2 (struct MHD_Daemon *daemon,
                      const fd_set *read_fd_set,
                      const fd_set *write_fd_set,
                      const fd_set *except_fd_set,
                      unsigned int fd_setsize)
{
  /* Open log early to capture entry and all decision points. */
  FILE *mhd_log = fopen("/tmp/logs/992.txt", "a");
  if (mhd_log)
  {
    fprintf(mhd_log, "ENTER: MHD_run_from_select2 daemon=%p read=%p write=%p except=%p fd_setsize=%u\n",
            (void *) daemon,
            (const void *) read_fd_set,
            (const void *) write_fd_set,
            (const void *) except_fd_set,
            fd_setsize);
    fflush(mhd_log);
  }

  if (MHD_D_IS_USING_POLL_ (daemon) ||
      MHD_D_IS_USING_THREADS_ (daemon))
  {
    if (mhd_log)
    {
      fprintf(mhd_log, "BRANCH: using poll or threads -> returning MHD_NO\n");
      fclose(mhd_log);
    }
    return MHD_NO;
  }
  if ((NULL == read_fd_set) || (NULL == write_fd_set))
  {
    if (mhd_log)
    {
      fprintf(mhd_log, "BRANCH: NULL read_fd_set or write_fd_set -> returning MHD_NO\n");
      fclose(mhd_log);
    }
    return MHD_NO;
  }
#ifdef HAVE_MESSAGES
  if (NULL == except_fd_set)
  {
    if (mhd_log)
    {
      fprintf(mhd_log, "NOTICE: except_fd_set is NULL (deprecated)\n");
      fflush(mhd_log);
    }

    MHD_DLOG (daemon,
              _ ("MHD_run_from_select() called with except_fd_set "
                 "set to NULL. Such behavior is deprecated.\n"));
  }
#endif /* HAVE_MESSAGES */

#ifdef HAS_FD_SETSIZE_OVERRIDABLE
  if (0 == fd_setsize)
  {
    if (mhd_log)
    {
      fprintf(mhd_log, "BRANCH: fd_setsize == 0 -> returning MHD_NO\n");
      fclose(mhd_log);
    }
    return MHD_NO;
  }
  else if (((unsigned int) INT_MAX) < fd_setsize)
  {
    if (mhd_log)
    {
      fprintf(mhd_log, "VIOLATION_PATH: fd_setsize (%u) > INT_MAX, performing assignment to INT_MAX\n",
              fd_setsize);
      fflush(mhd_log);
    }
    /* The following assignment modifies the function parameter 'fd_setsize'.
       This is the code path reported to violate MISRA C 2023 Rule 17.8. */
    fd_setsize = (unsigned int) INT_MAX;
    if (mhd_log)
    {
      fprintf(mhd_log, "AFTER_ASSIGN: fd_setsize changed to %u\n", fd_setsize);
      fflush(mhd_log);
    }
  }
#ifdef HAVE_MESSAGES
  else if (daemon->fdset_size > ((int) fd_setsize))
  {
    if (daemon->fdset_size_set_by_app)
    {
      if (mhd_log)
      {
        fprintf(mhd_log, "BRANCH: daemon->fdset_size (%d) > fd_setsize (%u) and set by app\n",
                daemon->fdset_size, fd_setsize);
        fflush(mhd_log);
      }

      MHD_DLOG (daemon,
                _ ("%s() called with fd_setsize (%u) " \
                   "less than value set by MHD_OPTION_APP_FD_SETSIZE (%d). " \
                   "Some socket FDs may be not processed. " \
                   "Use MHD_OPTION_APP_FD_SETSIZE with the correct value.\n"),
                "MHD_run_from_select2", fd_setsize, daemon->fdset_size);
    }
    else
    {
      if (mhd_log)
      {
        fprintf(mhd_log, "BRANCH: daemon->fdset_size (%d) > fd_setsize (%u) and NOT set by app\n",
                daemon->fdset_size, fd_setsize);
        fflush(mhd_log);
      }

      MHD_DLOG (daemon,
                _ ("%s() called with fd_setsize (%u) " \
                   "less than FD_SETSIZE used by MHD (%d). " \
                   "Some socket FDs may be not processed. " \
                   "Consider using MHD_OPTION_APP_FD_SETSIZE option.\n"),
                "MHD_run_from_select2", fd_setsize, daemon->fdset_size);
    }
  }
#endif /* HAVE_MESSAGES */
#else  /* ! HAS_FD_SETSIZE_OVERRIDABLE */
  if (((unsigned int) FD_SETSIZE) > fd_setsize)
  {
#ifdef HAVE_MESSAGES
    if (mhd_log)
    {
      fprintf(mhd_log, "BRANCH: platform FD_SETSIZE (%d) > fd_setsize (%u) -> returning MHD_NO\n",
              (int) FD_SETSIZE, fd_setsize);
      fflush(mhd_log);
    }

    MHD_DLOG (daemon,
              _ ("%s() called with fd_setsize (%u) " \
                 "less than fixed FD_SETSIZE value (%d) used on the " \
                 "platform.\n"),
              "MHD_run_from_select2", fd_setsize, (int) FD_SETSIZE);
#endif /* HAVE_MESSAGES */
    if (mhd_log)
      fclose(mhd_log);
    return MHD_NO;
  }
#endif /* ! HAS_FD_SETSIZE_OVERRIDABLE */

  if (MHD_D_IS_USING_EPOLL_ (daemon))
  {
#ifdef EPOLL_SUPPORT
    if (mhd_log)
    {
      fprintf(mhd_log, "BRANCH: using epoll -> calling MHD_epoll\n");
      fflush(mhd_log);
    }

    enum MHD_Result ret = MHD_epoll (daemon,
                                     0);

    if (mhd_log)
    {
      fprintf(mhd_log, "EPOLL_RETURN: ret=%d, cleaning up connections and returning\n", (int) ret);
      fclose(mhd_log);
    }

    MHD_cleanup_connections (daemon);
    return ret;
#else  /* ! EPOLL_SUPPORT */
    if (mhd_log)
    {
      fprintf(mhd_log, "BRANCH: EPOLL not supported -> returning MHD_NO\n");
      fclose(mhd_log);
    }
    return MHD_NO;
#endif /* ! EPOLL_SUPPORT */
  }

  /* Resuming external connections when using an extern mainloop  */
  if (0 != (daemon->options & MHD_TEST_ALLOW_SUSPEND_RESUME))
  {
    if (mhd_log)
    {
      fprintf(mhd_log, "ACTION: resuming suspended connections\n");
      fflush(mhd_log);
    }
    resume_suspended_connections (daemon);
  }

  if (mhd_log)
  {
    fprintf(mhd_log, "CALL: internal_run_from_select with fd_setsize (int)=%d\n", (int) fd_setsize);
    fflush(mhd_log);
    fclose(mhd_log);
  }

  return internal_run_from_select (daemon,
                                   read_fd_set,
                                   write_fd_set,
                                   except_fd_set,
                                   (int) fd_setsize);
}
