_MHD_EXTERN enum MHD_Result
MHD_run_from_select2 (struct MHD_Daemon *daemon,
                      const fd_set *read_fd_set,
                      const fd_set *write_fd_set,
                      const fd_set *except_fd_set,
                      unsigned int fd_setsize)
{
  /* Logging probe: record entry and initial state to help reproduce
     potential const-qualification issues reported at call sites of
     MHD_DLOG (e.g. passing string literals and daemon pointer). */
  {
    FILE *___mhd_log_f = fopen("/tmp/logs/995.txt", "a");
    if (___mhd_log_f)
    {
      fprintf(___mhd_log_f, "ENTER MHD_run_from_select2: daemon=%p read_fd_set=%p write_fd_set=%p except_fd_set=%p fd_setsize=%u\n",
              (void *) daemon, (const void *) read_fd_set, (const void *) write_fd_set, (const void *) except_fd_set, fd_setsize);
      fclose(___mhd_log_f);
    }
  }

  if (MHD_D_IS_USING_POLL_ (daemon) ||
      MHD_D_IS_USING_THREADS_ (daemon))
  {
    FILE *___mhd_log_f = fopen("/tmp/logs/995.txt", "a");
    if (___mhd_log_f)
    {
      fprintf(___mhd_log_f, "BRANCH: using poll or threads -> returning MHD_NO\n");
      fclose(___mhd_log_f);
    }
    return MHD_NO;
  }
  if ((NULL == read_fd_set) || (NULL == write_fd_set))
  {
    FILE *___mhd_log_f = fopen("/tmp/logs/995.txt", "a");
    if (___mhd_log_f)
    {
      fprintf(___mhd_log_f, "BRANCH: read_fd_set==NULL or write_fd_set==NULL -> returning MHD_NO\n");
      fclose(___mhd_log_f);
    }
    return MHD_NO;
  }
#ifdef HAVE_MESSAGES
  if (NULL == except_fd_set)
  {
    /* Log details about this deprecated usage, including the format string
       pointer and the daemon pointer value to capture the potential
       const-qualification mismatch reported. */
    {
      FILE *___mhd_log_f = fopen("/tmp/logs/995.txt", "a");
      if (___mhd_log_f)
      {
        const char *___fmt = _ ("MHD_run_from_select() called with except_fd_set "
                               "set to NULL. Such behavior is deprecated.\n");
        fprintf(___mhd_log_f, "MHD_DLOG about to be called (except_fd_set==NULL): daemon=%p fmt_ptr=%p fmt_text=\"MHD_run_from_select() called with except_fd_set set to NULL. Such behavior is deprecated.\\n\"\n",
                (void *) daemon, (const void *) ___fmt);
        fclose(___mhd_log_f);
      }
    }
    MHD_DLOG (daemon,
              _ ("MHD_run_from_select() called with except_fd_set "
                 "set to NULL. Such behavior is deprecated.\n"));
  }
#endif /* HAVE_MESSAGES */

#ifdef HAS_FD_SETSIZE_OVERRIDABLE
  if (0 == fd_setsize)
  {
    FILE *___mhd_log_f = fopen("/tmp/logs/995.txt", "a");
    if (___mhd_log_f)
    {
      fprintf(___mhd_log_f, "BRANCH: fd_setsize==0 -> returning MHD_NO\n");
      fclose(___mhd_log_f);
    }
    return MHD_NO;
  }
  else if (((unsigned int) INT_MAX) < fd_setsize)
    fd_setsize = (unsigned int) INT_MAX;
#ifdef HAVE_MESSAGES
  else if (daemon->fdset_size > ((int) fd_setsize))
  {
    if (daemon->fdset_size_set_by_app)
    {
      /* Log the imminent MHD_DLOG call and the addresses of the
         format string and the literal argument "MHD_run_from_select2". */
      FILE *___mhd_log_f = fopen("/tmp/logs/995.txt", "a");
      if (___mhd_log_f)
      {
        const char *___fmt = _ ("%s() called with fd_setsize (%u) " \
                               "less than value set by MHD_OPTION_APP_FD_SETSIZE (%d). " \
                               "Some socket FDs may be not processed. " \
                               "Use MHD_OPTION_APP_FD_SETSIZE with the correct value.\n");
        fprintf(___mhd_log_f, "MHD_DLOG about to be called (fdset_size > fd_setsize && fdset_size_set_by_app): daemon=%p fmt_ptr=%p literal_ptr=%p fd_setsize=%u daemon_fdset_size=%d\n",
                (void *) daemon, (const void *) ___fmt, (const void *) "MHD_run_from_select2", fd_setsize, daemon->fdset_size);
        fclose(___mhd_log_f);
      }
      MHD_DLOG (daemon,
                _ ("%s() called with fd_setsize (%u) " \
                   "less than value set by MHD_OPTION_APP_FD_SETSIZE (%d). " \
                   "Some socket FDs may be not processed. " \
                   "Use MHD_OPTION_APP_FD_SETSIZE with the correct value.\n"),
                "MHD_run_from_select2", fd_setsize, daemon->fdset_size);
    }
    else
    {
      FILE *___mhd_log_f = fopen("/tmp/logs/995.txt", "a");
      if (___mhd_log_f)
      {
        const char *___fmt = _ ("%s() called with fd_setsize (%u) " \
                               "less than FD_SETSIZE used by MHD (%d). " \
                               "Some socket FDs may be not processed. " \
                               "Consider using MHD_OPTION_APP_FD_SETSIZE option.\n");
        fprintf(___mhd_log_f, "MHD_DLOG about to be called (fdset_size > fd_setsize && !fdset_size_set_by_app): daemon=%p fmt_ptr=%p literal_ptr=%p fd_setsize=%u daemon_fdset_size=%d\n",
                (void *) daemon, (const void *) ___fmt, (const void *) "MHD_run_from_select2", fd_setsize, daemon->fdset_size);
        fclose(___mhd_log_f);
      }
      MHD_DLOG (daemon,
                _ ("%s() called with fd_setsize (%u) " \
                   "less than FD_SETSIZE used by MHD (%d). " \
                   "Some socket FDs may be not processed. " \
                   "Consider using MHD_OPTION_APP_FD_SETSIZE option.\n"),
                "MHD_run_from_select2", fd_setsize, daemon->fdset_size);
    }
  }
#endif /* HAVE_MESSAGES */
#else  /* ! HAS_FD_SETSIZE_OVERRIDABLE */
  if (((unsigned int) FD_SETSIZE) > fd_setsize)
  {
#ifdef HAVE_MESSAGES
    {
      FILE *___mhd_log_f = fopen("/tmp/logs/995.txt", "a");
      if (___mhd_log_f)
      {
        const char *___fmt = _ ("%s() called with fd_setsize (%u) " \
                               "less than fixed FD_SETSIZE value (%d) used on the " \
                               "platform.\n");
        fprintf(___mhd_log_f, "MHD_DLOG about to be called (FD_SETSIZE > fd_setsize): daemon=%p fmt_ptr=%p literal_ptr=%p fd_setsize=%u FD_SETSIZE=%d\n",
                (void *) daemon, (const void *) ___fmt, (const void *) "MHD_run_from_select2", fd_setsize, (int) FD_SETSIZE);
        fclose(___mhd_log_f);
      }
    }
    MHD_DLOG (daemon,
              _ ("%s() called with fd_setsize (%u) " \
                 "less than fixed FD_SETSIZE value (%d) used on the " \
                 "platform.\n"),
              "MHD_run_from_select2", fd_setsize, (int) FD_SETSIZE);
#endif /* HAVE_MESSAGES */
    FILE *___mhd_log_f = fopen("/tmp/logs/995.txt", "a");
    if (___mhd_log_f)
    {
      fprintf(___mhd_log_f, "BRANCH: FD_SETSIZE > fd_setsize -> returning MHD_NO\n");
      fclose(___mhd_log_f);
    }
    return MHD_NO;
  }
#endif /* ! HAS_FD_SETSIZE_OVERRIDABLE */

  if (MHD_D_IS_USING_EPOLL_ (daemon))
  {
#ifdef EPOLL_SUPPORT
    enum MHD_Result ret = MHD_epoll (daemon,
                                     0);

    MHD_cleanup_connections (daemon);

    {
      FILE *___mhd_log_f = fopen("/tmp/logs/995.txt", "a");
      if (___mhd_log_f)
      {
        fprintf(___mhd_log_f, "BRANCH: using epoll path -> called MHD_epoll, returning ret=%d\n", (int) ret);
        fclose(___mhd_log_f);
      }
    }
    return ret;
#else  /* ! EPOLL_SUPPORT */
    FILE *___mhd_log_f = fopen("/tmp/logs/995.txt", "a");
    if (___mhd_log_f)
    {
      fprintf(___mhd_log_f, "BRANCH: EPOLL not supported -> returning MHD_NO\n");
      fclose(___mhd_log_f);
    }
    return MHD_NO;
#endif /* ! EPOLL_SUPPORT */
  }

  /* Resuming external connections when using an extern mainloop  */
  if (0 != (daemon->options & MHD_TEST_ALLOW_SUSPEND_RESUME))
  {
    FILE *___mhd_log_f = fopen("/tmp/logs/995.txt", "a");
    if (___mhd_log_f)
    {
      fprintf(___mhd_log_f, "ACTION: resume_suspended_connections will be called (options=0x%08x)\n", (unsigned int) daemon->options);
      fclose(___mhd_log_f);
    }
    resume_suspended_connections (daemon);
  }

  {
    FILE *___mhd_log_f = fopen("/tmp/logs/995.txt", "a");
    if (___mhd_log_f)
    {
      fprintf(___mhd_log_f, "CALL: internal_run_from_select with daemon=%p read=%p write=%p except=%p fd_setsize=%d\n",
              (void *) daemon, (const void *) read_fd_set, (const void *) write_fd_set, (const void *) except_fd_set, (int) fd_setsize);
      fclose(___mhd_log_f);
    }
  }

  return internal_run_from_select (daemon,
                                   read_fd_set,
                                   write_fd_set,
                                   except_fd_set,
                                   (int) fd_setsize);
}
