_MHD_EXTERN enum MHD_Result
MHD_run_wait (struct MHD_Daemon *daemon,
              int32_t millisec)
{
  enum MHD_Result res;
  /* Open log file early to record control flow and state */
  FILE *mhd_log = fopen("/tmp/logs/4069.txt", "a");
  if (mhd_log)
  {
    fprintf(mhd_log, "MHD_run_wait ENTER: daemon=%p\n", (void *)daemon);
    /* Be careful: the original code dereferences daemon->shutdown immediately,
       so we mirror that behavior in the logged values. */
    /* If daemon is NULL this will reproduce original behavior (likely crash) */
    if (daemon)
      fprintf(mhd_log, " initial state: shutdown=%d, tid=%p\n",
              (int)daemon->shutdown, (void *)daemon->tid);
    else
      fprintf(mhd_log, " initial state: daemon is NULL\n");
    fflush(mhd_log);
  }

  if ( (daemon->shutdown) ||
       MHD_D_IS_USING_THREADS_ (daemon) )
  {
    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_run_wait: early return MHD_NO (shutdown or using threads)\n");
      fclose(mhd_log);
    }
    return MHD_NO;
  }

  mhd_assert (! MHD_thread_handle_ID_is_valid_handle_ (daemon->tid));

  if (0 > millisec)
    millisec = -1;
  if (mhd_log)
  {
    fprintf(mhd_log, "MHD_run_wait: normalized millisec=%d\n", (int)millisec);
    fflush(mhd_log);
  }
#ifdef HAVE_POLL
  if (MHD_D_IS_USING_POLL_ (daemon))
  {
    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_run_wait: taking POLL branch\n");
      fflush(mhd_log);
    }
    res = MHD_poll_all (daemon, millisec);
    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_run_wait: POLL returned res=%d\n", (int)res);
      fflush(mhd_log);
    }
    MHD_cleanup_connections (daemon);
    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_run_wait: POLL branch cleaned up connections\n");
      fclose(mhd_log);
    }
  }
  else
#endif /* HAVE_POLL */
#ifdef EPOLL_SUPPORT
  if (MHD_D_IS_USING_EPOLL_ (daemon))
  {
    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_run_wait: taking EPOLL branch\n");
      fflush(mhd_log);
    }
    res = MHD_epoll (daemon, millisec);
    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_run_wait: EPOLL returned res=%d\n", (int)res);
      fflush(mhd_log);
    }
    MHD_cleanup_connections (daemon);
    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_run_wait: EPOLL branch cleaned up connections\n");
      fclose(mhd_log);
    }
  }
  else
#endif
  if (1)
  {
    /* This is the invariant controlling expression flagged by the tooling.
       Log that this constant-true branch is being taken so reviewers can
       reproduce evidence of the Rule 14.3 scenario. */
    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_run_wait: taking SELECT branch (invariant if(1) entry)\n");
      fprintf(mhd_log, "MHD_run_wait: assert MHD_D_IS_USING_SELECT_(daemon) -> %d\n",
              (int) MHD_D_IS_USING_SELECT_ (daemon));
      fflush(mhd_log);
    }
    mhd_assert (MHD_D_IS_USING_SELECT_ (daemon));
#ifdef HAS_FD_SETSIZE_OVERRIDABLE
#ifdef HAVE_MESSAGES
    if (daemon->fdset_size_set_by_app
        && (((int) FD_SETSIZE) < daemon->fdset_size))
    {
      MHD_DLOG (daemon,
                _ ("MHD_run()/MHD_run_wait() called for daemon started with " \
                   "MHD_OPTION_APP_FD_SETSIZE option (%d). " \
                   "The library was compiled with smaller FD_SETSIZE (%d). " \
                   "Some socket FDs may be not processed. " \
                   "Use MHD_run_from_select2() instead of MHD_run() or " \
                   "do not use MHD_OPTION_APP_FD_SETSIZE option.\n"),
                daemon->fdset_size, (int) FD_SETSIZE);
      if (mhd_log)
      {
        fprintf(mhd_log, "MHD_run_wait: FD_SETSIZE warning: app fdset_size=%d compiled FD_SETSIZE=%d\n",
                (int)daemon->fdset_size, (int)FD_SETSIZE);
        fflush(mhd_log);
      }
    }
#endif /* HAVE_MESSAGES */
#endif /* HAS_FD_SETSIZE_OVERRIDABLE */

    res = MHD_select (daemon, millisec);
    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_run_wait: SELECT returned res=%d\n", (int)res);
      fprintf(mhd_log, "MHD_run_wait: NOTE: MHD_select() is expected to do MHD_cleanup_connections() itself\n");
      fclose(mhd_log);
    }
    /* MHD_select does MHD_cleanup_connections already */
  }
  return res;
}
