_MHD_EXTERN enum MHD_Result
MHD_run_wait (struct MHD_Daemon *daemon,
              int32_t millisec)
{
  enum MHD_Result res;
  /* Open log for tracing control flow and state to help reproduce violation */
  FILE *log = fopen("/tmp/logs/4068.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER MHD_run_wait: daemon=%p, millisec=%d\n",
            (void *) daemon, (int) millisec);
    /* Log some relevant state fields used below (print as integers) */
    fprintf(log, "STATE before checks: shutdown=%d, using_threads=%d\n",
            (int) daemon->shutdown,
            (int) MHD_D_IS_USING_THREADS_ (daemon));
    fflush(log);
  }

  if ( (daemon->shutdown) ||
       MHD_D_IS_USING_THREADS_ (daemon) )
  {
    if (log)
    {
      fprintf(log, "EARLY RETURN: daemon->shutdown=%d or using_threads=%d -> MHD_NO\n",
              (int) daemon->shutdown,
              (int) MHD_D_IS_USING_THREADS_ (daemon));
      fclose(log);
    }
    return MHD_NO;
  }

  mhd_assert (! MHD_thread_handle_ID_is_valid_handle_ (daemon->tid));

  if (0 > millisec)
    millisec = -1;

#ifdef HAVE_POLL
  if (MHD_D_IS_USING_POLL_ (daemon))
  {
    if (log)
    {
      fprintf(log, "BRANCH: using poll branch taken (MHD_poll_all) millisec=%d\n",
              (int) millisec);
      fflush(log);
    }

    res = MHD_poll_all (daemon, millisec);
    MHD_cleanup_connections (daemon);

    if (log)
    {
      fprintf(log, "RETURN FROM poll branch: res=%d\n", (int) res);
      fclose(log);
    }
  }
  else
#endif /* HAVE_POLL */
#ifdef EPOLL_SUPPORT
  if (MHD_D_IS_USING_EPOLL_ (daemon))
  {
    if (log)
    {
      fprintf(log, "BRANCH: using epoll branch taken (MHD_epoll) millisec=%d\n",
              (int) millisec);
      fflush(log);
    }

    res = MHD_epoll (daemon, millisec);
    MHD_cleanup_connections (daemon);

    if (log)
    {
      fprintf(log, "RETURN FROM epoll branch: res=%d\n", (int) res);
      fclose(log);
    }
  }
  else
#endif
  if (1)
  {
    /* This is the branch highlighted in the violation report. Log that it was reached. */
    if (log)
    {
      fprintf(log, "BRANCH: final select branch taken. NOTE: condition is constant '1' -> always executed when reached.\n");
      fprintf(log, "ASSERT check: MHD_D_IS_USING_SELECT_ (daemon) == %d\n",
              (int) MHD_D_IS_USING_SELECT_ (daemon));
      fflush(log);
    }

    mhd_assert (MHD_D_IS_USING_SELECT_ (daemon));
#ifdef HAS_FD_SETSIZE_OVERRIDABLE
#ifdef HAVE_MESSAGES
    if (daemon->fdset_size_set_by_app
        && (((int) FD_SETSIZE) < daemon->fdset_size))
    {
      MHD_DLOG (daemon,
                _ ("MHD_run()/MHD_run_wait() called for daemon started with " \
                   "MHD_OPTION_APP_FD_SETSIZE option (%d). " \
                   "The library was compiled with smaller FD_SETSIZE (%d). " \
                   "Some socket FDs may be not processed. " \
                   "Use MHD_run_from_select2() instead of MHD_run() or " \
                   "do not use MHD_OPTION_APP_FD_SETSIZE option.\n"),
                daemon->fdset_size, (int) FD_SETSIZE);

      if (log)
      {
        fprintf(log, "WARNING: fdset_size_set_by_app=1, app_fdset_size=%d, compiled_FD_SETSIZE=%d\n",
                (int) daemon->fdset_size, (int) FD_SETSIZE);
        fflush(log);
      }
    }
#endif /* HAVE_MESSAGES */
#endif /* HAS_FD_SETSIZE_OVERRIDABLE */

    res = MHD_select (daemon, millisec);
    /* MHD_select does MHD_cleanup_connections already */

    if (log)
    {
      fprintf(log, "RETURN FROM select branch: res=%d\n", (int) res);
      fclose(log);
    }
  }
  return res;
}
