     potential modification of the function parameter 'millisec'. Logs
     are appended to /tmp/logs/3402.txt. */
  {
    FILE *log = fopen("/tmp/logs/3402.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "ENTER MHD_select: daemon=%p initial_millisec=%d\n", (void *)daemon, (int)millisec);
      fclose(log);
    }
  }

  timeout.tv_sec = 0;
  timeout.tv_usec = 0;
  if (daemon->shutdown)
    return MHD_NO;
  FD_ZERO (&rs);
  FD_ZERO (&ws);
  FD_ZERO (&es);
  maxsock = MHD_INVALID_SOCKET;
  err_state = MHD_NO;
