    {
      fprintf(log, "err_state != MHD_NO, setting millisec=0. old_millisec=%d daemon=%p err_state=%d\n", (int)millisec, (void *)daemon, err_state);
      fclose(log);
    }
    millisec = 0;
    /* log after assignment */
    log = fopen("/tmp/logs/3402.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "Assigned millisec=0 (after err_state). daemon=%p\n", (void *)daemon);
