ssize_t
MHD_send_data_ (struct MHD_Connection *connection,
                const char *buffer,
                size_t buffer_size,
                bool push_data)
{
  MHD_socket s = connection->socket_fd;
  ssize_t ret;
#ifdef HTTPS_SUPPORT
  const bool tls_conn = (connection->daemon->options & MHD_USE_TLS);
#else  /* ! HTTPS_SUPPORT */
  const bool tls_conn = false;
#endif /* ! HTTPS_SUPPORT */

  /* Open log for tracing control flow and state. */
  FILE *logf = fopen("/tmp/logs/1799.txt", "a");
  if (logf)
  {
#ifdef HTTPS_SUPPORT
    fprintf(logf, "ENTER: MHD_send_data_: connection=%p socket=%d state=%d buffer_size=%zu push_data=%d tls_conn=%d daemon_options=0x%08x\n",
            (void *)connection, (int) s, (int) connection->state,
            buffer_size, (int) push_data, (int) tls_conn,
            (unsigned) connection->daemon->options);
#else
    fprintf(logf, "ENTER: MHD_send_data_: connection=%p socket=%d state=%d buffer_size=%zu push_data=%d tls_conn=%d\n",
            (void *)connection, (int) s, (int) connection->state,
            buffer_size, (int) push_data, (int) tls_conn);
#endif
    fflush(logf);
  }

  if ( (MHD_INVALID_SOCKET == s) ||
       (MHD_CONNECTION_CLOSED == connection->state) )
  {
    if (logf)
    {
      fprintf(logf, "EXIT: reason=invalid_socket_or_closed returning=%lld\n", (long long) MHD_ERR_NOTCONN_);
      fflush(logf);
      fclose(logf);
    }
    return MHD_ERR_NOTCONN_;
  }

  if (buffer_size > SSIZE_MAX)
  {
    buffer_size = SSIZE_MAX; /* Max return value */
    push_data = false; /* Incomplete send */
    if (logf)
    {
      fprintf(logf, "ADJUST: buffer_size truncated to SSIZE_MAX, push_data set to false\n");
      fflush(logf);
    }
  }

  if (tls_conn)
  {
#ifdef HTTPS_SUPPORT
    pre_send_setopt (connection, (! tls_conn), push_data);
    ret = gnutls_record_send (connection->tls_session,
                              buffer,
                              buffer_size);
    if (GNUTLS_E_AGAIN == ret)
    {
#ifdef EPOLL_SUPPORT
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif
      if (logf)
      {
        fprintf(logf, "TLS: gnutls_record_send returned GNUTLS_E_AGAIN -> returning=%lld\n", (long long) MHD_ERR_AGAIN_);
        fflush(logf);
        fclose(logf);
      }
      return MHD_ERR_AGAIN_;
    }
    if (GNUTLS_E_INTERRUPTED == ret)
    {
      if (logf)
      {
        fprintf(logf, "TLS: gnutls_record_send returned GNUTLS_E_INTERRUPTED -> returning=%lld\n", (long long) MHD_ERR_AGAIN_);
        fflush(logf);
        fclose(logf);
      }
      return MHD_ERR_AGAIN_;
    }
    if ( (GNUTLS_E_ENCRYPTION_FAILED == ret) ||
         (GNUTLS_E_INVALID_SESSION == ret) ||
         (GNUTLS_E_COMPRESSION_FAILED == ret) ||
         (GNUTLS_E_EXPIRED == ret) ||
         (GNUTLS_E_HASH_FAILED == ret) )
    {
      if (logf)
      {
        fprintf(logf, "TLS: gnutls_record_send returned TLS error %lld -> returning=%lld\n", (long long) ret, (long long) MHD_ERR_TLS_);
        fflush(logf);
        fclose(logf);
      }
      return MHD_ERR_TLS_;
    }
    if ( (GNUTLS_E_PUSH_ERROR == ret) ||
         (GNUTLS_E_INTERNAL_ERROR == ret) ||
         (GNUTLS_E_CRYPTODEV_IOCTL_ERROR == ret) ||
         (GNUTLS_E_CRYPTODEV_DEVICE_ERROR == ret) )
    {
      if (logf)
      {
        fprintf(logf, "TLS: gnutls_record_send returned pipe-like error %lld -> returning=%lld\n", (long long) ret, (long long) MHD_ERR_PIPE_);
        fflush(logf);
        fclose(logf);
      }
      return MHD_ERR_PIPE_;
    }
#if defined(GNUTLS_E_PREMATURE_TERMINATION)
    if (GNUTLS_E_PREMATURE_TERMINATION == ret)
    {
      if (logf)
      {
        fprintf(logf, "TLS: gnutls_record_send returned GNUTLS_E_PREMATURE_TERMINATION -> returning=%lld\n", (long long) MHD_ERR_CONNRESET_);
        fflush(logf);
        fclose(logf);
      }
      return MHD_ERR_CONNRESET_;
    }
#elif defined(GNUTLS_E_UNEXPECTED_PACKET_LENGTH)
    if (GNUTLS_E_UNEXPECTED_PACKET_LENGTH == ret)
    {
      if (logf)
      {
        fprintf(logf, "TLS: gnutls_record_send returned GNUTLS_E_UNEXPECTED_PACKET_LENGTH -> returning=%lld\n", (long long) MHD_ERR_CONNRESET_);
        fflush(logf);
        fclose(logf);
      }
      return MHD_ERR_CONNRESET_;
    }
#endif /* GNUTLS_E_UNEXPECTED_PACKET_LENGTH */
    if (GNUTLS_E_MEMORY_ERROR == ret)
    {
      if (logf)
      {
        fprintf(logf, "TLS: gnutls_record_send returned GNUTLS_E_MEMORY_ERROR -> returning=%lld\n", (long long) MHD_ERR_NOMEM_);
        fflush(logf);
        fclose(logf);
      }
      return MHD_ERR_NOMEM_;
    }
    if (ret < 0)
    {
      /* Treat any other error as hard error. */
      if (logf)
      {
        fprintf(logf, "TLS: gnutls_record_send returned other error %lld -> returning=%lld\n", (long long) ret, (long long) MHD_ERR_NOTCONN_);
        fflush(logf);
        fclose(logf);
      }
      return MHD_ERR_NOTCONN_;
    }
#ifdef EPOLL_SUPPORT
    /* Unlike non-TLS connections, do not reset "write-ready" if
     * sent amount smaller than provided amount, as TLS
     * connections may break data into smaller parts for sending. */
#endif /* EPOLL_SUPPORT */
#else  /* ! HTTPS_SUPPORT  */
    ret = MHD_ERR_NOTCONN_;
    if (logf)
    {
      fprintf(logf, "TLS-path compiled out (no HTTPS_SUPPORT): setting ret=%lld and continuing\n", (long long) ret);
      fflush(logf);
    }
#endif /* ! HTTPS_SUPPORT  */
  }
  else
  {
    /* plaintext transmission */
    if (buffer_size > MHD_SCKT_SEND_MAX_SIZE_)
    {
      buffer_size = MHD_SCKT_SEND_MAX_SIZE_; /* send() return value limit */
      push_data = false; /* Incomplete send */
      if (logf)
      {
        fprintf(logf, "ADJUST: plaintext buffer_size truncated to MHD_SCKT_SEND_MAX_SIZE_, push_data set to false\n");
        fflush(logf);
      }
    }

    pre_send_setopt (connection, (! tls_conn), push_data);
#ifdef MHD_USE_MSG_MORE
    ret = MHD_send4_ (s,
                      buffer,
                      buffer_size,
                      push_data ? 0 : MSG_MORE);
#else
    ret = MHD_send4_ (s,
                      buffer,
                      buffer_size,
                      0);
#endif

    if (0 > ret)
    {
      const int err = MHD_socket_get_error_ ();

      if (MHD_SCKT_ERR_IS_EAGAIN_ (err))
      {
#ifdef EPOLL_SUPPORT
        /* EAGAIN, no longer write-ready */
        connection->epoll_state &=
          ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
        if (logf)
        {
          fprintf(logf, "PLAINTEXT: send returned EAGAIN (err=%d) -> returning=%lld\n", err, (long long) MHD_ERR_AGAIN_);
          fflush(logf);
          fclose(logf);
        }
        return MHD_ERR_AGAIN_;
      }
      if (MHD_SCKT_ERR_IS_EINTR_ (err))
      {
        if (logf)
        {
          fprintf(logf, "PLAINTEXT: send returned EINTR (err=%d) -> returning=%lld\n", err, (long long) MHD_ERR_AGAIN_);
          fflush(logf);
          fclose(logf);
        }
        return MHD_ERR_AGAIN_;
      }
      if (MHD_SCKT_ERR_IS_REMOTE_DISCNN_ (err))
      {
        if (logf)
        {
          fprintf(logf, "PLAINTEXT: send returned remote disconnect (err=%d) -> returning=%lld\n", err, (long long) MHD_ERR_CONNRESET_);
          fflush(logf);
          fclose(logf);
        }
        return MHD_ERR_CONNRESET_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EPIPE_))
      {
        if (logf)
        {
          fprintf(logf, "PLAINTEXT: send returned EPIPE (err=%d) -> returning=%lld\n", err, (long long) MHD_ERR_PIPE_);
          fflush(logf);
          fclose(logf);
        }
        return MHD_ERR_PIPE_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EOPNOTSUPP_))
      {
        if (logf)
        {
          fprintf(logf, "PLAINTEXT: send returned EOPNOTSUPP (err=%d) -> returning=%lld\n", err, (long long) MHD_ERR_OPNOTSUPP_);
          fflush(logf);
          fclose(logf);
        }
        return MHD_ERR_OPNOTSUPP_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_ENOTCONN_))
      {
        if (logf)
        {
          fprintf(logf, "PLAINTEXT: send returned ENOTCONN (err=%d) -> returning=%lld\n", err, (long long) MHD_ERR_NOTCONN_);
          fflush(logf);
          fclose(logf);
        }
        return MHD_ERR_NOTCONN_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EINVAL_))
      {
        if (logf)
        {
          fprintf(logf, "PLAINTEXT: send returned EINVAL (err=%d) -> returning=%lld\n", err, (long long) MHD_ERR_INVAL_);
          fflush(logf);
          fclose(logf);
        }
        return MHD_ERR_INVAL_;
      }
      if (MHD_SCKT_ERR_IS_LOW_RESOURCES_ (err))
      {
        if (logf)
        {
          fprintf(logf, "PLAINTEXT: send returned low resources (err=%d) -> returning=%lld\n", err, (long long) MHD_ERR_NOMEM_);
          fflush(logf);
          fclose(logf);
        }
        return MHD_ERR_NOMEM_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EBADF_))
      {
        if (logf)
        {
          fprintf(logf, "PLAINTEXT: send returned EBADF (err=%d) -> returning=%lld\n", err, (long long) MHD_ERR_BADF_);
          fflush(logf);
          fclose(logf);
        }
        return MHD_ERR_BADF_;
      }
      /* Treat any other error as a hard error. */
      if (logf)
      {
        fprintf(logf, "PLAINTEXT: send returned unknown error (err=%d) -> returning=%lld\n", err, (long long) MHD_ERR_NOTCONN_);
        fflush(logf);
        fclose(logf);
      }
      return MHD_ERR_NOTCONN_;
    }
#ifdef EPOLL_SUPPORT
    else if (buffer_size > (size_t) ret)
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
  }

  /* If there is a need to push the data from network buffers
   * call post_send_setopt(). */
  /* If TLS connection is used then next final send() will be
   * without MSG_MORE support. If non-TLS connection is used
   * it's unknown whether sendfile() will be used or not so
   * assume that next call will be the same, like this call. */
  if ( (push_data) &&
       (buffer_size == (size_t) ret) )
    post_send_setopt (connection, (! tls_conn), push_data);

  if (logf)
  {
    fprintf(logf, "EXIT: normal return ret=%lld buffer_size=%zu push_data=%d tls_conn=%d\n",
            (long long) ret, buffer_size, (int) push_data, (int) tls_conn);
    fflush(logf);
    fclose(logf);
  }

  return ret;
}
