ssize_t
MHD_send_data_ (struct MHD_Connection *connection,
                const char *buffer,
                size_t buffer_size,
                bool push_data)
{
  MHD_socket s = connection->socket_fd;
  ssize_t ret;
#ifdef HTTPS_SUPPORT
  const bool tls_conn = (connection->daemon->options & MHD_USE_TLS);
#else  /* ! HTTPS_SUPPORT */
  const bool tls_conn = false;
#endif /* ! HTTPS_SUPPORT */

  /* Logging probe */
  FILE *mhd_log_1796 = fopen("/tmp/logs/1796.txt", "a");
  if (mhd_log_1796)
  {
#ifdef HTTPS_SUPPORT
    fprintf(mhd_log_1796, "MHD_send_data_: compiled WITH HTTPS_SUPPORT\n");
#else
    fprintf(mhd_log_1796, "MHD_send_data_: compiled WITHOUT HTTPS_SUPPORT\n");
#endif
    fprintf(mhd_log_1796, "entry: connection=%p s=%p buffer_size=%zu push_data=%d tls_conn=%d\n",
            (void *) connection,
            (void *) (intptr_t) s,
            buffer_size,
            (int) push_data,
            (int) tls_conn);
    fflush(mhd_log_1796);
  }

  if ( (MHD_INVALID_SOCKET == s) ||
       (MHD_CONNECTION_CLOSED == connection->state) )
  {
    if (mhd_log_1796)
    {
      fprintf(mhd_log_1796, "early-return: NOTCONN (invalid socket or closed)\n");
      fclose(mhd_log_1796);
    }
    return MHD_ERR_NOTCONN_;
  }

  if (buffer_size > SSIZE_MAX)
  {
    buffer_size = SSIZE_MAX; /* Max return value */
    push_data = false; /* Incomplete send */
    if (mhd_log_1796)
    {
      fprintf(mhd_log_1796, "adjusted buffer_size to SSIZE_MAX and cleared push_data\n");
      fflush(mhd_log_1796);
    }
  }

  if (tls_conn)
  {
    if (mhd_log_1796)
    {
      fprintf(mhd_log_1796, "runtime: entering TLS branch (tls_conn == true)\n");
      fflush(mhd_log_1796);
    }
#ifdef HTTPS_SUPPORT
    pre_send_setopt (connection, (! tls_conn), push_data);
    ret = gnutls_record_send (connection->tls_session,
                              buffer,
                              buffer_size);
    if (GNUTLS_E_AGAIN == ret)
    {
#ifdef EPOLL_SUPPORT
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif
      if (mhd_log_1796)
      {
        fprintf(mhd_log_1796, "TLS gnutls_record_send: E_AGAIN -> MHD_ERR_AGAIN_\n");
        fclose(mhd_log_1796);
      }
      return MHD_ERR_AGAIN_;
    }
    if (GNUTLS_E_INTERRUPTED == ret)
    {
      if (mhd_log_1796)
      {
        fprintf(mhd_log_1796, "TLS gnutls_record_send: INTERRUPTED -> MHD_ERR_AGAIN_\n");
        fclose(mhd_log_1796);
      }
      return MHD_ERR_AGAIN_;
    }
    if ( (GNUTLS_E_ENCRYPTION_FAILED == ret) ||
         (GNUTLS_E_INVALID_SESSION == ret) ||
         (GNUTLS_E_COMPRESSION_FAILED == ret) ||
         (GNUTLS_E_EXPIRED == ret) ||
         (GNUTLS_E_HASH_FAILED == ret) )
    {
      if (mhd_log_1796)
      {
        fprintf(mhd_log_1796, "TLS gnutls_record_send: TLS error -> MHD_ERR_TLS_ (ret=%zd)\n", ret);
        fclose(mhd_log_1796);
      }
      return MHD_ERR_TLS_;
    }
    if ( (GNUTLS_E_PUSH_ERROR == ret) ||
         (GNUTLS_E_INTERNAL_ERROR == ret) ||
         (GNUTLS_E_CRYPTODEV_IOCTL_ERROR == ret) ||
         (GNUTLS_E_CRYPTODEV_DEVICE_ERROR == ret) )
    {
      if (mhd_log_1796)
      {
        fprintf(mhd_log_1796, "TLS gnutls_record_send: PIPE error -> MHD_ERR_PIPE_ (ret=%zd)\n", ret);
        fclose(mhd_log_1796);
      }
      return MHD_ERR_PIPE_;
    }
#if defined(GNUTLS_E_PREMATURE_TERMINATION)
    if (GNUTLS_E_PREMATURE_TERMINATION == ret)
    {
      if (mhd_log_1796)
      {
        fprintf(mhd_log_1796, "TLS gnutls_record_send: PREMATURE_TERMINATION -> MHD_ERR_CONNRESET_\n");
        fclose(mhd_log_1796);
      }
      return MHD_ERR_CONNRESET_;
    }
#elif defined(GNUTLS_E_UNEXPECTED_PACKET_LENGTH)
    if (GNUTLS_E_UNEXPECTED_PACKET_LENGTH == ret)
    {
      if (mhd_log_1796)
      {
        fprintf(mhd_log_1796, "TLS gnutls_record_send: UNEXPECTED_PACKET_LENGTH -> MHD_ERR_CONNRESET_\n");
        fclose(mhd_log_1796);
      }
      return MHD_ERR_CONNRESET_;
    }
#endif /* GNUTLS_E_UNEXPECTED_PACKET_LENGTH */
    if (GNUTLS_E_MEMORY_ERROR == ret)
    {
      if (mhd_log_1796)
      {
        fprintf(mhd_log_1796, "TLS gnutls_record_send: MEMORY_ERROR -> MHD_ERR_NOMEM_\n");
        fclose(mhd_log_1796);
      }
      return MHD_ERR_NOMEM_;
    }
    if (ret < 0)
    {
      /* Treat any other error as hard error. */
      if (mhd_log_1796)
      {
        fprintf(mhd_log_1796, "TLS gnutls_record_send: other error ret=%zd -> MHD_ERR_NOTCONN_\n", ret);
        fclose(mhd_log_1796);
      }
      return MHD_ERR_NOTCONN_;
    }
#ifdef EPOLL_SUPPORT
    /* Unlike non-TLS connections, do not reset "write-ready" if
     * sent amount smaller than provided amount, as TLS
     * connections may break data into smaller parts for sending. */
#endif /* EPOLL_SUPPORT */
#else  /* ! HTTPS_SUPPORT  */
    /* This assignment is present when compiled without HTTPS_SUPPORT
     * but the tls_conn variable is set to false under that compilation
     * mode, so the enclosing "if (tls_conn)" is unreachable at runtime.
     * We keep the original assignment to preserve behavior. */
    ret = MHD_ERR_NOTCONN_;
    if (mhd_log_1796)
    {
      fprintf(mhd_log_1796, "(compiled without HTTPS_SUPPORT) inside tls_conn branch: ret set to MHD_ERR_NOTCONN_ (this code is unreachable if tls_conn==false)\n");
      fflush(mhd_log_1796);
    }
#endif /* ! HTTPS_SUPPORT  */
  }
  else
  {
    /* plaintext transmission */
    if (mhd_log_1796)
    {
      fprintf(mhd_log_1796, "runtime: entering plaintext branch (tls_conn == false)\n");
      fflush(mhd_log_1796);
    }

    if (buffer_size > MHD_SCKT_SEND_MAX_SIZE_)
    {
      buffer_size = MHD_SCKT_SEND_MAX_SIZE_; /* send() return value limit */
      push_data = false; /* Incomplete send */
      if (mhd_log_1796)
      {
        fprintf(mhd_log_1796, "plaintext: adjusted buffer_size to MHD_SCKT_SEND_MAX_SIZE_ and cleared push_data\n");
        fflush(mhd_log_1796);
      }
    }

    pre_send_setopt (connection, (! tls_conn), push_data);
#ifdef MHD_USE_MSG_MORE
    if (mhd_log_1796)
      fprintf(mhd_log_1796, "plaintext: calling MHD_send4_ with MSG_MORE flag state\n");
    ret = MHD_send4_ (s,
                      buffer,
                      buffer_size,
                      push_data ? 0 : MSG_MORE);
#else
    if (mhd_log_1796)
      fprintf(mhd_log_1796, "plaintext: calling MHD_send4_ without MSG_MORE\n");
    ret = MHD_send4_ (s,
                      buffer,
                      buffer_size,
                      0);
#endif

    if (mhd_log_1796)
    {
      fprintf(mhd_log_1796, "plaintext: MHD_send4_ returned ret=%zd\n", ret);
      fflush(mhd_log_1796);
    }

    if (0 > ret)
    {
      const int err = MHD_socket_get_error_ ();

      if (MHD_SCKT_ERR_IS_EAGAIN_ (err))
      {
#ifdef EPOLL_SUPPORT
        /* EAGAIN, no longer write-ready */
        connection->epoll_state &=
          ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
        if (mhd_log_1796)
        {
          fprintf(mhd_log_1796, "plaintext: socket EAGAIN -> MHD_ERR_AGAIN_ (err=%d)\n", err);
          fclose(mhd_log_1796);
        }
        return MHD_ERR_AGAIN_;
      }
      if (MHD_SCKT_ERR_IS_EINTR_ (err))
      {
        if (mhd_log_1796)
        {
          fprintf(mhd_log_1796, "plaintext: socket EINTR -> MHD_ERR_AGAIN_ (err=%d)\n", err);
          fclose(mhd_log_1796);
        }
        return MHD_ERR_AGAIN_;
      }
      if (MHD_SCKT_ERR_IS_REMOTE_DISCNN_ (err))
      {
        if (mhd_log_1796)
        {
          fprintf(mhd_log_1796, "plaintext: remote disconnect -> MHD_ERR_CONNRESET_ (err=%d)\n", err);
          fclose(mhd_log_1796);
        }
        return MHD_ERR_CONNRESET_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EPIPE_))
      {
        if (mhd_log_1796)
        {
          fprintf(mhd_log_1796, "plaintext: EPIPE -> MHD_ERR_PIPE_ (err=%d)\n", err);
          fclose(mhd_log_1796);
        }
        return MHD_ERR_PIPE_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EOPNOTSUPP_))
      {
        if (mhd_log_1796)
        {
          fprintf(mhd_log_1796, "plaintext: EOPNOTSUPP -> MHD_ERR_OPNOTSUPP_ (err=%d)\n", err);
          fclose(mhd_log_1796);
        }
        return MHD_ERR_OPNOTSUPP_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_ENOTCONN_))
      {
        if (mhd_log_1796)
        {
          fprintf(mhd_log_1796, "plaintext: ENOTCONN -> MHD_ERR_NOTCONN_ (err=%d)\n", err);
          fclose(mhd_log_1796);
        }
        return MHD_ERR_NOTCONN_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EINVAL_))
      {
        if (mhd_log_1796)
        {
          fprintf(mhd_log_1796, "plaintext: EINVAL -> MHD_ERR_INVAL_ (err=%d)\n", err);
          fclose(mhd_log_1796);
        }
        return MHD_ERR_INVAL_;
      }
      if (MHD_SCKT_ERR_IS_LOW_RESOURCES_ (err))
      {
        if (mhd_log_1796)
        {
          fprintf(mhd_log_1796, "plaintext: low resources -> MHD_ERR_NOMEM_ (err=%d)\n", err);
          fclose(mhd_log_1796);
        }
        return MHD_ERR_NOMEM_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EBADF_))
      {
        if (mhd_log_1796)
        {
          fprintf(mhd_log_1796, "plaintext: EBADF -> MHD_ERR_BADF_ (err=%d)\n", err);
          fclose(mhd_log_1796);
        }
        return MHD_ERR_BADF_;
      }
      /* Treat any other error as a hard error. */
      if (mhd_log_1796)
      {
        fprintf(mhd_log_1796, "plaintext: other socket error err=%d -> MHD_ERR_NOTCONN_\n", err);
        fclose(mhd_log_1796);
      }
      return MHD_ERR_NOTCONN_;
    }
#ifdef EPOLL_SUPPORT
    else if (buffer_size > (size_t) ret)
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
  }

  /* If there is a need to push the data from network buffers
   * call post_send_setopt(). */
  /* If TLS connection is used then next final send() will be
   * without MSG_MORE support. If non-TLS connection is used
   * it's unknown whether sendfile() will be used or not so
   * assume that next call will be the same, like this call. */
  if ( (push_data) &&
       (buffer_size == (size_t) ret) )
  {
    if (mhd_log_1796)
      fprintf(mhd_log_1796, "calling post_send_setopt (push_data true and buffer_size==ret)\n");
    post_send_setopt (connection, (! tls_conn), push_data);
  }

  if (mhd_log_1796)
  {
    fprintf(mhd_log_1796, "final-return: ret=%zd\n", ret);
    fclose(mhd_log_1796);
  }

  return ret;
}
