ssize_t
MHD_send_hdr_and_body_ (struct MHD_Connection *connection,
                        const char *header,
                        size_t header_size,
                        bool never_push_hdr,
                        const char *body,
                        size_t body_size,
                        bool complete_response)
{
  ssize_t ret;
  bool push_hdr;
  bool push_body;
  MHD_socket s = connection->socket_fd;
#ifndef _WIN32
#define _MHD_SEND_VEC_MAX   MHD_SCKT_SEND_MAX_SIZE_
#else  /* ! _WIN32 */
#define _MHD_SEND_VEC_MAX   UINT32_MAX
#endif /* ! _WIN32 */
#ifdef MHD_VECT_SEND
#if defined(HAVE_SENDMSG) || defined(HAVE_WRITEV)
  struct iovec vector[2];
#ifdef HAVE_SENDMSG
  struct msghdr msg;
#endif /* HAVE_SENDMSG */
#endif /* HAVE_SENDMSG || HAVE_WRITEV */
#ifdef _WIN32
  WSABUF vector[2];
  DWORD vec_sent;
#endif /* _WIN32 */
  bool no_vec; /* Is vector-send() disallowed? */

  no_vec = false;
#ifdef HTTPS_SUPPORT
  no_vec = no_vec || (connection->daemon->options & MHD_USE_TLS);
#endif /* HTTPS_SUPPORT */
#if (! defined(HAVE_SENDMSG) || ! defined(MSG_NOSIGNAL) ) && \
  defined(MHD_SEND_SPIPE_SEND_SUPPRESS_POSSIBLE) && \
  defined(MHD_SEND_SPIPE_SUPPRESS_NEEDED)
  no_vec = no_vec || (! connection->daemon->sigpipe_blocked &&
                      ! connection->sk_spipe_suppress);
#endif /* (!HAVE_SENDMSG || ! MSG_NOSIGNAL) &&
          MHD_SEND_SPIPE_SEND_SUPPRESS_POSSIBLE &&
          MHD_SEND_SPIPE_SUPPRESS_NEEDED */
#endif /* MHD_VECT_SEND */

  /* Open a log file to record control flow and state for reproducing
   * the const-cast scenario. Use append mode so multiple calls accumulate.
   */
  FILE *log = fopen("/tmp/logs/2445.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER: MHD_send_hdr_and_body_: connection=%p socket=%d header=%p header_size=%zu never_push_hdr=%d body=%p body_size=%zu complete_response=%d\n",
            (void *) connection, (int) s, (const void *) header, header_size,
            (int) never_push_hdr, (const void *) body, body_size,
            (int) complete_response);
    fflush(log);
  }

  mhd_assert ( (NULL != body) || (0 == body_size) );

  if ( (MHD_INVALID_SOCKET == s) ||
       (MHD_CONNECTION_CLOSED == connection->state) )
  {
    if (log)
    {
      fprintf(log, "RETURN: invalid socket or connection closed -> %d\n", (int) MHD_ERR_NOTCONN_);
      fflush(log);
    }
    return MHD_ERR_NOTCONN_;
  }

  push_body = complete_response;

  if (! never_push_hdr)
  {
    if (! complete_response)
      push_hdr = true; /* Push the header as the client may react
                        * on header alone while the body data is
                        * being prepared. */
    else
    {
      if (1400 > (header_size + body_size))
        push_hdr = false;  /* Do not push the header as complete
                           * reply is already ready and the whole
                           * reply most probably will fit into
                           * the single IP packet. */
      else
        push_hdr = true;   /* Push header alone so client may react
                           * on it while reply body is being delivered. */
    }
  }
  else
    push_hdr = false;

  if (complete_response && (0 == body_size))
    push_hdr = true; /* The header alone is equal to the whole response. */

  if (
#ifdef MHD_VECT_SEND
    (no_vec) ||
    (0 == body_size) ||
    ((size_t) SSIZE_MAX <= header_size) ||
    ((size_t) _MHD_SEND_VEC_MAX < header_size)
#ifdef _WIN32
    || ((size_t) UINT_MAX < header_size)
#endif /* _WIN32 */
#else  /* ! MHD_VECT_SEND */
    true
#endif /* ! MHD_VECT_SEND */
    )
  {
    if (log)
    {
      fprintf(log, "PATH: non-vector send chosen: no_vec=%d header_size=%zu body_size=%zu push_hdr=%d push_body=%d\n",
              (int) ( 
#ifdef MHD_VECT_SEND
                no_vec
#else
                1
#endif
              ), header_size, body_size, (int) push_hdr, (int) push_body);
      fflush(log);
    }

    ret = MHD_send_data_ (connection,
                          header,
                          header_size,
                          push_hdr);

    if (log)
    {
      fprintf(log, "AFTER MHD_send_data_: ret=%zd header_size=%zu\n", (ssize_t) ret, header_size);
      fflush(log);
    }

    if ( (header_size == (size_t) ret) &&
         ((size_t) SSIZE_MAX > header_size) &&
         (0 != body_size) &&
         (connection->sk_nonblck) )
    {
      ssize_t ret2;
      /* The header has been sent completely.
       * Try to send the reply body without waiting for
       * the next round. */
      /* Make sure that sum of ret + ret2 will not exceed SSIZE_MAX as
       * function needs to return positive value if succeed. */
      if ( (((size_t) SSIZE_MAX) - ((size_t) ret)) <  body_size)
      {
        body_size = (((size_t) SSIZE_MAX) - ((size_t) ret));
        complete_response = false;
        push_body = complete_response;
      }

      if (log)
      {
        fprintf(log, "ATTEMPT send body without waiting: body_size=%zu push_body=%d\n", body_size, (int) push_body);
        fflush(log);
      }

      ret2 = MHD_send_data_ (connection,
                             body,
                             body_size,
                             push_body);
      if (log)
      {
        fprintf(log, "AFTER MHD_send_data_(body): ret2=%zd\n", (ssize_t) ret2);
        fflush(log);
      }
      if (0 < ret2)
      {
        if (log)
        {
          fprintf(log, "RETURN: total bytes sent (ret + ret2) = %zd\n", (ssize_t) (ret + ret2));
          fflush(log);
        }
        return ret + ret2; /* Total data sent */
      }
      if (MHD_ERR_AGAIN_ == ret2)
      {
        if (log)
        {
          fprintf(log, "RETURN: would block, returning MHD_ERR_AGAIN_=%d\n", (int) MHD_ERR_AGAIN_);
          fflush(log);
        }
        return ret;
      }

      if (log)
      {
        fprintf(log, "RETURN: error code from second send = %zd\n", (ssize_t) ret2);
        fflush(log);
      }
      return ret2; /* Error code */
    }
    if (log)
    {
      fprintf(log, "RETURN: non-vector send returned ret=%zd\n", (ssize_t) ret);
      fflush(log);
    }
    return ret;
  }
#ifdef MHD_VECT_SEND

  if ( ((size_t) SSIZE_MAX <= body_size) ||
       ((size_t) SSIZE_MAX < (header_size + body_size)) )
  {
    /* Return value limit */
    body_size = SSIZE_MAX - header_size;
    complete_response = false;
    push_body = complete_response;
  }
#if (SSIZE_MAX != _MHD_SEND_VEC_MAX) || (_MHD_SEND_VEC_MAX + 0 == 0)
  if (((size_t) _MHD_SEND_VEC_MAX <= body_size) ||
      ((size_t) _MHD_SEND_VEC_MAX < (header_size + body_size)))
  {
    /* Send total amount limit */
    body_size = _MHD_SEND_VEC_MAX - header_size;
    complete_response = false;
    push_body = complete_response;
  }
#endif /* SSIZE_MAX != _MHD_SEND_VEC_MAX */

  pre_send_setopt (connection,
#ifdef HAVE_SENDMSG
                   true,
#else  /* ! HAVE_SENDMSG */
                   false,
#endif /* ! HAVE_SENDMSG */
                   push_hdr || push_body);
#if defined(HAVE_SENDMSG) || defined(HAVE_WRITEV)
  if (log)
  {
    fprintf(log, "VECTOR PATH: preparing vector send. header=%p body=%p header_size=%zu body_size=%zu\n",
            (const void *) header, (const void *) body, header_size, body_size);
    fflush(log);
  }
  vector[0].iov_base = _MHD_DROP_CONST (header);
  vector[0].iov_len = header_size;

  /* Probe: log the moment when const qualification is dropped for 'body'. */
  if (log)
  {
    fprintf(log, "About to assign vector[1].iov_base = _MHD_DROP_CONST(body). body pointer=%p\n", (const void *) body);
    if (body != NULL && body_size > 0)
    {
      /* Safely log first byte (as an unsigned int) to show content without altering it. */
      unsigned char fc = (unsigned char) body[0];
      fprintf(log, "body_first_byte=0x%02x ('%c')\n", (unsigned int) fc, (fc >= 0x20 && fc < 0x7f) ? (char) fc : '?');
    }
    fflush(log);
  }

  vector[1].iov_base = _MHD_DROP_CONST (body);
  vector[1].iov_len = body_size;

  if (log)
  {
    fprintf(log, "After assignment: vector[0].iov_base=%p vector[1].iov_base=%p\n",
            vector[0].iov_base, vector[1].iov_base);
    fflush(log);
  }

#if defined(HAVE_SENDMSG)
  memset (&msg, 0, sizeof(msg));
  msg.msg_iov = vector;
  msg.msg_iovlen = 2;

  if (log)
  {
    fprintf(log, "Calling sendmsg(s=%d, msg.msg_iovlen=%d)\n", (int) s, (int) msg.msg_iovlen);
    fflush(log);
  }

  ret = sendmsg (s, &msg, MSG_NOSIGNAL_OR_ZERO);
#elif defined(HAVE_WRITEV)
  if (log)
  {
    fprintf(log, "Calling writev(s=%d, iovcnt=2)\n", (int) s);
    fflush(log);
  }
  ret = writev (s, vector, 2);
#endif /* HAVE_WRITEV */
#endif /* HAVE_SENDMSG || HAVE_WRITEV */
#ifdef _WIN32
  if ((size_t) UINT_MAX < body_size)
  {
    /* Send item size limit */
    body_size = UINT_MAX;
    complete_response = false;
    push_body = complete_response;
  }
  vector[0].buf = (char *) _MHD_DROP_CONST (header);
  vector[0].len = (unsigned long) header_size;
  vector[1].buf = (char *) _MHD_DROP_CONST (body);
  vector[1].len = (unsigned long) body_size;

  if (log)
  {
    fprintf(log, "Calling WSASend(s=%d, bufcnt=2)\n", (int) s);
    fflush(log);
  }
  ret = WSASend (s, vector, 2, &vec_sent, 0, NULL, NULL);
  if (0 == ret)
    ret = (ssize_t) vec_sent;
  else
    ret = -1;
#endif /* _WIN32 */

  if (0 > ret)
  {
    const int err = MHD_socket_get_error_ ();

    if (MHD_SCKT_ERR_IS_EAGAIN_ (err))
    {
#ifdef EPOLL_SUPPORT
      /* EAGAIN, no longer write-ready */
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
      if (log)
      {
        fprintf(log, "RETURN: send returned would block -> MHD_ERR_AGAIN_=%d err=%d\n", (int) MHD_ERR_AGAIN_, err);
        fflush(log);
      }
      return MHD_ERR_AGAIN_;
    }
    if (MHD_SCKT_ERR_IS_EINTR_ (err))
    {
      if (log)
      {
        fprintf(log, "RETURN: send interrupted (EINTR) -> MHD_ERR_AGAIN_=%d err=%d\n", (int) MHD_ERR_AGAIN_, err);
        fflush(log);
      }
      return MHD_ERR_AGAIN_;
    }
    if (MHD_SCKT_ERR_IS_REMOTE_DISCNN_ (err))
    {
      if (log)
      {
        fprintf(log, "RETURN: remote disconnect -> MHD_ERR_CONNRESET_ err=%d\n", err);
        fflush(log);
      }
      return MHD_ERR_CONNRESET_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EPIPE_))
    {
      if (log)
      {
        fprintf(log, "RETURN: EPIPE -> MHD_ERR_PIPE_ err=%d\n", err);
        fflush(log);
      }
      return MHD_ERR_PIPE_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EOPNOTSUPP_))
    {
      if (log)
      {
        fprintf(log, "RETURN: EOPNOTSUPP -> MHD_ERR_OPNOTSUPP_ err=%d\n", err);
        fflush(log);
      }
      return MHD_ERR_OPNOTSUPP_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_ENOTCONN_))
    {
      if (log)
      {
        fprintf(log, "RETURN: ENOTCONN -> MHD_ERR_NOTCONN_ err=%d\n", err);
        fflush(log);
      }
      return MHD_ERR_NOTCONN_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EINVAL_))
    {
      if (log)
      {
        fprintf(log, "RETURN: EINVAL -> MHD_ERR_INVAL_ err=%d\n", err);
        fflush(log);
      }
      return MHD_ERR_INVAL_;
    }
    if (MHD_SCKT_ERR_IS_LOW_RESOURCES_ (err))
    {
      if (log)
      {
        fprintf(log, "RETURN: low resources -> MHD_ERR_NOMEM_ err=%d\n", err);
        fflush(log);
      }
      return MHD_ERR_NOMEM_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EBADF_))
    {
      if (log)
      {
        fprintf(log, "RETURN: EBADF -> MHD_ERR_BADF_ err=%d\n", err);
        fflush(log);
      }
      return MHD_ERR_BADF_;
    }
    /* Treat any other error as a hard error. */
    if (log)
    {
      fprintf(log, "RETURN: unknown socket error -> MHD_ERR_NOTCONN_ err=%d\n", err);
      fflush(log);
    }
    return MHD_ERR_NOTCONN_;
  }
#ifdef EPOLL_SUPPORT
  else if ((header_size + body_size) > (size_t) ret)
    connection->epoll_state &=
      ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */

  /* If there is a need to push the data from network buffers
   * call post_send_setopt(). */
  if ( (push_body) &&
       ((header_size + body_size) == (size_t) ret) )
  {
    /* Complete reply has been sent. */
    /* If TLS connection is used then next final send() will be
     * without MSG_MORE support. If non-TLS connection is used
     * it's unknown whether next 'send' will be plain send() / sendmsg() or
     * sendfile() will be used so assume that next final send() will be
     * the same, like for this response. */
    post_send_setopt (connection,
#ifdef HAVE_SENDMSG
                      true,
#else  /* ! HAVE_SENDMSG */
                      false,
#endif /* ! HAVE_SENDMSG */
                      true);
  }
  else if ( (push_hdr) &&
            (header_size <= (size_t) ret))
  {
    /* The header has been sent completely and there is a
     * need to push the header data. */
    /* Luckily the type of send function will be used next is known. */
    post_send_setopt (connection,
#if defined(_MHD_HAVE_SENDFILE)
                      MHD_resp_sender_std == connection->rp.resp_sender,
#else  /* ! _MHD_HAVE_SENDFILE */
                      true,
#endif /* ! _MHD_HAVE_SENDFILE */
                      true);
  }

  if (log)
  {
    fprintf(log, "RETURN: vector send ret=%zd (header_size=%zu body_size=%zu)\n", (ssize_t) ret, header_size, body_size);
    fflush(log);
  }

  return ret;
#else  /* ! MHD_VECT_SEND */
  mhd_assert (false);
  if (log)
  {
    fprintf(log, "RETURN: unreachable (no vector send support) -> MHD_ERR_CONNRESET_\n");
    fflush(log);
  }
  return MHD_ERR_CONNRESET_; /* Unreachable. Mute warnings. */
#endif /* ! MHD_VECT_SEND */
}
