ssize_t
MHD_send_iovec_ (struct MHD_Connection *connection,
                 struct MHD_iovec_track_ *const r_iov,
                 bool push_data)
{
  /* Logging probe to capture control flow and state for Rule 8.13 investigation. */
  FILE *mhd_log = fopen("/tmp/logs/4405.txt", "a");
  if (mhd_log)
    {
      fprintf(mhd_log, "MHD_send_iovec_ entered: connection=%p, r_iov=%p, push_data=%d\n",
              (void *) connection, (void *) r_iov, (int) push_data);
      if (connection)
        {
          /* print response iov info if available */
          fprintf(mhd_log, " connection->rp.resp_iov.iov=%p\n",
                  (void *) connection->rp.resp_iov.iov);
          fprintf(mhd_log, " connection->rp.response=%p\n",
                  (void *) connection->rp.response);
          if (connection->rp.response)
            fprintf(mhd_log, " connection->rp.response->data_iov=%p\n",
                    (void *) connection->rp.response->data_iov);
          fprintf(mhd_log, " connection->rp.resp_iov.cnt=%zu\n",
                  (size_t) connection->rp.resp_iov.cnt);
          fprintf(mhd_log, " connection->rp.resp_iov.sent=%zu\n",
                  (size_t) connection->rp.resp_iov.sent);

          /* daemon pointer and relevant members that are implicated in the violation scenario */
          fprintf(mhd_log, " connection->daemon=%p\n",
                  (void *) connection->daemon);
          if (connection->daemon)
            {
              /* options and sigpipe_blocked are read in the original code; log them */
              fprintf(mhd_log, " connection->daemon->options=0x%lx\n",
                      (unsigned long) connection->daemon->options);
              fprintf(mhd_log, " connection->daemon->sigpipe_blocked=%d\n",
                      (int) connection->daemon->sigpipe_blocked);
            }

          /* sk_spipe_suppress is also used in the decision path */
          fprintf(mhd_log, " connection->sk_spipe_suppress=%d\n",
                  (int) connection->sk_spipe_suppress);
        }
      fflush(mhd_log);
      fclose(mhd_log);
    }

#ifdef MHD_VECT_SEND
#if defined(HTTPS_SUPPORT) || \
  defined(_MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED)
  bool use_iov_send = true;
#endif /* HTTPS_SUPPORT || _MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED */
#endif /* MHD_VECT_SEND */

  mhd_assert (NULL != connection->rp.resp_iov.iov);
  mhd_assert (NULL != connection->rp.response->data_iov);
  mhd_assert (connection->rp.resp_iov.cnt > connection->rp.resp_iov.sent);
#ifdef MHD_VECT_SEND
#if defined(HTTPS_SUPPORT) || \
  defined(_MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED)
#ifdef HTTPS_SUPPORT
  use_iov_send = use_iov_send &&
                 (0 == (connection->daemon->options & MHD_USE_TLS));
#endif /* HTTPS_SUPPORT */
#ifdef _MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED
  use_iov_send = use_iov_send && (connection->daemon->sigpipe_blocked ||
                                  connection->sk_spipe_suppress);
#endif /* _MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED */
  if (use_iov_send)
#endif /* HTTPS_SUPPORT || _MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED */
  return send_iov_nontls (connection, r_iov, push_data);
#endif /* MHD_VECT_SEND */

#if ! defined(MHD_VECT_SEND) || defined(HTTPS_SUPPORT) || \
  defined(_MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED)
  return send_iov_emu (connection, r_iov, push_data);
#endif /* !MHD_VECT_SEND || HTTPS_SUPPORT
          || _MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED */
}
