ssize_t
MHD_send_iovec_ (struct MHD_Connection *connection,
                 struct MHD_iovec_track_ *const r_iov,
                 bool push_data)
{
#ifdef MHD_VECT_SEND
#if defined(HTTPS_SUPPORT) || \
  defined(_MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED)
  bool use_iov_send = true;
#endif /* HTTPS_SUPPORT || _MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED */
#endif /* MHD_VECT_SEND */

  /* Logging probe: record control flow entry and key state into a file
     so other agents can reproduce and inspect the suspected const-qualification
     violation scenario. This is intentionally conservative and checks for NULL
     before dereferencing to avoid introducing crashes. */
  {
    FILE *log = fopen("/tmp/logs/4405.txt", "a");
    if (log)
      {
        fprintf(log, "MHD_send_iovec_ entered: connection=%p, r_iov=%p, push_data=%d\n",
                (void *) connection, (void *) r_iov, (int) push_data);
        if (NULL == connection)
          {
            fprintf(log, " connection is NULL\n");
          }
        else
          {
            /* rp (response packet) may be a struct inside connection; print addresses
               and check members carefully to avoid deref of NULL pointers. */
            fprintf(log, " connection->daemon=%p\n", (void *) connection->daemon);
            /* resp_iov is expected to be a struct with members iov, cnt, sent */
            fprintf(log, " connection->rp at %p\n", (void *) &connection->rp);
            fprintf(log, "  resp_iov.iov=%p, resp_iov.cnt=%lu, resp_iov.sent=%lu\n",
                    (void *) connection->rp.resp_iov.iov,
                    (unsigned long) connection->rp.resp_iov.cnt,
                    (unsigned long) connection->rp.resp_iov.sent);
            if (NULL == connection->rp.response)
              {
                fprintf(log, "  response=NULL\n");
              }
            else
              {
                fprintf(log, "  response=%p, response->data_iov=%p\n",
                        (void *) connection->rp.response,
                        (void *) connection->rp.response->data_iov);
              }
#ifdef HTTPS_SUPPORT
            /* If HTTPS_SUPPORT is defined, attempt to log daemon options (if daemon present). */
            if (connection->daemon)
              fprintf(log, " HTTPS_SUPPORT defined, daemon->options=0x%08x\n",
                      (unsigned) connection->daemon->options);
            else
              fprintf(log, " HTTPS_SUPPORT defined, daemon=NULL\n");
#endif /* HTTPS_SUPPORT */
#ifdef _MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED
            if (connection->daemon)
              fprintf(log, " _MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED defined, sigpipe_blocked=%d, sk_spipe_suppress=%d\n",
                      (int) connection->daemon->sigpipe_blocked,
                      (int) connection->sk_spipe_suppress);
            else
              fprintf(log, " _MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED defined, daemon=NULL\n");
#endif /* _MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED */
          }
        fflush(log);
        fclose(log);
      }
  }

  mhd_assert (NULL != connection->rp.resp_iov.iov);
  mhd_assert (NULL != connection->rp.response->data_iov);
  mhd_assert (connection->rp.resp_iov.cnt > connection->rp.resp_iov.sent);
#ifdef MHD_VECT_SEND
#if defined(HTTPS_SUPPORT) || \
  defined(_MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED)
#ifdef HTTPS_SUPPORT
  use_iov_send = use_iov_send &&
                 (0 == (connection->daemon->options & MHD_USE_TLS));
#endif /* HTTPS_SUPPORT */
#ifdef _MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED
  use_iov_send = use_iov_send && (connection->daemon->sigpipe_blocked ||
                                  connection->sk_spipe_suppress);
#endif /* _MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED */
  if (use_iov_send)
#endif /* HTTPS_SUPPORT || _MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED */
  return send_iov_nontls (connection, r_iov, push_data);
#endif /* MHD_VECT_SEND */

#if ! defined(MHD_VECT_SEND) || defined(HTTPS_SUPPORT) || \
  defined(_MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED)
  return send_iov_emu (connection, r_iov, push_data);
#endif /* !MHD_VECT_SEND || HTTPS_SUPPORT
          || _MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED */
}
