ssize_t
MHD_send_iovec_ (struct MHD_Connection *connection,
                 struct MHD_iovec_track_ *const r_iov,
                 bool push_data)
{
    /* Logging probe: write control-flow and state information to file for
       diagnosing potential const-qualification issues and to trace which
       send path is taken. */
    FILE *logf = fopen("/tmp/logs/4405.txt", "a");
    if (NULL != logf)
    {
        fprintf(logf, "ENTER MHD_send_iovec_: connection=%p, r_iov=%p, push_data=%d\n",
                (void *)connection, (void *)r_iov, (int)push_data);
        fflush(logf);
    }

#ifdef MHD_VECT_SEND
#if defined(HTTPS_SUPPORT) || \
  defined(_MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED)
  bool use_iov_send = true;
#endif /* HTTPS_SUPPORT || _MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED */
#endif /* MHD_VECT_SEND */

  mhd_assert (NULL != connection->rp.resp_iov.iov);
  mhd_assert (NULL != connection->rp.response->data_iov);
  mhd_assert (connection->rp.resp_iov.cnt > connection->rp.resp_iov.sent);

    if (NULL != logf)
    {
        /* Log some connection/rp state used by the function (addresses and
           numeric fields). Avoid dereferencing unknown pointee internals that
           may not be stable across builds. */
        fprintf(logf, "rp.resp_iov.iov=%p, rp.response->data_iov=%p, resp_cnt=%ld, resp_sent=%ld\n",
                (void *)connection->rp.resp_iov.iov,
                (void *)connection->rp.response->data_iov,
                (long)connection->rp.resp_iov.cnt,
                (long)connection->rp.resp_iov.sent);
        fflush(logf);
    }

#ifdef MHD_VECT_SEND
#if defined(HTTPS_SUPPORT) || \
  defined(_MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED)
#ifdef HTTPS_SUPPORT
  use_iov_send = use_iov_send &&
                 (0 == (connection->daemon->options & MHD_USE_TLS));
#endif /* HTTPS_SUPPORT */
#ifdef _MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED
  use_iov_send = use_iov_send && (connection->daemon->sigpipe_blocked ||
                                  connection->sk_spipe_suppress);
#endif /* _MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED */
  if (use_iov_send)
  {
      if (NULL != logf)
      {
          fprintf(logf, "CHOICE: send_iov_nontls chosen: use_iov_send=%d, connection=%p, r_iov=%p, push_data=%d\n",
                  (int)use_iov_send, (void *)connection, (void *)r_iov, (int)push_data);
          fflush(logf);
          fclose(logf);
      }
      return send_iov_nontls (connection, r_iov, push_data);
  }
#endif /* HTTPS_SUPPORT || _MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED */
  /* If we get here under MHD_VECT_SEND, fall through to emulation path. */
  if (NULL != logf)
  {
      fprintf(logf, "FALLTHROUGH: send_iov_emu will be used (MHD_VECT_SEND path)\n");
      fflush(logf);
      fclose(logf);
  }
  return send_iov_emu (connection, r_iov, push_data);
#endif /* MHD_VECT_SEND */

#if ! defined(MHD_VECT_SEND) || defined(HTTPS_SUPPORT) || \
  defined(_MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED)
    if (NULL != logf)
    {
        fprintf(logf, "CHOICE: send_iov_emu chosen: connection=%p, r_iov=%p, push_data=%d\n",
                (void *)connection, (void *)r_iov, (int)push_data);
        fflush(logf);
        fclose(logf);
    }
    return send_iov_emu (connection, r_iov, push_data);
#endif /* !MHD_VECT_SEND || HTTPS_SUPPORT
          || _MHD_VECT_SEND_NEEDS_SPIPE_SUPPRESSED */
}
