_MHD_EXTERN enum MHD_Result
MHD_set_connection_value (struct MHD_Connection *connection,
                          enum MHD_ValueKind kind,
                          const char *key,
                          const char *value)
{
  enum MHD_Result res;
  size_t key_len = 0;
  size_t value_len = 0;
  FILE *log = fopen("/tmp/logs/3253.txt", "a");

  if (log)
    {
      fprintf(log, "MHD_set_connection_value: entered\n");
      fprintf(log, "  connection=%p, kind=%d, key=%p, value=%p\n",
              (void *) connection, (int) kind, (void *) key, (void *) value);
      fflush(log);
    }

  if (NULL != key)
    {
      /* Preserve original behavior: compute length only when key is non-NULL. */
      key_len = strlen (key);
      if (log)
        {
          fprintf(log, "  key non-NULL: address=%p, strlen(key)=%zu\n",
                  (void *) key, key_len);
          fflush(log);
        }
    }
  else
    {
      if (log)
        {
          fprintf(log, "  key is NULL\n");
          fflush(log);
        }
    }

  if (NULL != value)
    {
      /* Preserve original behavior: compute length only when value is non-NULL. */
      value_len = strlen (value);
      if (log)
        {
          fprintf(log, "  value non-NULL: address=%p, strlen(value)=%zu\n",
                  (void *) value, value_len);
          fflush(log);
        }
    }
  else
    {
      if (log)
        {
          fprintf(log, "  value is NULL\n");
          fflush(log);
        }
    }

  if (log)
    {
      fprintf(log, "  calling MHD_set_connection_value_n_nocheck_: key_len=%zu, value_len=%zu\n",
              key_len, value_len);
      fclose(log);
    }

  res = MHD_set_connection_value_n_nocheck_ (connection,
                                             kind,
                                             key,
                                             key_len,
                                             value,
                                             value_len);
  return res;
}
