_MHD_EXTERN enum MHD_Result
MHD_set_connection_value (struct MHD_Connection *connection,
                          enum MHD_ValueKind kind,
                          const char *key,
                          const char *value)
{
  /* Compute lengths exactly as the original implementation did. */
  size_t key_len = NULL != key ? strlen (key) : 0;
  size_t value_len = NULL != value ? strlen (value) : 0;

  /* Logging probe: append a record describing the call and computed state. */
  {
    FILE *logf = fopen("/tmp/logs/3255.txt", "a");
    if (logf != NULL)
    {
      fprintf(logf, "MHD_set_connection_value ENTER\n");
      fprintf(logf, "  connection=%p, kind=%d\n", (void *) connection, (int) kind);
      fprintf(logf, "  key=%p, value=%p\n", (const void *) key, (const void *) value);
      fprintf(logf, "  key_is_null=%d, value_is_null=%d\n", (key == NULL), (value == NULL));
      fprintf(logf, "  key_len=%zu, value_len=%zu\n", key_len, value_len);
      fflush(logf);
      /* Keep the file open until after the callee returns so we can log the result. */
      enum MHD_Result result = MHD_set_connection_value_n_nocheck_ (connection,
                                                                   kind,
                                                                   key,
                                                                   key_len,
                                                                   value,
                                                                   value_len);
      fprintf(logf, "  callee returned=%d\n", (int) result);
      fprintf(logf, "MHD_set_connection_value EXIT\n\n");
      fclose(logf);
      return result;
    }
  }

  /* Fallback: if logging could not open the file, just call the original logic. */
  return MHD_set_connection_value_n_nocheck_ (connection,
                                              kind,
                                              key,
                                              key_len,
                                              value,
                                              value_len);
}
