MHD_socket
MHD_socket_create_listen_ (int pf)
{
  MHD_socket fd;
  int cloexec_set;
#if defined(SOCK_NOSIGPIPE) || defined(MHD_socket_nosignal_)
  int nosigpipe_set;
#endif /* SOCK_NOSIGPIPE ||  MHD_socket_nosignal_ */

  /* Basic entry probe */
  {
    FILE *log = fopen("/tmp/logs/359.txt", "a");
    if (log)
    {
      fprintf(log, "ENTER: MHD_socket_create_listen_(pf=%d)\n", pf);
      fclose(log);
    }
  }

#if defined(MHD_POSIX_SOCKETS) && (defined(SOCK_CLOEXEC) || \
  defined(SOCK_NOSIGPIPE) )

  fd = socket (pf,
               SOCK_STREAM | SOCK_CLOEXEC | SOCK_NOSIGPIPE_OR_ZERO,
               0);
  cloexec_set = (SOCK_CLOEXEC_OR_ZERO != 0);
#if defined(SOCK_NOSIGPIPE) || defined(MHD_socket_nosignal_)
  nosigpipe_set = (SOCK_NOSIGPIPE_OR_ZERO != 0);
#endif /* SOCK_NOSIGPIPE ||  MHD_socket_nosignal_ */
#elif defined(MHD_WINSOCK_SOCKETS) && defined(WSA_FLAG_NO_HANDLE_INHERIT)
  fd = WSASocketW (pf,
                   SOCK_STREAM,
                   0,
                   NULL,
                   0,
                   WSA_FLAG_OVERLAPPED | WSA_FLAG_NO_HANDLE_INHERIT);
  cloexec_set = ! 0;
#else  /* No special socket init function / flags */
  fd = MHD_INVALID_SOCKET;
  cloexec_set = 0;
#if defined(SOCK_NOSIGPIPE) || defined(MHD_socket_nosignal_)
  nosigpipe_set = 0;
#endif /* SOCK_NOSIGPIPE ||  MHD_socket_nosignal_ */
#endif /* No special socket init function / flags */

  /* Log state after initial socket creation/flags setup */
  {
    FILE *log = fopen("/tmp/logs/359.txt", "a");
    if (log)
    {
      fprintf(log, "AFTER INIT: fd=%lld, cloexec_set=%d\n",
              (long long) fd, cloexec_set);
#if defined(SOCK_NOSIGPIPE) || defined(MHD_socket_nosignal_)
      fprintf(log, "AFTER INIT: nosigpipe_set=%d\n", nosigpipe_set);
#endif /* SOCK_NOSIGPIPE ||  MHD_socket_nosignal_ */
      fclose(log);
    }
  }

  if (MHD_INVALID_SOCKET == fd)
  {
    FILE *log = fopen("/tmp/logs/359.txt", "a");
    if (log)
    {
      fprintf(log, "FALLBACK: initial socket() failed, trying fallback socket()\n");
      fclose(log);
    }

    fd = socket (pf,
                 SOCK_STREAM,
                 0);
    cloexec_set = 0;
#if defined(SOCK_NOSIGPIPE) || defined(MHD_socket_nosignal_)
    nosigpipe_set = 0;
#endif /* SOCK_NOSIGPIPE ||  MHD_socket_nosignal_ */

    {
      FILE *log2 = fopen("/tmp/logs/359.txt", "a");
      if (log2)
      {
        fprintf(log2, "FALLBACK AFTER: fd=%lld, cloexec_set=%d\n",
                (long long) fd, cloexec_set);
#if defined(SOCK_NOSIGPIPE) || defined(MHD_socket_nosignal_)
        fprintf(log2, "FALLBACK AFTER: nosigpipe_set=%d\n", nosigpipe_set);
#endif /* SOCK_NOSIGPIPE ||  MHD_socket_nosignal_ */
        fclose(log2);
      }
    }
  }
  if (MHD_INVALID_SOCKET == fd)
    return MHD_INVALID_SOCKET;

#if defined(MHD_socket_nosignal_)
  /* Evaluate the components of the condition in the same short-circuiting
     order as the original code and log the intermediate values so that
     reproducer agents can see whether the controlling expression is
     invariant (e.g. (! nosigpipe_set) always false). */
  {
    int left = (! nosigpipe_set);
    int second = 0;
    int third = (0 == MSG_NOSIGNAL_OR_ZERO);

    FILE *log = fopen("/tmp/logs/359.txt", "a");
    if (log)
    {
      fprintf(log, "CHECK: before MHD_socket_nosignal_ condition: left=(!nosigpipe_set)=%d\n", left);
      fclose(log);
    }

    if (left)
    {
      /* Only call MHD_socket_nosignal_ if the left operand permits it so
         we preserve the original short-circuit semantics and avoid any
         additional side-effects when the original code would not call it. */
      second = (0 == MHD_socket_nosignal_ (fd));

      FILE *log2 = fopen("/tmp/logs/359.txt", "a");
      if (log2)
      {
        fprintf(log2, "CHECK: MHD_socket_nosignal_(fd) returned %d, second=(0==ret)=%d\n", (int) !second ? 1 : 0, second);
        fprintf(log2, "CHECK: third=(0==MSG_NOSIGNAL_OR_ZERO)=%d\n", third);
        fclose(log2);
      }
    }
    else
    {
      FILE *log3 = fopen("/tmp/logs/359.txt", "a");
      if (log3)
      {
        fprintf(log3, "CHECK: short-circuited: left is false, MHD_socket_nosignal_ not called\n");
        fprintf(log3, "CHECK: third=(0==MSG_NOSIGNAL_OR_ZERO)=%d\n", third);
        fclose(log3);
      }
    }

    /* Now perform the original guarded action using the computed pieces. */
    if (left && second && third)
    {
      FILE *log4 = fopen("/tmp/logs/359.txt", "a");
      if (log4)
      {
        fprintf(log4, "BRANCH TAKEN: left && second && third is true -> handling failure path\n");
        fclose(log4);
      }

      /* SIGPIPE disable is possible on this platform
       * (so application expect that it will be disabled),
       * but failed to be disabled here and it is not
       * possible to disable SIGPIPE by MSG_NOSIGNAL. */
      const int err = MHD_socket_get_error_ ();
      (void) MHD_socket_close_ (fd);
      MHD_socket_fset_error_ (err);

      FILE *log5 = fopen("/tmp/logs/359.txt", "a");
      if (log5)
      {
        fprintf(log5, "EXIT: returning MHD_INVALID_SOCKET after SIGPIPE disable failure, err=%d\n", err);
        fclose(log5);
      }

      return MHD_INVALID_SOCKET;
    }
    else
    {
      FILE *log6 = fopen("/tmp/logs/359.txt", "a");
      if (log6)
      {
        fprintf(log6, "BRANCH NOT TAKEN: left=%d, second=%d, third=%d\n", left, second, third);
        fclose(log6);
      }
    }
  }
#endif /* defined(MHD_socket_nosignal_) */

  if (! cloexec_set)
  {
    FILE *log = fopen("/tmp/logs/359.txt", "a");
    if (log)
    {
      fprintf(log, "ACTION: calling MHD_socket_noninheritable_(fd=%lld) because cloexec_set==0\n", (long long) fd);
      fclose(log);
    }
    (void) MHD_socket_noninheritable_ (fd);
  }

  {
    FILE *log = fopen("/tmp/logs/359.txt", "a");
    if (log)
    {
      fprintf(log, "EXIT: returning fd=%lld\n", (long long) fd);
      fclose(log);
    }
  }

  return fd;
}
