int
MHD_str_equal_caseless_ (const char *str1,
                         const char *str2)
{
  /* Logging to help reproduce the pointer-parameter modification scenario. */
  FILE *log = NULL;
  log = fopen("/tmp/logs/2042.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER: MHD_str_equal_caseless_: str1=%p, str2=%p, str1[0]=0x%02x, str2[0]=0x%02x\n",
            (const void *)str1, (const void *)str2,
            (unsigned char)(str1 ? str1[0] : '\0'), (unsigned char)(str2 ? str2[0] : '\0'));
  }

  int iter = 0;
  while (0 != (*str1))
  {
    const char c1 = *str1;
    const char c2 = *str2;

    if (log)
    {
      fprintf(log, "ITER %d: before compare: str1=%p, str2=%p, c1=0x%02x '%c', c2=0x%02x '%c'\n",
              iter, (const void *)str1, (const void *)str2,
              (unsigned char)c1, (c1 >= 32 && c1 <= 126) ? c1 : '?',
              (unsigned char)c2, (c2 >= 32 && c2 <= 126) ? c2 : '?');
    }

    if (charsequalcaseless (c1, c2))
    {
      /* Log the pointer values that will be incremented. This demonstrates the
         modification of the pointer parameter 'str2' (the MISRA Rule 17.8 case). */
      if (log)
      {
        fprintf(log, "ITER %d: match -> incrementing pointers: str1=%p -> %p, str2=%p -> %p\n",
                iter, (const void *)str1, (const void *)(str1 + 1),
                (const void *)str2, (const void *)(str2 + 1));
      }

      str1++;
      str2++; /* Modification of pointer parameter (intended to reproduce/report). */

      if (log)
      {
        fprintf(log, "ITER %d: after increment: str1=%p, str2=%p, next_c1=0x%02x, next_c2=0x%02x\n",
                iter, (const void *)str1, (const void *)str2,
                (unsigned char)(str1 ? str1[0] : '\0'), (unsigned char)(str2 ? str2[0] : '\0'));
      }
    }
    else
    {
      if (log)
      {
        fprintf(log, "ITER %d: mismatch: c1=0x%02x '%c', c2=0x%02x '%c' -> returning 0\n",
                iter,
                (unsigned char)c1, (c1 >= 32 && c1 <= 126) ? c1 : '?',
                (unsigned char)c2, (c2 >= 32 && c2 <= 126) ? c2 : '?');
        fclose(log);
      }
      return 0;
    }

    ++iter;
  }

  int result = (0 == (*str2));
  if (log)
  {
    fprintf(log, "EXIT: MHD_str_equal_caseless_: final str1=%p, str2=%p, result=%d\n",
            (const void *)str1, (const void *)str2, result);
    fclose(log);
  }

  return result;
}
