bool
MHD_str_equal_caseless_quoted_bin_n (const char *quoted,
                                     size_t quoted_len,
                                     const char *unquoted,
                                     size_t unquoted_len)
{
  size_t i;
  size_t j;
  FILE *f = fopen("/tmp/logs/4031.txt", "a");
  if (NULL == f)
    /* Fallback if the directory does not exist */
    f = fopen("/tmp/4031.txt", "a");

  if (f)
    fprintf(f, "ENTER MHD_str_equal_caseless_quoted_bin_n: quoted_len=%zu unquoted_len=%zu\n",
            quoted_len, unquoted_len);

  if (unquoted_len < quoted_len / 2)
  {
    if (f)
      fprintf(f, "EARLY RETURN: unquoted_len (%zu) < quoted_len/2 (%zu) -> false\n",
              unquoted_len, quoted_len / 2);
    if (f)
      fclose(f);
    return false;
  }

  j = 0;
  if (f)
    fprintf(f, "START LOOP: initial j=%zu (i will be set in for)\n", j);

  for (i = 0; quoted_len > i && unquoted_len > j; ++i, ++j)
  {
    if (f)
      fprintf(f, "LOOP ITER: before body i=%zu j=%zu quoted[i]=0x%02x unquoted[j]=0x%02x\n",
              i, j, (unsigned int)(unsigned char)quoted[i], (unsigned int)(unsigned char)unquoted[j]);

    if ('\\' == quoted[i])
    {
      i++; /* Advance to the next character */
      if (f)
        fprintf(f, "ESCAPE: found \\ at i=%zu; advanced i to %zu\n", i - 1, i);

      if (quoted_len == i)
      {
        if (f)
          fprintf(f, "EARLY RETURN: escape at end of quoted (i==quoted_len=%zu) -> false\n", i);
        if (f)
          fclose(f);
        return false; /* No character after escaping backslash */
      }
    }
    if (! charsequalcaseless (quoted[i], unquoted[j]))
    {
      if (f)
        fprintf(f, "CHAR MISMATCH: i=%zu j=%zu quoted=0x%02x unquoted=0x%02x -> false\n",
                i, j, (unsigned int)(unsigned char)quoted[i], (unsigned int)(unsigned char)unquoted[j]);
      if (f)
        fclose(f);
      return false; /* Different characters */
    }
    if (f)
      fprintf(f, "CHAR MATCH: i=%zu j=%zu (about to execute third clause ++i, ++j)\n", i, j);
  }

  if (f)
    fprintf(f, "AFTER LOOP: i=%zu j=%zu quoted_len=%zu unquoted_len=%zu\n",
            i, j, quoted_len, unquoted_len);

  if ((quoted_len != i) || (unquoted_len != j))
  {
    if (f)
      fprintf(f, "FINAL CHECK FAILED: (quoted_len != i) || (unquoted_len != j) -> false\n");
    if (f)
      fclose(f);
    return false; /* The strings have different length */
  }

  if (f)
    fprintf(f, "RETURN TRUE\n");
  if (f)
    fclose(f);

  return true;
}
