bool
MHD_str_equal_caseless_quoted_bin_n (const char *quoted,
                                     size_t quoted_len,
                                     const char *unquoted,
                                     size_t unquoted_len)
{
  size_t i;
  size_t j;
  FILE *log = fopen("/tmp/logs/4031.txt", "a");

  if (log)
    fprintf(log, "ENTER: MHD_str_equal_caseless_quoted_bin_n quoted=%p quoted_len=%zu unquoted=%p unquoted_len=%zu\n",
            (const void *)quoted, quoted_len, (const void *)unquoted, unquoted_len);

  if (unquoted_len < quoted_len / 2)
  {
    if (log)
      fprintf(log, "EARLY RETURN false: unquoted_len < quoted_len/2 (%zu < %zu)\n", unquoted_len, quoted_len / 2);
    if (log)
      fclose(log);
    return false;
  }

  j = 0;
  if (log)
    fprintf(log, "INIT: j set to %zu outside for header\n", j);

  for (i = 0; quoted_len > i && unquoted_len > j; ++i, ++j)
  {
    if (log)
      fprintf(log, "LOOP ITERATION START: i=%zu j=%zu quoted[i]=%p unquoted[j]=%p\n",
              i, j, (const void *)&quoted[i], (const void *)&unquoted[j]);

    if ('\\' == quoted[i])
    {
      if (log)
        fprintf(log, "ESCAPE: backslash at quoted[%zu], advancing i to %zu\n", i, i + 1);

      i++; /* Advance to the next character */
      if (quoted_len == i)
      {
        if (log)
          fprintf(log, "RETURN false: No character after escaping backslash (quoted_len==i==%zu)\n", i);
        if (log)
          fclose(log);
        return false; /* No character after escaping backslash */
      }
    }

    if (! charsequalcaseless (quoted[i], unquoted[j]))
    {
      if (log)
      {
        /* Print raw byte values to avoid problems with non-printable characters */
        unsigned char qc = (unsigned char)quoted[i];
        unsigned char uc = (unsigned char)unquoted[j];
        fprintf(log, "RETURN false: Different characters at quoted[%zu]=0x%02X and unquoted[%zu]=0x%02X\n",
                i, qc, j, uc);
      }
      if (log)
        fclose(log);
      return false; /* Different characters */
    }

    if (log)
      fprintf(log, "LOOP ITERATION END: matched quoted[%zu] and unquoted[%zu] -> continue\n", i, j);
  }

  if ((quoted_len != i) || (unquoted_len != j))
  {
    if (log)
      fprintf(log, "RETURN false: length mismatch after loop quoted_len=%zu i=%zu unquoted_len=%zu j=%zu\n",
              quoted_len, i, unquoted_len, j);
    if (log)
      fclose(log);
    return false; /* The strings have different length */
  }

  if (log)
    fprintf(log, "RETURN true: strings matched quoted_len=%zu unquoted_len=%zu final i=%zu j=%zu\n",
            quoted_len, unquoted_len, i, j);

  if (log)
    fclose(log);
  return true;
}
