bool
MHD_str_equal_caseless_quoted_bin_n (const char *quoted,
                                     size_t quoted_len,
                                     const char *unquoted,
                                     size_t unquoted_len)
{
  size_t i;
  size_t j;
  FILE *logf = fopen("/tmp/logs/4031.txt", "a");
  if (logf)
  {
    fprintf(logf, "ENTER MHD_str_equal_caseless_quoted_bin_n: quoted_len=%zu, unquoted_len=%zu\n",
            quoted_len, unquoted_len);
    fflush(logf);
  }

  if (unquoted_len < quoted_len / 2)
  {
    if (logf)
    {
      fprintf(logf, "EARLY RETURN false: unquoted_len < quoted_len/2 (%zu < %zu)\n",
              unquoted_len, quoted_len / 2);
      fflush(logf);
      fclose(logf);
    }
    return false;
  }

  j = 0;
  if (logf)
  {
    fprintf(logf, "Init counters: i=(uninitialized), j=%zu\n", j);
    fflush(logf);
  }

  for (i = 0; quoted_len > i && unquoted_len > j; ++i, ++j)
  {
    if (logf)
      fprintf(logf, "LOOP START: i=%zu, j=%zu, quoted[i]=%c, unquoted[j]=%c\n",
              i, j,
              (quoted && quoted_len > i) ? quoted[i] : '?',
              (unquoted && unquoted_len > j) ? unquoted[j] : '?');

    if ('\\' == quoted[i])
    {
      if (logf)
        fprintf(logf, "  Found backslash at quoted[%zu] => advancing i to %zu\n", i, i + 1);

      i++; /* Advance to the next character */
      if (quoted_len == i)
      {
        if (logf)
        {
          fprintf(logf, "  RETURN false: escape at end (quoted_len == i == %zu)\n", i);
          fflush(logf);
          fclose(logf);
        }
        return false; /* No character after escaping backslash */
      }
    }

    if (logf)
      fprintf(logf, "  After backslash-handling: i=%zu, j=%zu, comparing quoted[%zu]=%c with unquoted[%zu]=%c\n",
              i, j, i, quoted[i], j, unquoted[j]);

    if (! charsequalcaseless (quoted[i], unquoted[j]))
    {
      if (logf)
      {
        fprintf(logf, "  RETURN false: characters differ at quoted[%zu]=%c vs unquoted[%zu]=%c\n",
                i, quoted[i], j, unquoted[j]);
        fflush(logf);
        fclose(logf);
      }
      return false; /* Different characters */
    }

    if (logf)
      fflush(logf);
  }

  if (logf)
  {
    fprintf(logf, "LOOP EXIT: final i=%zu, j=%zu, quoted_len=%zu, unquoted_len=%zu\n",
            /* i and j hold the values after loop termination */
            i, j, quoted_len, unquoted_len);
    fflush(logf);
  }

  if ((quoted_len != i) || (unquoted_len != j))
  {
    if (logf)
    {
      fprintf(logf, "FINAL RETURN false: length mismatch (quoted_len != i) || (unquoted_len != j) -> (%zu != %zu) || (%zu != %zu)\n",
              quoted_len, i, unquoted_len, j);
      fflush(logf);
      fclose(logf);
    }
    return false; /* The strings have different length */
  }

  if (logf)
  {
    fprintf(logf, "FINAL RETURN true: strings match caselessly\n");
    fflush(logf);
    fclose(logf);
  }

  return true;
}
