bool
MHD_str_equal_quoted_bin_n (const char *quoted,
                            size_t quoted_len,
                            const char *unquoted,
                            size_t unquoted_len)
{
  size_t i;
  size_t j;
  FILE *log = fopen("/tmp/logs/3661.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER MHD_str_equal_quoted_bin_n: quoted_len=%zu unquoted_len=%zu\n",
            quoted_len, unquoted_len);
    fclose(log);
  }

  if (unquoted_len < quoted_len / 2)
  {
    log = fopen("/tmp/logs/3661.txt", "a");
    if (log)
    {
      fprintf(log, "EARLY RETURN: unquoted_len < quoted_len/2 -> %zu < %zu\n",
              unquoted_len, quoted_len / 2);
      fclose(log);
    }
    return false;
  }

  j = 0;
  log = fopen("/tmp/logs/3661.txt", "a");
  if (log)
  {
    fprintf(log, "Initialized j outside for: j=%zu\n", j);
    fclose(log);
  }

  for (i = 0; quoted_len > i && unquoted_len > j; ++i, ++j)
  {
    log = fopen("/tmp/logs/3661.txt", "a");
    if (log)
    {
      /* Print the bytes being compared in hex to avoid embedding raw characters */
      fprintf(log, "LOOP START: i=%zu j=%zu quoted_byte=0x%02x unquoted_byte=0x%02x\n",
              i, j, (unsigned int)(unsigned char)quoted[i], (unsigned int)(unsigned char)unquoted[j]);
      fclose(log);
    }

    if ('\\' == quoted[i])
    {
      i++; /* Advance to the next character */
      log = fopen("/tmp/logs/3661.txt", "a");
      if (log)
      {
        fprintf(log, "ESCAPE SEEN: advanced i to %zu (quoted_len=%zu)\n", i, quoted_len);
        fclose(log);
      }
      if (quoted_len == i)
      {
        log = fopen("/tmp/logs/3661.txt", "a");
        if (log)
        {
          fprintf(log, "RETURN false: escape at end of quoted string (i==quoted_len==%zu)\n", i);
          fclose(log);
        }
        return false; /* No character after escaping backslash */
      }
    }
    if (quoted[i] != unquoted[j])
    {
      log = fopen("/tmp/logs/3661.txt", "a");
      if (log)
      {
        fprintf(log, "RETURN false: mismatch at i=%zu j=%zu quoted=0x%02x unquoted=0x%02x\n",
                i, j, (unsigned int)(unsigned char)quoted[i], (unsigned int)(unsigned char)unquoted[j]);
        fclose(log);
      }
      return false; /* Different characters */
    }
  }

  log = fopen("/tmp/logs/3661.txt", "a");
  if (log)
  {
    fprintf(log, "LOOP EXIT: i=%zu j=%zu quoted_len=%zu unquoted_len=%zu\n",
            i, j, quoted_len, unquoted_len);
    fclose(log);
  }

  if ((quoted_len != i) || (unquoted_len != j))
  {
    log = fopen("/tmp/logs/3661.txt", "a");
    if (log)
    {
      fprintf(log, "RETURN false: length mismatch after loop: quoted_len=%zu i=%zu unquoted_len=%zu j=%zu\n",
              quoted_len, i, unquoted_len, j);
      fclose(log);
    }
    return false; /* The strings have different length */
  }

  log = fopen("/tmp/logs/3661.txt", "a");
  if (log)
  {
    fprintf(log, "RETURN true: strings equal after quoted-unquoted compare\n");
    fclose(log);
  }

  return true;
}
