bool
MHD_str_has_token_caseless_ (const char *str,
                             const char *const token,
                             size_t token_len)
{
  /* Logging file. Open once and close on each exit to ensure logs persist. */
  FILE *fp = fopen("/tmp/logs/4502.txt", "a");
  if (fp)
    fprintf(fp, "ENTER MHD_str_has_token_caseless_: str=%p token=%p token_len=%zu first_char=%d\n",
            (void *)str, (void *)token, token_len, (int)(*str));

  if (0 == token_len)
  {
    if (fp)
    {
      fprintf(fp, "token_len == 0 -> returning false\n");
      fclose(fp);
    }
    return false;
  }

  while (0 != *str)
  {
    size_t i;
    /* Skip all whitespaces and empty tokens. */
    while (' ' == *str || '\t' == *str || ',' == *str)
    {
      if (fp)
        fprintf(fp, "skip_whitespace before: str=%p char=%d\n", (void *)str, (int)(*str));
      /* preserve original semantics of incrementing the pointer */
      str++;
      if (fp)
        fprintf(fp, "skip_whitespace after:  str=%p char=%d\n", (void *)str, (int)(*str));
    }

    /* Check for token match. */
    i = 0;
    while (1)
    {
      /* Keep behavior identical to original: read current char, then advance pointer. */
      const char sc = *str;
      if (fp)
        fprintf(fp, "inner_loop before increment: str=%p sc=%d\n", (void *)str, (int)sc);
      str++; /* originally done via *(str++) */
      if (fp)
        fprintf(fp, "inner_loop after increment:  str=%p (next char=%d)\n", (void *)str, (int)(*str));

      const char tc = token[i++];
      if (fp)
        fprintf(fp, "compare chars: sc=%d tc=%d i=%zu\n", (int)sc, (int)tc, i);

      if (0 == sc)
      {
        if (fp)
        {
          fprintf(fp, "sc == 0 -> returning false (end of string)\n");
          fclose(fp);
        }
        return false;
      }
      if (! charsequalcaseless (sc, tc))
      {
        if (fp)
          fprintf(fp, "chars not equal (sc=%d tc=%d) -> break to find next substring\n", (int)sc, (int)tc);
        break;
      }
      if (i >= token_len)
      {
        /* Check whether substring match token fully or
         * has additional unmatched chars at tail. */
        if (fp)
          fprintf(fp, "matched token length i=%zu >= token_len=%zu -> check tail\n", i, token_len);
        while (' ' == *str || '\t' == *str)
        {
          if (fp)
            fprintf(fp, "tail_skip before: str=%p char=%d\n", (void *)str, (int)(*str));
          str++;
          if (fp)
            fprintf(fp, "tail_skip after:  str=%p char=%d\n", (void *)str, (int)(*str));
        }
        /* End of (sub)string? */
        if ((0 == *str) || (',' == *str) )
        {
          if (fp)
          {
            fprintf(fp, "match succeeded -> returning true (str=%p char=%d)\n", (void *)str, (int)(*str));
            fclose(fp);
          }
          return true;
        }
        /* Unmatched chars at end of substring. */
        if (fp)
          fprintf(fp, "unmatched chars at end of substring -> break to find next substring\n");
        break;
      }
    }
    /* Find next substring. */
    while (0 != *str && ',' != *str)
    {
      if (fp)
        fprintf(fp, "find_next before: str=%p char=%d\n", (void *)str, (int)(*str));
      str++;
      if (fp)
        fprintf(fp, "find_next after:  str=%p char=%d\n", (void *)str, (int)(*str));
    }
  }
  if (fp)
  {
    fprintf(fp, "no match found -> returning false\n");
    fclose(fp);
  }
  return false;
}
