bool
MHD_str_has_token_caseless_ (const char *str,
                             const char *const token,
                             size_t token_len)
{
  /* Logging helper: append traces to /tmp/logs/4502.txt */
  FILE *log = fopen("/tmp/logs/4502.txt", "a");
  if (NULL != log)
  {
    fprintf(log, "MHD_str_has_token_caseless_: entry str=%p token=%p token_len=%zu\n",
            (const void *)str, (const void *)token, token_len);
    fflush(log);
  }

  if (0 == token_len)
  {
    if (NULL != log)
    {
      fprintf(log, "MHD_str_has_token_caseless_: token_len == 0 -> return false\n");
      fclose(log);
    }
    return false;
  }

  while (0 != *str)
  {
    size_t i;
    /* Skip all whitespaces and empty tokens. */
    while (' ' == *str || '\t' == *str || ',' == *str)
    {
      const char skipped = *str;
      const void *addr_before = (const void *)str;
      str++;
      if (NULL != log)
      {
        fprintf(log, "skip_ws: skipped='%c' (0x%02x) addr_before=%p addr_after=%p\n",
                (skipped >= ' ' && skipped <= '~') ? skipped : '?', (unsigned char)skipped,
                addr_before, (const void *)str);
        fflush(log);
      }
    }

    /* Check for token match. */
    i = 0;
    while (1)
    {
      const void *addr_before_inc = (const void *)str;
      const char sc = *(str++);
      const void *addr_after_inc = (const void *)str;
      const char tc = token[i++];

      if (NULL != log)
      {
        /* Log the post-increment behavior on the parameter 'str'. */
        fprintf(log, "inner: addr_before=%p addr_after=%p sc='%c'(0x%02x) tc='%c'(0x%02x) i=%zu\n",
                addr_before_inc, addr_after_inc,
                (sc >= ' ' && sc <= '~') ? sc : '?', (unsigned char)sc,
                (tc >= ' ' && tc <= '~') ? tc : '?', (unsigned char)tc,
                i);
        fflush(log);
      }

      if (0 == sc)
      {
        if (NULL != log)
        {
          fprintf(log, "inner: sc == 0 -> return false (end of string)\n");
          fclose(log);
        }
        return false;
      }
      if (! charsequalcaseless (sc, tc))
      {
        if (NULL != log)
        {
          fprintf(log, "inner: charsequalcaseless(sc,tc) == false -> break to find next substring\n");
          fflush(log);
        }
        break;
      }
      if (i >= token_len)
      {
        /* Check whether substring match token fully or
         * has additional unmatched chars at tail. */
        while (' ' == *str || '\t' == *str)
        {
          const void *addr_before = (const void *)str;
          const char skipped = *str;
          str++;
          if (NULL != log)
          {
            fprintf(log, "post-match: skip_ws='%c' addr_before=%p addr_after=%p\n",
                    (skipped >= ' ' && skipped <= '~') ? skipped : '?', addr_before, (const void *)str);
            fflush(log);
          }
        }
        /* End of (sub)string? */
        if ((0 == *str) || (',' == *str) )
        {
          if (NULL != log)
          {
            fprintf(log, "match: token fully matched and followed by end or comma -> return true (str now=%p)\n",
                    (const void *)str);
            fclose(log);
          }
          return true;
        }
        /* Unmatched chars at end of substring. */
        if (NULL != log)
        {
          fprintf(log, "match: token matched but substring has extra trailing chars -> break to find next substring (str=%p)\n",
                  (const void *)str);
          fflush(log);
        }
        break;
      }
    }
    /* Find next substring. */
    while (0 != *str && ',' != *str)
    {
      const void *addr_before = (const void *)str;
      const char skipped = *str;
      str++;
      if (NULL != log)
      {
        fprintf(log, "find_next: advancing through '%c' addr_before=%p addr_after=%p\n",
                (skipped >= ' ' && skipped <= '~') ? skipped : '?', addr_before, (const void *)str);
        fflush(log);
      }
    }
  }

  if (NULL != log)
  {
    fprintf(log, "MHD_str_has_token_caseless_: reached end -> return false\n");
    fclose(log);
  }
  return false;
}
