MHD_str_pct_decode_in_place_strict_ (char *str)
{
#ifdef MHD_FAVOR_SMALL_CODE
  size_t res;
  bool broken;
  FILE *f = fopen("/tmp/logs/4218.txt", "a");
  if (f)
  {
    fprintf(f, "MHD_str_pct_decode_in_place_strict_: entered (small code path), str=%s\n", str ? str : "(null)");
    fflush(f);
  }

  res = MHD_str_pct_decode_in_place_lenient_ (str, &broken);

  if (f)
  {
    fprintf(f, "MHD_str_pct_decode_in_place_lenient_ returned res=%zu, broken=%d\n", res, broken);
    fflush(f);
  }

  if (broken)
  {
    if (f)
    {
      fprintf(f, "broken detected: clearing output and returning 0\n");
      fclose(f);
    }
    res = 0;
    str[0] = 0;
  }
  else
  {
    if (f)
      fclose(f);
  }

  return res;
#else  /* ! MHD_FAVOR_SMALL_CODE */
  size_t r;
  size_t w;
  FILE *f = fopen("/tmp/logs/4218.txt", "a");

  if (f)
    fprintf(f, "MHD_str_pct_decode_in_place_strict_: entered, initial str=%s\n", str ? str : "(null)");

  r = 0;
  w = 0;

  while (0 != str[r])
  {
    const char chr = str[r++];
    if (f)
      fprintf(f, "loop: r=%zu, w=%zu, chr='%c' (0x%02x)\n", r - 1, w, (chr >= 32 && chr < 127) ? chr : '?', (unsigned char) chr);

    if ('%' == chr)
    {
      const char d1 = str[r++];
      if (f)
        fprintf(f, "found '%%' at r=%zu, d1='%c' (0x%02x)\n", r - 1, (d1 >= 32 && d1 < 127) ? d1 : '?', (unsigned char) d1);

      if (0 == d1)
      {
        if (f)
        {
          fprintf(f, "early return: d1 == 0 at r=%zu\n", r - 1);
          fclose(f);
        }
        return 0;
      }
      else
      {
        const char d2 = str[r++];
        if (f)
          fprintf(f, "d2='%c' (0x%02x) at r=%zu\n", (d2 >= 32 && d2 < 127) ? d2 : '?', (unsigned char) d2, r - 1);

        if (0 == d2)
        {
          if (f)
          {
            fprintf(f, "early return: d2 == 0 at r=%zu\n", r - 1);
            fclose(f);
          }
          return 0;
        }
        else
        {
          const int h = toxdigitvalue (d1);
          const int l = toxdigitvalue (d2);
          unsigned char out;

          if (f)
            fprintf(f, "toxdigitvalue: d1=%c->h=%d, d2=%c->l=%d\n",
                    (d1 >= 32 && d1 < 127) ? d1 : '?', h,
                    (d2 >= 32 && d2 < 127) ? d2 : '?', l);

          if ((0 > h) || (0 > l))
          {
            if (f)
            {
              fprintf(f, "invalid hex digits: h=%d, l=%d -> returning 0\n", h, l);
              fclose(f);
            }
            return 0;
          }

          /* Original computation (preserve exact expression) */
          out =
            (unsigned char) (((uint8_t) (((uint8_t) ((unsigned int) h)) << 4))
                             | ((uint8_t) ((unsigned int) l)));

          /* Compute an alternative (simpler) result for logging/comparison */
          {
            unsigned char alt = (unsigned char) (((unsigned int) h << 4) | (unsigned int) l);
            if (f)
            {
              fprintf(f, "computed out(original) = 0x%02x (%u), alt = 0x%02x (%u), h=%d, l=%d\n",
                      (unsigned int) out, (unsigned int) out, (unsigned int) alt, (unsigned int) alt, h, l);
              fflush(f);
            }
          }

          str[w++] = (char) out;
        }
      }
    }
    else
      str[w++] = chr;
  }

  str[w] = 0;

  if (f)
  {
    fprintf(f, "finished decoding, final w=%zu, final str=%s\n", w, str ? str : "(null)");
    fclose(f);
  }

  return w;
#endif /* ! MHD_FAVOR_SMALL_CODE */
}
