size_t
MHD_str_pct_decode_strict_n_ (const char *pct_encoded,
                              size_t pct_encoded_len,
                              char *decoded,
                              size_t buf_size)
{
#ifdef MHD_FAVOR_SMALL_CODE
  bool broken;
  size_t res;

  /* Open log file for tracing control flow/state for violation analysis. */
  {
    FILE *log = fopen("/tmp/logs/1618.txt", "a");
    if (log)
    {
      fprintf(log, "MHD_str_pct_decode_strict_n_: entry (MHD_FAVOR_SMALL_CODE) pct_encoded_len=%zu, buf_size=%zu\n",
              pct_encoded_len, buf_size);
    }

    res = MHD_str_pct_decode_lenient_n_ (pct_encoded, pct_encoded_len, decoded,
                                         buf_size, &broken);

    if (log)
    {
      fprintf(log, "MHD_str_pct_decode_strict_n_: lenient returned res=%zu, broken=%d\n", res, (int)broken);
      fclose(log);
    }
  }

  if (broken)
    return 0;
  return res;
#else  /* ! MHD_FAVOR_SMALL_CODE */
  size_t r;
  size_t w;
  r = 0;
  w = 0;

  /* Open log file for tracing control flow/state for violation analysis. */
  FILE *log = fopen("/tmp/logs/1618.txt", "a");
  if (log)
    fprintf(log, "MHD_str_pct_decode_strict_n_: entry pct_encoded_len=%zu, buf_size=%zu\n",
            pct_encoded_len, buf_size);

  if (buf_size >= pct_encoded_len)
  {
    while (r < pct_encoded_len)
    {
      const char chr = pct_encoded[r];
      if (log)
        fprintf(log, "loop(buf>=len): r=%zu, w=%zu, chr='%c' (0x%02x)\n", r, w, (chr >= 32 && chr < 127) ? chr : '?', (unsigned char) chr);
      if ('%' == chr)
      {
        if (2 > pct_encoded_len - r)
        {
          if (log)
            fprintf(log, "early return: insufficient remaining characters after %% at r=%zu\n", r);
          if (log) fclose(log);
          return 0;
        }
        else
        {
          const int h = toxdigitvalue (pct_encoded[++r]);
          const int l = toxdigitvalue (pct_encoded[++r]);
          unsigned char out;
          if (log)
            fprintf(log, "percent-encoding at r=%zu: raw h_char='%c', raw l_char='%c', h=%d, l=%d\n",
                    r - 1, pct_encoded[r - 1], pct_encoded[r], h, l);
          if ((0 > h) || (0 > l))
          {
            if (log)
              fprintf(log, "invalid hex digits: h=%d, l=%d -> returning 0\n", h, l);
            if (log) fclose(log);
            return 0;
          }

          /* Replicate the original computation but capture intermediate values for logging. */
          {
            unsigned int h_ui = (unsigned int) h;
            unsigned int l_ui = (unsigned int) l;
            uint8_t h_u8 = (uint8_t) h_ui;
            uint8_t l_u8 = (uint8_t) l_ui;
            uint8_t h_shift = (uint8_t) (h_u8 << 4);
            uint8_t combined = (uint8_t) (h_shift | l_u8);
            out = (unsigned char) combined;
            if (log)
              fprintf(log, "casts/intermediates: (unsigned int)h=%u, (uint8_t)h=%u, h<<4=%u, (unsigned int)l=%u, (uint8_t)l=%u, combined=%u, out=%u\n",
                      h_ui, (unsigned)h_u8, (unsigned)h_shift, l_ui, (unsigned)l_u8, (unsigned)combined, (unsigned)out);
          }

          decoded[w] = (char) out;
        }
      }
      else
        decoded[w] = chr;
      ++r;
      ++w;
    }
    if (log)
      fprintf(log, "returning w=%zu (buf>=len path)\n", w);
    if (log) fclose(log);
    return w;
  }

  while (r < pct_encoded_len)
  {
    const char chr = pct_encoded[r];
    if (log)
      fprintf(log, "loop(buf<len): r=%zu, w=%zu, chr='%c' (0x%02x)\n", r, w, (chr >= 32 && chr < 127) ? chr : '?', (unsigned char) chr);
    if (w >= buf_size)
    {
      if (log)
        fprintf(log, "early return: buffer overflow would occur w=%zu, buf_size=%zu\n", w, buf_size);
      if (log) fclose(log);
      return 0;
    }
    if ('%' == chr)
    {
      if (2 > pct_encoded_len - r)
      {
        if (log)
          fprintf(log, "early return: insufficient remaining characters after %% at r=%zu\n", r);
        if (log) fclose(log);
        return 0;
      }
      else
      {
        const int h = toxdigitvalue (pct_encoded[++r]);
        const int l = toxdigitvalue (pct_encoded[++r]);
        unsigned char out;
        if (log)
          fprintf(log, "percent-encoding at r=%zu: raw h_char='%c', raw l_char='%c', h=%d, l=%d\n",
                  r - 1, pct_encoded[r - 1], pct_encoded[r], h, l);
        if ((0 > h) || (0 > l))
        {
          if (log)
            fprintf(log, "invalid hex digits: h=%d, l=%d -> returning 0\n", h, l);
          if (log) fclose(log);
          return 0;
        }

        /* Replicate the original computation but capture intermediate values for logging. */
        {
          unsigned int h_ui = (unsigned int) h;
          unsigned int l_ui = (unsigned int) l;
          uint8_t h_u8 = (uint8_t) h_ui;
          uint8_t l_u8 = (uint8_t) l_ui;
          uint8_t h_shift = (uint8_t) (h_u8 << 4);
          uint8_t combined = (uint8_t) (h_shift | l_u8);
          out = (unsigned char) combined;
          if (log)
            fprintf(log, "casts/intermediates: (unsigned int)h=%u, (uint8_t)h=%u, h<<4=%u, (unsigned int)l=%u, (uint8_t)l=%u, combined=%u, out=%u\n",
                    h_ui, (unsigned)h_u8, (unsigned)h_shift, l_ui, (unsigned)l_u8, (unsigned)combined, (unsigned)out);
        }

        decoded[w] = (char) out;
      }
    }
    else
      decoded[w] = chr;
    ++r;
    ++w;
  }
  if (log)
    fprintf(log, "returning w=%zu (buf<len path)\n", w);
  if (log) fclose(log);
  return w;
#endif /* ! MHD_FAVOR_SMALL_CODE */
}
