size_t
MHD_str_quote (const char *unquoted,
               size_t unquoted_len,
               char *result,
               size_t buf_size)
{
  size_t r;
  size_t w;
  FILE *mhd_log = fopen("/tmp/logs/2498.txt", "a");

  r = 0;
  w = 0;

  if (mhd_log)
    fprintf(mhd_log, "ENTER MHD_str_quote: unquoted_len=%zu, buf_size=%zu\n", unquoted_len, buf_size);

#ifndef MHD_FAVOR_SMALL_CODE
  if (unquoted_len * 2 <= buf_size)
  {
    if (mhd_log)
      fprintf(mhd_log, "Fast loop selected (unquoted_len * 2 <= buf_size)\n");
    /* Fast loop: the output will fit the buffer with any input string content */
    while (unquoted_len > r)
    {
      const char chr = unquoted[r++];
      if (('\\' == chr) || ('\"' == chr))
      {
        result[w++] = '\\'; /* Escape current char */
        if (mhd_log)
          fprintf(mhd_log, "escaped char at r=%zu -> chr=0x%02x, w now=%zu\n", r - 1, (unsigned char) chr, w);
      }
      else
      {
        if (mhd_log)
          fprintf(mhd_log, "copied char at r=%zu -> chr=0x%02x, w now=%zu\n", r - 1, (unsigned char) chr, w + 1);
      }
      result[w++] = chr;
    }
  }
  else
  {
    if (unquoted_len > buf_size)
    {
      if (mhd_log)
        fprintf(mhd_log, "Quick fail: unquoted_len (%zu) > buf_size (%zu) -> returning 0\n", unquoted_len, buf_size);
      if (mhd_log) fclose(mhd_log);
      return 0; /* Quick fail: the output buffer is too small */
    }
#else  /* MHD_FAVOR_SMALL_CODE */
  if (1)
  {
    if (mhd_log)
      fprintf(mhd_log, "Small-code path selected (MHD_FAVOR_SMALL_CODE)\n");
#endif /* MHD_FAVOR_SMALL_CODE */

    while (unquoted_len > r)
    {
      if (buf_size <= w)
      {
        if (mhd_log)
          fprintf(mhd_log, "Buffer too small before writing: buf_size=%zu, w=%zu, r=%zu -> returning 0\n", buf_size, w, r);
        if (mhd_log) fclose(mhd_log);
        return 0; /* The output buffer is too small */
      }
      else
      {
        const char chr = unquoted[r++];
        if (('\\' == chr) || ('\"' == chr))
        {
          result[w++] = '\\'; /* Escape current char */
          if (mhd_log)
            fprintf(mhd_log, "escaped char at r=%zu -> chr=0x%02x, w now=%zu\n", r - 1, (unsigned char) chr, w);
          if (buf_size <= w)
          {
            if (mhd_log)
              fprintf(mhd_log, "Buffer too small after escape: buf_size=%zu, w=%zu, r=%zu -> returning 0\n", buf_size, w, r);
            if (mhd_log) fclose(mhd_log);
            return 0; /* The output buffer is too small */
          }
        }
        result[w++] = chr;
        if (mhd_log)
          fprintf(mhd_log, "wrote chr at r=%zu -> chr=0x%02x, w now=%zu\n", r - 1, (unsigned char) chr, w);
      }
    }
  }

  if (mhd_log)
    fprintf(mhd_log, "Before asserts: final r=%zu, w=%zu\n", r, w);

  mhd_assert (w >= r);
  mhd_assert (w <= r * 2);

  if (mhd_log)
  {
    fprintf(mhd_log, "EXIT MHD_str_quote: returning w=%zu\n", w);
    fclose(mhd_log);
  }
  return w;
}
