size_t
MHD_str_quote (const char *unquoted,
               size_t unquoted_len,
               char *result,
               size_t buf_size)
{
  size_t r;
  size_t w;
  FILE *log = NULL;

  /* Try to open log file. If it fails, continue without logging. */
  log = fopen("/tmp/logs/2498.txt", "a");

  if (log)
  {
#ifdef MHD_FAVOR_SMALL_CODE
    fprintf(log, "MHD_str_quote ENTER: unquoted_len=%zu buf_size=%zu (MHD_FAVOR_SMALL_CODE defined)\n",
            unquoted_len, buf_size);
#else
    fprintf(log, "MHD_str_quote ENTER: unquoted_len=%zu buf_size=%zu (MHD_FAVOR_SMALL_CODE NOT defined)\n",
            unquoted_len, buf_size);
#endif
    fflush(log);
  }

  r = 0;
  w = 0;

#ifndef MHD_FAVOR_SMALL_CODE
  if (unquoted_len * 2 <= buf_size)
  {
    /* Fast loop: the output will fit the buffer with any input string content */
    if (log)
    {
      fprintf(log, "branch: fast loop (unquoted_len * 2 <= buf_size)\n");
      fflush(log);
    }
    while (unquoted_len > r)
    {
      const char chr = unquoted[r++];
      if (('\\' == chr) || ('\"' == chr))
      {
        if (log)
        {
          fprintf(log, "iter fast: r=%zu w=%zu chr=0x%02x ESCAPE -> prepend '\\\\'\n", r, w, (unsigned char)chr);
          fflush(log);
        }
        result[w++] = '\\'; /* Escape current char */
      }
      else
      {
        if (log)
        {
          fprintf(log, "iter fast: r=%zu w=%zu chr=0x%02x (no escape)\n", r, w, (unsigned char)chr);
          fflush(log);
        }
      }
      result[w++] = chr;
    }
  }
  else
  {
    if (log)
    {
      fprintf(log, "branch: slow path (unquoted_len * 2 > buf_size)\n");
      fflush(log);
    }
    if (unquoted_len > buf_size)
    {
      if (log)
      {
        fprintf(log, "early return: unquoted_len (%zu) > buf_size (%zu) -> return 0\n", unquoted_len, buf_size);
        fclose(log);
      }
      return 0; /* Quick fail: the output buffer is too small */
    }
#else  /* MHD_FAVOR_SMALL_CODE */
  if (1)
  {
    if (log)
    {
      fprintf(log, "branch: MHD_FAVOR_SMALL_CODE path (if (1) taken)\n");
      fflush(log);
    }
#endif /* MHD_FAVOR_SMALL_CODE */

    while (unquoted_len > r)
    {
      if (buf_size <= w)
      {
        if (log)
        {
          fprintf(log, "return 0: buf_size (=%zu) <= w (=%zu) -> buffer too small\n", buf_size, w);
          fclose(log);
        }
        return 0; /* The output buffer is too small */
      }
      else
      {
        const char chr = unquoted[r++];
        if (('\\' == chr) || ('\"' == chr))
        {
          if (log)
          {
            fprintf(log, "iter slow: r=%zu w=%zu chr=0x%02x ESCAPE -> prepend '\\\\'\n", r, w, (unsigned char)chr);
            fflush(log);
          }
          result[w++] = '\\'; /* Escape current char */
          if (buf_size <= w)
          {
            if (log)
            {
              fprintf(log, "return 0 after escape: buf_size (=%zu) <= w (=%zu) -> buffer too small\n", buf_size, w);
              fclose(log);
            }
            return 0; /* The output buffer is too small */
          }
        }
        else
        {
          if (log)
          {
            fprintf(log, "iter slow: r=%zu w=%zu chr=0x%02x (no escape)\n", r, w, (unsigned char)chr);
            fflush(log);
          }
        }
        result[w++] = chr;
      }
    }
  }

  if (log)
  {
    fprintf(log, "exit: finished loops r=%zu w=%zu -> returning %zu\n", r, w, w);
    fclose(log);
  }

  mhd_assert (w >= r);
  mhd_assert (w <= r * 2);
  return w;
}
