bool
MHD_str_remove_token_caseless_ (const char *str,
                                size_t str_len,
                                const char *const token,
                                const size_t token_len,
                                char *buf,
                                ssize_t *buf_size)
{
  const char *s1; /**< the "input" string / character */
  char *s2;       /**< the "output" string / character */
  size_t t_pos;   /**< position of matched character in the token */
  bool token_removed;

  /* Open log file for probes. Best-effort: if fopen is unavailable/NULL,
   * we simply skip logging. */
  {
    FILE *log_fp = fopen("/tmp/logs/33.txt", "a");
    if (NULL != log_fp)
    {
      fprintf(log_fp, "ENTER MHD_str_remove_token_caseless_: str=%p str_len=%zu token=%p token_len=%zu buf=%p buf_size_ptr=%p initial_buf_size=%zd\n",
              (const void *) str, str_len, (const void *) token, token_len, (void *) buf, (void *) buf_size, (NULL == buf_size) ? (ssize_t)-1 : *buf_size);
      fprintf(log_fp, "About to perform initial token assertions (these may read token_len bytes from token)\n");
      fclose(log_fp);
    }
  }

  mhd_assert (NULL == memchr (token, 0, token_len));
  mhd_assert (NULL == memchr (token, ' ', token_len));
  mhd_assert (NULL == memchr (token, '\t', token_len));
  mhd_assert (NULL == memchr (token, ',', token_len));
  mhd_assert (0 <= *buf_size);

  if (SSIZE_MAX <= ((str_len / 2) * 3 + 3))
  {
    /* The return value may overflow, refuse */
    *buf_size = (ssize_t) -1;
    {
      FILE *log_fp = fopen("/tmp/logs/33.txt", "a");
      if (NULL != log_fp)
      {
        fprintf(log_fp, "EARLY RETURN: overflow risk: computed limit exceeded. Setting buf_size=-1 and returning false\n");
        fclose(log_fp);
      }
    }
    return false;
  }
  s1 = str;
  s2 = buf;
  token_removed = false;

  while ((size_t) (s1 - str) < str_len)
  {
    const char *cur_token; /**< the first char of current token */
    size_t copy_size;

    /* Skip any initial whitespaces and empty tokens */
    while ( ((size_t) (s1 - str) < str_len) &&
            ((' ' == *s1) || ('\t' == *s1) || (',' == *s1)) )
      s1++;

    /* 's1' points to the first char of token in the input string or
     * points just beyond the end of the input string */

    if ((size_t) (s1 - str) >= str_len)
      break; /* Nothing to copy, end of the input string */

    /* 's1' points to the first char of token in the input string */

    cur_token = s1; /* the first char of input token */

    /* Check the token with case-insensetive match */
    t_pos = 0;
    {
      FILE *log_fp = fopen("/tmp/logs/33.txt", "a");
      if (NULL != log_fp)
      {
        fprintf(log_fp, "TOKEN_CHECK_START: cur_token_offset=%zu (s1-str), remaining_input=%zu, token_len=%zu\n",
                (size_t)(cur_token - str), str_len - (size_t)(s1 - str), token_len);
        fclose(log_fp);
      }
    }

    while ( ((size_t) (s1 - str) < str_len) && (token_len > t_pos) &&
            (charsequalcaseless (*s1, token[t_pos])) )
    {
      /* Log each matched character (within bounds check) */
      {
        FILE *log_fp = fopen("/tmp/logs/33.txt", "a");
        if (NULL != log_fp)
        {
          fprintf(log_fp, "  MATCHED char #%zu: input_offset=%zu input_char=0x%02x token_char=0x%02x\n",
                  t_pos, (size_t)(s1 - str), (unsigned char)*s1, (unsigned char)token[t_pos]);
          fclose(log_fp);
        }
      }
      s1++;
      t_pos++;
    }
    /* s1 may point just beyond the end of the input string */
    if ( (token_len == t_pos) && (0 != token_len) )
    {
      /* 'token' matched, check that current input token does not have
       * any suffixes */
      while ( ((size_t) (s1 - str) < str_len) &&
              ((' ' == *s1) || ('\t' == *s1)) )
        s1++;
      /* 's1' points to the first non-whitespace char after the token matched
       * requested token or points just beyond the end of the input string after
       * the requested token */
      if (((size_t) (s1 - str) == str_len) || (',' == *s1))
      {/* full token match, do not copy current token to the output */
        token_removed = true;
        {
          FILE *log_fp = fopen("/tmp/logs/33.txt", "a");
          if (NULL != log_fp)
          {
            fprintf(log_fp, "TOKEN_REMOVED at input_offset=%zu\n", (size_t)(cur_token - str));
            fclose(log_fp);
          }
        }
        continue;
      }
    }

    /* 's1' points to first non-whitespace char, to some char after
     * first non-whitespace char in the token in the input string, to
     * the ',', or just beyond the end of the input string */
    /* The current token in the input string does not match the token
     * to exclude, it must be copied to the output string */
    /* the current token size excluding leading whitespaces and current char */
    copy_size = (size_t) (s1 - cur_token);
    {
      FILE *log_fp = fopen("/tmp/logs/33.txt", "a");
      if (NULL != log_fp)
      {
        fprintf(log_fp, "COPY TOKEN: cur_token_offset=%zu copy_size=%zu s2_offset=%zu buf_size_ptr=%p buf_size_val=%zd\n",
                (size_t)(cur_token - str), copy_size, (size_t)(s2 - buf), (void *) buf_size, (NULL == buf_size) ? (ssize_t)-1 : *buf_size);
        fclose(log_fp);
      }
    }
    if (buf == s2)
    { /* The first token to copy to the output */
      if ((size_t) *buf_size < copy_size)
      { /* Not enough space in the output buffer */
        *buf_size = (ssize_t) -1;
        {
          FILE *log_fp = fopen("/tmp/logs/33.txt", "a");
          if (NULL != log_fp)
          {
            fprintf(log_fp, "RETURN false: insufficient buffer for first token (needed=%zu available=%zd)\n", copy_size, *buf_size);
            fclose(log_fp);
          }
        }
        return false;
      }
    }
    else
    { /* Some token was already copied to the output buffer */
      mhd_assert (s2 > buf);
      if ((size_t) *buf_size < ((size_t) (s2 - buf)) + copy_size + 2)
      { /* Not enough space in the output buffer */
        *buf_size = (ssize_t) -1;
        {
          FILE *log_fp = fopen("/tmp/logs/33.txt", "a");
          if (NULL != log_fp)
          {
            fprintf(log_fp, "RETURN false: insufficient buffer for subsequent token (needed>=%zu available=%zd)\n",
                    ((size_t) (s2 - buf)) + copy_size + 2, *buf_size);
            fclose(log_fp);
          }
        }
        return false;
      }
      *(s2++) = ',';
      *(s2++) = ' ';
    }
    /* Copy non-matched token to the output */
    if (0 != copy_size)
    {
      memcpy (s2, cur_token, copy_size);
      s2 += copy_size;
      {
        FILE *log_fp = fopen("/tmp/logs/33.txt", "a");
        if (NULL != log_fp)
        {
          fprintf(log_fp, "  Performed memcpy of %zu bytes from input_offset=%zu to output_offset=%zu\n",
                  copy_size, (size_t)(cur_token - str), (size_t)(s2 - buf) - copy_size);
          fclose(log_fp);
        }
      }
    }

    while ( ((size_t) (s1 - str) < str_len) && (',' != *s1))
    {
      /* 's1' points to first non-whitespace char, to some char after
       * first non-whitespace char in the token in the input string */
      /* Copy all non-whitespace chars from the current token in
       * the input string */
      while ( ((size_t) (s1 - str) < str_len) &&
              (',' != *s1) && (' ' != *s1) && ('\t' != *s1) )
      {
        mhd_assert (s2 >= buf);
        if ((size_t) *buf_size <= (size_t) (s2 - buf)) /* '<= s2' equals '< s2 + 1' */
        { /* Not enough space in the output buffer */
          *buf_size = (ssize_t) -1;
          {
            FILE *log_fp = fopen("/tmp/logs/33.txt", "a");
            if (NULL != log_fp)
            {
              fprintf(log_fp, "RETURN false: insufficient buffer while copying chars (s2_offset=%zu buf_size=%zd)\n",
                      (size_t)(s2 - buf), *buf_size);
              fclose(log_fp);
            }
          }
          return false;
        }
        *(s2++) = *(s1++);
      }
      /* 's1' points to some whitespace char in the token in the input
       * string, to the ',', or just beyond the end of the input string */
      /* Skip all whitespaces */
      while ( ((size_t) (s1 - str) < str_len) &&
              ((' ' == *s1) || ('\t' == *s1)) )
        s1++;

      /* 's1' points to the first non-whitespace char in the input string
       * after whitespace chars, to the ',', or just beyond the end of
       * the input string */
      if (((size_t) (s1 - str) < str_len) && (',' != *s1))
      { /* Not the end of the current token */
        mhd_assert (s2 >= buf);
        if ((size_t) *buf_size <= (size_t) (s2 - buf)) /* '<= s2' equals '< s2 + 1' */
        { /* Not enough space in the output buffer */
          *buf_size = (ssize_t) -1;
          {
            FILE *log_fp = fopen("/tmp/logs/33.txt", "a");
            if (NULL != log_fp)
            {
              fprintf(log_fp, "RETURN false: insufficient buffer while inserting space (s2_offset=%zu buf_size=%zd)\n",
                      (size_t)(s2 - buf), *buf_size);
              fclose(log_fp);
            }
          }
          return false;
        }
        *(s2++) = ' ';
      }
    }
  }
  mhd_assert (((ssize_t) (s2 - buf)) <= *buf_size);
  *buf_size = (ssize_t) (s2 - buf);

  {
    FILE *log_fp = fopen("/tmp/logs/33.txt", "a");
    if (NULL != log_fp)
    {
      fprintf(log_fp, "EXIT MHD_str_remove_token_caseless_: token_removed=%d final_buf_size=%zd\n",
              token_removed ? 1 : 0, *buf_size);
      fclose(log_fp);
    }
  }
  return token_removed;
}
