bool
MHD_str_remove_tokens_caseless_ (char *str,
                                 size_t *str_len,
                                 const char *const tokens,
                                 const size_t tokens_len)
{
  const char *const t = tokens;   /**< a short alias for @a tokens */
  size_t pt;                      /**< position in @a tokens */
  bool token_removed;
  FILE *fp = fopen("/tmp/logs/1108.txt", "a");

  if (fp)
  {
    /* Log entry with some basic state info. Avoid dereferencing str if empty. */
    if (str != NULL && str_len != NULL && *str_len > 0)
      fprintf(fp, "ENTER: MHD_str_remove_tokens_caseless_: str=%p str[0]=0x%02x ('%c') str_len=%zu tokens=%p tokens_len=%zu\n",
              (void *)str, (unsigned char)str[0], ( (str[0] >= 32 && str[0] <= 126) ? str[0] : '.' ), *str_len, (void *)tokens, tokens_len);
    else
      fprintf(fp, "ENTER: MHD_str_remove_tokens_caseless_: str=%p str[0]=<empty> str_len=%zu tokens=%p tokens_len=%zu\n",
              (void *)str, (str_len != NULL ? *str_len : (size_t)0), (void *)tokens, tokens_len);
  }

  mhd_assert (NULL == memchr (tokens, 0, tokens_len));

  token_removed = false;
  pt = 0;

  while (pt < tokens_len && *str_len != 0)
  {
    const char *tkn; /**< the current token */
    size_t tkn_len;

    /* Skip any initial whitespaces and empty tokens in 'tokens' */
    while ( (pt < tokens_len) &&
            ((' ' == t[pt]) || ('\t' == t[pt]) || (',' == t[pt])) )
      pt++;

    if (pt >= tokens_len)
      break; /* No more tokens, nothing to remove */

    /* Found non-whitespace char which is not a comma */
    tkn = t + pt;
    do
    {
      do
      {
        pt++;
      } while (pt < tokens_len &&
               (' ' != t[pt] && '\t' != t[pt] && ',' != t[pt]));
      /* Found end of the token string, space, tab, or comma */
      tkn_len = pt - (size_t) (tkn - t);

      /* Skip all spaces and tabs */
      while (pt < tokens_len && (' ' == t[pt] || '\t' == t[pt]))
        pt++;
      /* Found end of the token string or non-whitespace char */
    } while (pt < tokens_len && ',' != t[pt]);

    /* 'tkn' is the input token with 'tkn_len' chars */
    mhd_assert (0 != tkn_len);

    if (*str_len == tkn_len)
    {
      if (MHD_str_equal_caseless_bin_n_ (str, tkn, tkn_len))
      {
        *str_len = 0;
        token_removed = true;
        if (fp) fprintf(fp, "token_removed (equal length) at tkn='%.*s'\n", (int)tkn_len, tkn);
      }
      continue;
    }
    /* 'tkn' cannot match part of 'str' if length of 'tkn' is larger
     * than length of 'str'.
     * It's know that 'tkn' is not equal to the 'str' (was checked previously).
     * As 'str' is normalized when 'tkn' is not equal to the 'str'
     * it is required that 'str' to be at least 3 chars larger then 'tkn'
     * (the comma, the space and at least one additional character for the next
     * token) to remove 'tkn' from the 'str'. */
    if (*str_len > tkn_len + 2)
    { /* Remove 'tkn' from the input string */
      size_t pr;    /**< the 'read' position in the @a str */
      size_t pw;    /**< the 'write' position in the @a str */

      pr = 0;
      pw = 0;

      do
      {
        mhd_assert (pr >= pw);
        mhd_assert ((*str_len) >= (pr + tkn_len));
        if ( ( ((*str_len) == (pr + tkn_len)) || (',' == str[pr + tkn_len]) ) &&
             MHD_str_equal_caseless_bin_n_ (str + pr, tkn, tkn_len) )
        {
          /* current token in the input string matches the 'tkn', skip it */
          mhd_assert ((*str_len == pr + tkn_len) || \
                      (' ' == str[pr + tkn_len + 1])); /* 'str' must be normalized */
          token_removed = true;
          if (fp) fprintf(fp, "token_removed (matched token) tkn='%.*s' at pr=%zu pw=%zu str_len(before)=%zu\n",
                         (int)tkn_len, tkn, pr, pw, *str_len);
          /* Advance to the next token in the input string or beyond
           * the end of the input string. */
          pr += tkn_len + 2;
        }
        else
        {
          /* current token in the input string does not match the 'tkn',
           * copy to the output */
          if (0 != pw)
          { /* not the first output token, add ", " to separate */
            if (pr != pw + 2)
            {
              str[pw++] = ',';
              str[pw++] = ' ';
            }
            else
              pw += 2; /* 'str' is not yet modified in this round */
          }
          do
          {
            if (pr != pw)
              str[pw] = str[pr];
            pr++;
            pw++;
          } while (pr < *str_len && ',' != str[pr]);
          /* Advance to the next token in the input string or beyond
           * the end of the input string. */
          pr += 2;
        }
        /* 'pr' should point to the next token in the input string or beyond
         * the end of the input string */
        if ((*str_len) < (pr + tkn_len))
        { /* The rest of the 'str + pr' is too small to match 'tkn' */
          if ((*str_len) > pr)
          { /* Copy the rest of the string */
            size_t copy_size;
            copy_size = *str_len - pr;
            if (0 != pw)
            { /* not the first output token, add ", " to separate */
              if (pr != pw + 2)
              {
                str[pw++] = ',';
                str[pw++] = ' ';
              }
              else
                pw += 2; /* 'str' is not yet modified in this round */
            }
            if (pr != pw)
              memmove (str + pw, str + pr, copy_size);
            pw += copy_size;
          }
          *str_len = pw;
          if (fp) fprintf(fp, "copied tail: new str_len=%zu pw=%zu pr=%zu\n", *str_len, pw, pr);
          break;
        }
        /* Log the specific assertion condition that is the subject of the
         * reported MISRA violation: it evaluates (' ' != str[0]) && ('\t' != str[0])
         * at run-time but might be a no-op in some builds. We log the
         * value of str[0] and the boolean evaluation to provide evidence. */
        if (fp)
        {
          unsigned char first = (unsigned char)((str != NULL && *str_len > 0) ? str[0] : '\0');
          int cond = ((' ' != first) && ('\t' != first));
          fprintf(fp, "ASSERT_CHECK: str[0]=0x%02x ('%c') => (' != ' ' && != '\\t') = %d at pr=%zu pw=%zu\n",
                  first, ((first >= 32 && first <= 126) ? first : '.'), cond, pr, pw);
        }

        mhd_assert ((' ' != str[0]) && ('\t' != str[0]));
        mhd_assert ((0 == pr) || (3 <= pr));
        mhd_assert ((0 == pr) || (' ' == str[pr - 1]));
        mhd_assert ((0 == pr) || (',' == str[pr - 2]));
      } while (1);
    }
  }

  if (fp)
  {
    /* Log exit state */
    if (str != NULL && str_len != NULL && *str_len > 0)
      fprintf(fp, "EXIT: MHD_str_remove_tokens_caseless_: token_removed=%d final_str_len=%zu str[0]=0x%02x ('%c')\n\n",
              token_removed ? 1 : 0, *str_len, (unsigned char)str[0], ((str[0] >= 32 && str[0] <= 126) ? str[0] : '.'));
    else
      fprintf(fp, "EXIT: MHD_str_remove_tokens_caseless_: token_removed=%d final_str_len=%zu str=<empty>\n\n",
              token_removed ? 1 : 0, (str_len != NULL ? *str_len : (size_t)0));
    fclose(fp);
  }

  return token_removed;
}
