bool
MHD_str_remove_tokens_caseless_ (char *str,
                                 size_t *str_len,
                                 const char *const tokens,
                                 const size_t tokens_len)
{
  const char *const t = tokens;   /**< a short alias for @a tokens */
  size_t pt;                      /**< position in @a tokens */
  bool token_removed;
  FILE *mhd_dbg_log = NULL;

  /* Open log file for append. We intentionally do not change control flow;
     logging is side-effect only for diagnostics. */
  mhd_dbg_log = fopen("/tmp/logs/1108.txt", "a");
  if (mhd_dbg_log)
  {
    fprintf(mhd_dbg_log, "MHD_str_remove_tokens_caseless_: ENTRY str=%p str_len=%zu tokens=%p tokens_len=%zu\n",
            (void *) str, (size_t) *str_len, (void *) tokens, tokens_len);
  }

  mhd_assert (NULL == memchr (tokens, 0, tokens_len));

  token_removed = false;
  pt = 0;

  while (pt < tokens_len && *str_len != 0)
  {
    const char *tkn; /**< the current token */
    size_t tkn_len;

    /* Skip any initial whitespaces and empty tokens in 'tokens' */
    while ( (pt < tokens_len) &&
            ((' ' == t[pt]) || ('\t' == t[pt]) || (',' == t[pt])) )
      pt++;

    if (pt >= tokens_len)
      break; /* No more tokens, nothing to remove */

    /* Found non-whitespace char which is not a comma */
    tkn = t + pt;
    do
    {
      do
      {
        pt++;
      } while (pt < tokens_len &&
               (' ' != t[pt] && '\t' != t[pt] && ',' != t[pt]));
      /* Found end of the token string, space, tab, or comma */
      tkn_len = pt - (size_t) (tkn - t);

      /* Log the parsed token info just after computing its length. This
         helps to reproduce if the invariant (tkn_len != 0) holds or not. */
      if (mhd_dbg_log)
      {
        /* Use %.*s to print non-null-terminated token safely. */
        fprintf(mhd_dbg_log, "Parsed token: tkn_len=%zu pt=%zu token='", tkn_len, pt);
        /* fwrite the token contents then newline */
        if (tkn_len > 0)
          fwrite(tkn, 1, tkn_len, mhd_dbg_log);
        fprintf(mhd_dbg_log, "'\n");
      }

      /* Skip all spaces and tabs */
      while (pt < tokens_len && (' ' == t[pt] || '\t' == t[pt]))
        pt++;
      /* Found end of the token string or non-whitespace char */
    } while (pt < tokens_len && ',' != t[pt]);

    /* 'tkn' is the input token with 'tkn_len' chars */
    /* Log right before the assertion to capture state for the reported
       dead-code assertion scenario. */
    if (mhd_dbg_log)
      fprintf(mhd_dbg_log, "About to assert tkn_len != 0 (tkn_len=%zu)\n", tkn_len);

    mhd_assert (0 != tkn_len);

    /* Log that execution continued past the assertion. If the assertion
       would abort, subsequent logs would not appear which is useful
       diagnostic evidence. */
    if (mhd_dbg_log)
      fprintf(mhd_dbg_log, "Assertion passed / continued execution (tkn_len=%zu)\n", tkn_len);

    if (*str_len == tkn_len)
    {
      if (MHD_str_equal_caseless_bin_n_ (str, tkn, tkn_len))
      {
        *str_len = 0;
        token_removed = true;
        if (mhd_dbg_log)
          fprintf(mhd_dbg_log, "Exact match: set str_len=0 token_removed=true\n");
      }
      continue;
    }
    /* 'tkn' cannot match part of 'str' if length of 'tkn' is larger
     * than length of 'str'.
     * It's know that 'tkn' is not equal to the 'str' (was checked previously).
     * As 'str' is normalized when 'tkn' is not equal to the 'str'
     * it is required that 'str' to be at least 3 chars larger then 'tkn'
     * (the comma, the space and at least one additional character for the next
     * token) to remove 'tkn' from the 'str'. */
    if (*str_len > tkn_len + 2)
    { /* Remove 'tkn' from the input string */
      size_t pr;    /**< the 'read' position in the @a str */
      size_t pw;    /**< the 'write' position in the @a str */

      pr = 0;
      pw = 0;

      if (mhd_dbg_log)
        fprintf(mhd_dbg_log, "Entering removal loop: initial str_len=%zu tkn_len=%zu\n", *str_len, tkn_len);

      do
      {
        mhd_assert (pr >= pw);
        mhd_assert ((*str_len) >= (pr + tkn_len));
        if ( ( ((*str_len) == (pr + tkn_len)) || (',' == str[pr + tkn_len]) ) &&
             MHD_str_equal_caseless_bin_n_ (str + pr, tkn, tkn_len) )
        {
          /* current token in the input string matches the 'tkn', skip it */
          mhd_assert ((*str_len == pr + tkn_len) || \
                      (' ' == str[pr + tkn_len + 1])); /* 'str' must be normalized */
          token_removed = true;
          if (mhd_dbg_log)
            fprintf(mhd_dbg_log, "Matched token at pr=%zu: skipping %zu chars -> pr from %zu to %zu token_removed=true\n",
                    pr, tkn_len, pr, pr + tkn_len + 2);
          /* Advance to the next token in the input string or beyond
           * the end of the input string. */
          pr += tkn_len + 2;
        }
        else
        {
          /* current token in the input string does not match the 'tkn',
           * copy to the output */
          if (mhd_dbg_log)
            fprintf(mhd_dbg_log, "No match at pr=%zu pw=%zu: copying token\n", pr, pw);
          if (0 != pw)
          { /* not the first output token, add ", " to separate */
            if (pr != pw + 2)
            {
              str[pw++] = ',';
              str[pw++] = ' ';
              if (mhd_dbg_log)
                fprintf(mhd_dbg_log, "Inserted separator at pw now=%zu\n", pw);
            }
            else
              pw += 2; /* 'str' is not yet modified in this round */
          }
          do
          {
            if (pr != pw)
              str[pw] = str[pr];
            pr++;
            pw++;
          } while (pr < *str_len && ',' != str[pr]);
          /* Advance to the next token in the input string or beyond
           * the end of the input string. */
          pr += 2;
          if (mhd_dbg_log)
            fprintf(mhd_dbg_log, "After copy: pr=%zu pw=%zu\n", pr, pw);
        }
        /* 'pr' should point to the next token in the input string or beyond
         * the end of the input string */
        if ((*str_len) < (pr + tkn_len))
        { /* The rest of the 'str + pr' is too small to match 'tkn' */
          if ((*str_len) > pr)
          { /* Copy the rest of the string */
            size_t copy_size;
            copy_size = *str_len - pr;
            if (mhd_dbg_log)
              fprintf(mhd_dbg_log, "Remaining tail copy: pr=%zu copy_size=%zu pw=%zu\n", pr, copy_size, pw);
            if (0 != pw)
            { /* not the first output token, add ", " to separate */
              if (pr != pw + 2)
              {
                str[pw++] = ',';
                str[pw++] = ' ';
              }
              else
                pw += 2; /* 'str' is not yet modified in this round */
            }
            if (pr != pw)
              memmove (str + pw, str + pr, copy_size);
            pw += copy_size;
          }
          *str_len = pw;
          if (mhd_dbg_log)
            fprintf(mhd_dbg_log, "Updated str_len after tail copy: %zu token_removed=%d\n", *str_len, token_removed);
          break;
        }
        mhd_assert ((' ' != str[0]) && ('\t' != str[0]));
        mhd_assert ((0 == pr) || (3 <= pr));
        mhd_assert ((0 == pr) || (' ' == str[pr - 1]));
        mhd_assert ((0 == pr) || (',' == str[pr - 2]));
      } while (1);
    }
  }

  if (mhd_dbg_log)
  {
    /* Log final state: token_removed, final str_len and a safe print of str */
    fprintf(mhd_dbg_log, "MHD_str_remove_tokens_caseless_: EXIT token_removed=%d final_str_len=%zu final_str='",
            token_removed ? 1 : 0, (size_t) *str_len);
    if (*str_len > 0)
      fwrite(str, 1, *str_len, mhd_dbg_log);
    fprintf(mhd_dbg_log, "'\n\n");
    fclose(mhd_dbg_log);
  }

  return token_removed;
}
