bool
MHD_str_remove_tokens_caseless_ (char *str,
                                 size_t *str_len,
                                 const char *const tokens,
                                 const size_t tokens_len)
{
#include <stdio.h>
#include <string.h>

  const char *const t = tokens;   /**< a short alias for @a tokens */
  size_t pt;                      /**< position in @a tokens */
  bool token_removed;

  /* Log function entry and initial state */
  {
    FILE *log = fopen("/tmp/logs/1108.txt", "a");
    if (log)
    {
      fprintf(log, "ENTER %s: str=%p, str_len=%zu, tokens=%p, tokens_len=%zu\n",
              __func__, (void *)str, (size_t)(str_len ? *str_len : 0), (void *)tokens, tokens_len);
      /* Print a short prefix of tokens for inspection (not null-terminated) */
      if (tokens_len > 0)
      {
        size_t show = tokens_len > 128 ? 128 : tokens_len;
        fprintf(log, "  tokens[0..%zu]=\'", show);
        fwrite(tokens, 1, show, log);
        fprintf(log, "\'\n");
      }
      fclose(log);
    }
  }

  /* Original assertion: ensure tokens does not contain a zero byte */
  {
    const void *mempos = memchr (tokens, 0, tokens_len);
    FILE *log = fopen("/tmp/logs/1108.txt", "a");
    if (log)
    {
      fprintf(log, "ASSERT_SITE[1]: memchr(tokens,0,tokens_len) -> %s\n",
              mempos ? "FOUND_ZERO" : "NO_ZERO");
      fclose(log);
    }
  }
  mhd_assert (NULL == memchr (tokens, 0, tokens_len));

  token_removed = false;
  pt = 0;

  while (pt < tokens_len && *str_len != 0)
  {
    const char *tkn; /**< the current token */
    size_t tkn_len;

    /* Skip any initial whitespaces and empty tokens in 'tokens' */
    while ( (pt < tokens_len) &&
            ((' ' == t[pt]) || ('\t' == t[pt]) || (',' == t[pt])) )
      pt++;

    if (pt >= tokens_len)
      break; /* No more tokens, nothing to remove */

    /* Found non-whitespace char which is not a comma */
    tkn = t + pt;
    do
    {
      do
      {
        pt++;
      } while (pt < tokens_len &&
               (' ' != t[pt] && '\t' != t[pt] && ',' != t[pt]));
      /* Found end of the token string, space, tab, or comma */
      tkn_len = pt - (size_t) (tkn - t);

      /* Skip all spaces and tabs */
      while (pt < tokens_len && (' ' == t[pt] || '\t' == t[pt]))
        pt++;
      /* Found end of the token string or non-whitespace char */
    } while (pt < tokens_len && ',' != t[pt]);

    /* 'tkn' is the input token with 'tkn_len' chars */
    {
      FILE *log = fopen("/tmp/logs/1108.txt", "a");
      if (log)
      {
        fprintf(log, "ASSERT_SITE[2]: tkn_len=%zu, pt=%zu, tokens_len=%zu\n", tkn_len, pt, tokens_len);
        if (tkn_len > 0)
        {
          size_t show = tkn_len > 128 ? 128 : tkn_len;
          fprintf(log, "  tkn[0..%zu]=\'", show);
          fwrite(tkn, 1, show, log);
          fprintf(log, "\'\n");
        }
        fclose(log);
      }
    }
    mhd_assert (0 != tkn_len);

    if (*str_len == tkn_len)
    {
      if (MHD_str_equal_caseless_bin_n_ (str, tkn, tkn_len))
      {
        *str_len = 0;
        token_removed = true;
        {
          FILE *log = fopen("/tmp/logs/1108.txt", "a");
          if (log)
          {
            fprintf(log, "MATCH_FULL: removed whole string; new str_len=0\n");
            fclose(log);
          }
        }
      }
      continue;
    }
    /* 'tkn' cannot match part of 'str' if length of 'tkn' is larger
     * than length of 'str'.
     * It's know that 'tkn' is not equal to the 'str' (was checked previously).
     * As 'str' is normalized when 'tkn' is not equal to the 'str'
     * it is required that 'str' to be at least 3 chars larger then 'tkn'
     * (the comma, the space and at least one additional character for the next
     * token) to remove 'tkn' from the 'str'. */
    if (*str_len > tkn_len + 2)
    { /* Remove 'tkn' from the input string */
      size_t pr;    /**< the 'read' position in the @a str */
      size_t pw;    /**< the 'write' position in the @a str */

      pr = 0;
      pw = 0;

      do
      {
        {
          FILE *log = fopen("/tmp/logs/1108.txt", "a");
          if (log)
          {
            fprintf(log, "LOOP_ITER: pr=%zu, pw=%zu, str_len=%zu, tkn_len=%zu\n",
                    pr, pw, *str_len, tkn_len);
            fclose(log);
          }
        }
        mhd_assert (pr >= pw);
        {
          FILE *log = fopen("/tmp/logs/1108.txt", "a");
          if (log)
          {
            fprintf(log, "ASSERT_SITE[3]: check (*str_len) >= (pr + tkn_len): %zu >= %zu -> %s\n",
                    *str_len, pr + tkn_len, ((*str_len) >= (pr + tkn_len)) ? "true" : "false");
            fclose(log);
          }
        }
        mhd_assert ((*str_len) >= (pr + tkn_len));
        if ( ( ((*str_len) == (pr + tkn_len)) || (',' == str[pr + tkn_len]) ) &&
             MHD_str_equal_caseless_bin_n_ (str + pr, tkn, tkn_len) )
        {
          /* current token in the input string matches the 'tkn', skip it */
          {
            FILE *log = fopen("/tmp/logs/1108.txt", "a");
            if (log)
            {
              fprintf(log, "MATCH_TOKEN_AT: pr=%zu: token matches tkn -> skipping\n", pr);
              fclose(log);
            }
          }
          mhd_assert ((*str_len == pr + tkn_len) || \
                      (' ' == str[pr + tkn_len + 1])); /* 'str' must be normalized */
          token_removed = true;
          /* Advance to the next token in the input string or beyond
           * the end of the input string. */
          pr += tkn_len + 2;
        }
        else
        {
          /* current token in the input string does not match the 'tkn',
           * copy to the output */
          if (0 != pw)
          { /* not the first output token, add ", " to separate */
            if (pr != pw + 2)
            {
              str[pw++] = ',';
              str[pw++] = ' ';
            }
            else
              pw += 2; /* 'str' is not yet modified in this round */
          }
          do
          {
            if (pr != pw)
              str[pw] = str[pr];
            pr++;
            pw++;
          } while (pr < *str_len && ',' != str[pr]);
          /* Advance to the next token in the input string or beyond
           * the end of the input string. */
          pr += 2;
        }
        /* 'pr' should point to the next token in the input string or beyond
         * the end of the input string */
        if ((*str_len) < (pr + tkn_len))
        { /* The rest of the 'str + pr' is too small to match 'tkn' */
          if ((*str_len) > pr)
          { /* Copy the rest of the string */
            size_t copy_size;
            copy_size = *str_len - pr;
            if (0 != pw)
            { /* not the first output token, add ", " to separate */
              if (pr != pw + 2)
              {
                str[pw++] = ',';
                str[pw++] = ' ';
              }
              else
                pw += 2; /* 'str' is not yet modified in this round */
            }
            if (pr != pw)
              memmove (str + pw, str + pr, copy_size);
            pw += copy_size;
          }
          *str_len = pw;
          {
            FILE *log = fopen("/tmp/logs/1108.txt", "a");
            if (log)
            {
              fprintf(log, "END_COPY_REMAIN: new str_len=%zu, token_removed=%s\n", *str_len,
                      token_removed ? "true" : "false");
              fclose(log);
            }
          }
          break;
        }
        {
          FILE *log = fopen("/tmp/logs/1108.txt", "a");
          if (log)
          {
            fprintf(log, "ASSERT_SITE[4]: (' ' != str[0]) && ('\t' != str[0]) -> %s\n",
                    ((' ' != str[0]) && ('\t' != str[0])) ? "true" : "false");
            fprintf(log, "ASSERT_SITE[5]: (0 == pr) || (3 <= pr) -> %s\n", ((0 == pr) || (3 <= pr)) ? "true" : "false");
            fprintf(log, "ASSERT_SITE[6]: (0 == pr) || (' ' == str[pr - 1]) -> %s\n", ((0 == pr) || (' ' == str[pr - 1])) ? "true" : "false");
            fprintf(log, "ASSERT_SITE[7]: (0 == pr) || (',' == str[pr - 2]) -> %s\n", ((0 == pr) || (',' == str[pr - 2])) ? "true" : "false");
            fclose(log);
          }
        }
        mhd_assert ((' ' != str[0]) && ('\t' != str[0]));
        mhd_assert ((0 == pr) || (3 <= pr));
        mhd_assert ((0 == pr) || (' ' == str[pr - 1]));
        mhd_assert ((0 == pr) || (',' == str[pr - 2]));
      } while (1);
    }
  }

  {
    FILE *log = fopen("/tmp/logs/1108.txt", "a");
    if (log)
    {
      fprintf(log, "EXIT %s: token_removed=%s, final_str_len=%zu\n",
              __func__, token_removed ? "true" : "false", *str_len);
      /* Optionally print resulting string (short prefix) */
      if (*str_len > 0)
      {
        size_t show = *str_len > 256 ? 256 : *str_len;
        fprintf(log, "  result[0..%zu]=\'", show);
        fwrite(str, 1, show, log);
        fprintf(log, "\'\n");
      }
      fclose(log);
    }
  }

  return token_removed;
}
