size_t
MHD_str_to_uint64_ (const char *str,
                    uint64_t *out_val)
{
  const char *const start = str;
  uint64_t res;
  FILE *logf = NULL;

  /* Attempt to open log file for appending; do not fail if logging fails. */
  logf = fopen("/tmp/logs/4532.txt", "a");
  if (logf)
  {
    fprintf(logf, "MHD_str_to_uint64_ ENTER: str=%p, out_val=%p\n", (const void *) str, (void *) out_val);
  }

  if (! str || ! out_val || ! isasciidigit (str[0]))
  {
    if (logf)
    {
      fprintf(logf, "MHD_str_to_uint64_: early return (invalid input). str=%p, out_val=%p, first_char=%d\n",
              (const void *) str, (void *) out_val,
              (str ? (int) (unsigned char) str[0] : -1));
      fclose(logf);
    }
    return 0;
  }

  res = 0;
  if (logf)
    fprintf(logf, "MHD_str_to_uint64_: start loop. start=%p, initial_res=%llu\n",
            (const void *) start, (unsigned long long) res);

  {
    int iter = 0;
    do
    {
      const int digit = (unsigned char) (*str) - '0';
      if (logf)
        fprintf(logf, "iter=%d: str=%p char=%d digit=%d res_before=%llu\n",
                iter, (const void *) str, (int) (unsigned char) (*str), digit,
                (unsigned long long) res);

      if ( (res > (UINT64_MAX / 10)) ||
           ( (res == (UINT64_MAX / 10)) &&
             ((uint64_t) digit > (UINT64_MAX % 10)) ) )
      {
        if (logf)
        {
          fprintf(logf, "MHD_str_to_uint64_: overflow detected at iter=%d res=%llu digit=%d\n",
                  iter, (unsigned long long) res, digit);
          fclose(logf);
        }
        return 0;
      }

      res *= 10;
      res += (unsigned int) digit;
      str++; /* NOTE: This modifies the function parameter 'str' (intended violation to be logged) */

      if (logf)
        fprintf(logf, "iter=%d: res_after=%llu str_now=%p next_char=%d\n",
                iter, (unsigned long long) res, (const void *) str,
                (int) (unsigned char) (*str));

      iter++;
    } while (isasciidigit (*str));
  }

  *out_val = res;
  if (logf)
  {
    size_t len = (size_t) (str - start);
    fprintf(logf, "MHD_str_to_uint64_: exit with value=%llu, length=%zu, final_str=%p\n",
            (unsigned long long) res, len, (const void *) str);
    fclose(logf);
  }

  return (size_t) (str - start);
}
