size_t
MHD_strx_to_uint32_ (const char *str,
                     uint32_t *out_val)
{
  const char *const start = str;
  uint32_t res;
  int digit;
  FILE *log = fopen("/tmp/logs/1332.txt", "a");

  if (log)
  {
    fprintf(log, "MHD_strx_to_uint32_ ENTER: str=%p out_val=%p\n", (void *) str, (void *) out_val);
    fflush(log);
  }

  if (! str || ! out_val)
  {
    if (log)
    {
      fprintf(log, "  NULL parameter(s): str=%p out_val=%p -> return 0\n", (void *) str, (void *) out_val);
      fclose(log);
    }
    return 0;
  }

  /* log initial character/state */
  if (log)
  {
    unsigned char ch = (unsigned char) *str;
    fprintf(log, "  start: start_ptr=%p initial_char=0x%02x (%c)\n",
            (void *) start, (unsigned int) ch,
            (ch >= 32 && ch < 127) ? (char) ch : '.');
    fflush(log);
  }

  res = 0;
  digit = toxdigitvalue (*str);
  while (digit >= 0)
  {
    if (log)
    {
      unsigned char ch = (unsigned char) *str;
      fprintf(log, "  loop: offset=%zu str=%p char=0x%02x (%c) digit=%d res_before=%u\n",
              (size_t) (str - start), (void *) str, (unsigned int) ch,
              (ch >= 32 && ch < 127) ? (char) ch : '.', digit, res);
      fflush(log);
    }

    if ( (res < (UINT32_MAX / 16)) ||
         ((res == (UINT32_MAX / 16)) &&
          ( (uint32_t) digit <= (UINT32_MAX % 16)) ) )
    {
      res *= 16;
      res += (unsigned int) digit;
      if (log)
      {
        fprintf(log, "    accepted: res_after=%u\n", res);
        fflush(log);
      }
    }
    else
    {
      if (log)
      {
        fprintf(log, "    overflow at offset=%zu res=%u digit=%d -> return 0\n",
                (size_t) (str - start), res, digit);
        fclose(log);
      }
      return 0;
    }

    /* Violation scenario: the pointer parameter 'str' is modified here. */
    str++;

    if (log)
    {
      fprintf(log, "    after str++: new_offset=%zu str=%p\n", (size_t) (str - start), (void *) str);
      fflush(log);
    }

    digit = toxdigitvalue (*str);
  }

  if (str - start > 0)
    *out_val = res;

  if (log)
  {
    fprintf(log, "MHD_strx_to_uint32_ EXIT: total_consumed=%zu final_res=%u wrote_out=%d\n",
            (size_t) (str - start), res, (int) ((str - start) > 0));
    fclose(log);
  }

  return (size_t) (str - start);
}
