size_t
MHD_strx_to_uint32_n_ (const char *str,
                       size_t maxlen,
                       uint32_t *out_val)
{
  size_t i;
  uint32_t res;
  int digit;
  FILE *log = fopen("/tmp/logs/1506.txt", "a");

  if (log)
    fprintf(log, "ENTER MHD_strx_to_uint32_n_: str=%p maxlen=%zu out_val=%p\n",
            (void *) str, maxlen, (void *) out_val);

  if (! str || ! out_val)
  {
    if (log)
      fprintf(log, "NULL parameter: str=%p out_val=%p -> return 0\n",
              (void *) str, (void *) out_val);
    if (log)
      fclose(log);
    return 0;
  }

  res = 0;
  i = 0;

  /* Precompute overflow thresholds for logging */
  {
    const uint32_t threshold_div = (UINT32_MAX / 16u);
    const uint32_t threshold_mod = (UINT32_MAX % 16u);
    if (log)
      fprintf(log, "threshold_div=%u threshold_mod=%u\n",
              (unsigned) threshold_div, (unsigned) threshold_mod);
  }

  while (i < maxlen && (digit = toxdigitvalue (str[i])) >= 0)
  {
    if (log)
      fprintf(log, "loop: i=%zu ch=0x%02x digit=%d res_before=%u\n",
              i, (unsigned char) str[i], digit, (unsigned) res);

    if ( (res > (UINT32_MAX / 16u)) ||
         ((res == (UINT32_MAX / 16u)) &&
          ( (uint32_t) digit > (UINT32_MAX % 16u)) ) )
    {
      if (log)
        fprintf(log, "overflow-branch-taken: res=%u digit=%d threshold_div=%u threshold_mod=%u -> return 0\n",
                (unsigned) res, digit,
                (unsigned) (UINT32_MAX / 16u), (unsigned) (UINT32_MAX % 16u));
      if (log)
        fclose(log);
      return 0;
    }

    res *= 16u;
    res += (unsigned int) digit;

    if (log)
      fprintf(log, "after update: res=%u\n", (unsigned) res);

    i++;
  }

  if (i)
  {
    *out_val = res;
    if (log)
      fprintf(log, "parsed %zu digit(s), out_val=%u -> return %zu\n", i, (unsigned) res, i);
  }
  else
  {
    if (log)
      fprintf(log, "no digits parsed (i=0) -> return 0\n");
  }

  if (log)
    fclose(log);

  return i;
}
