size_t
MHD_strx_to_uint32_n_ (const char *str,
                       size_t maxlen,
                       uint32_t *out_val)
{
  size_t i;
  uint32_t res;
  int digit;
  /* Try to append logs about control flow and state to the requested file. */
  FILE *logf = fopen("/tmp/logs/1508.txt", "a");
  if (logf) {
    /* Log entry with pointer values only (safe even if NULL). */
    fprintf(logf, "ENTER: MHD_strx_to_uint32_n_(str=%p, maxlen=%zu, out_val=%p)\n",
            (const void *)str, maxlen, (void *)out_val);
    fflush(logf);
  }

  if (! str || ! out_val)
  {
    if (logf)
    {
      fprintf(logf, "NULL argument detected: str=%p, out_val=%p -> return 0\n",
              (const void *)str, (void *)out_val);
      fclose(logf);
    }
    return 0;
  }

  res = 0;
  i = 0;
  /* Precompute thresholds used in the overflow check so we can log them. */
  const uint32_t thr_div = (UINT32_MAX / 16u);
  const uint32_t thr_mod = (UINT32_MAX % 16u);

  while (i < maxlen && (digit = toxdigitvalue (str[i])) >= 0)
  {
    /* Log current iteration state: index, input char, computed digit, current res, thresholds. */
    if (logf)
    {
      unsigned char uch = (unsigned char) str[i];
      char ch = (uch >= 32 && uch <= 126) ? (char) uch : '.';
      fprintf(logf,
              "ITER: i=%zu, ch='%c'(0x%02x), digit=%d, res=%u, thr_div=%u, thr_mod=%u\n",
              i, ch, (unsigned) uch, digit, res, thr_div, thr_mod);
      fflush(logf);
    }

    /* Evaluate the same overflow condition as the original code, but capture subresults for logging. */
    int cond1 = (res > thr_div) ? 1 : 0;
    int cond2 = ((res == thr_div) && ((uint32_t) digit > thr_mod)) ? 1 : 0;

    if (logf)
    {
      fprintf(logf, "EVAL: (res > thr_div)=%d, (res == thr_div)=%d, ((uint32_t)digit > thr_mod)=%d, combined=%d\n",
              cond1, (res == thr_div) ? 1 : 0,
              ((uint32_t) digit > thr_mod) ? 1 : 0,
              (cond1 || cond2) ? 1 : 0);
      fflush(logf);
    }

    if (cond1 || cond2)
    {
      if (logf)
      {
        fprintf(logf, "BRANCH: overflow detected, returning 0\n");
        fclose(logf);
      }
      return 0;
    }

    res *= 16u;
    res += (unsigned int) digit;
    i++;
  }

  if (i)
    *out_val = res;

  if (logf)
  {
    fprintf(logf, "EXIT: i=%zu, out_val_set=%d, out_val=%u\n",
            i, (i ? 1 : 0), (i ? res : 0u));
    fclose(logf);
  }

  return i;
}
