size_t
MHD_strx_to_uint64_ (const char *str,
                     uint64_t *out_val)
{
  const char *const start = str;
  uint64_t res;
  int digit;
  FILE *logf = NULL;
  int iter = 0;

  /* Try to open the log file; if it fails we proceed without logging. */
  logf = fopen("/tmp/logs/122.txt", "a");
  if (logf)
  {
    fprintf(logf, "ENTER MHD_strx_to_uint64_: str=%p, out_val=%p, start=%p\n",
            (void *) str, (void *) out_val, (void *) start);
    if (str)
      fprintf(logf, "  initial char: '%c' (0x%02x)\n", *str ? *str : '0', (unsigned char) *str);
    else
      fprintf(logf, "  str is NULL\n");
    fflush(logf);
  }

  if (! str || ! out_val)
  {
    if (logf)
    {
      fprintf(logf, "EARLY RETURN: null parameter(s): str=%p, out_val=%p\n",
              (void *) str, (void *) out_val);
      fclose(logf);
    }
    return 0;
  }

  res = 0;
  digit = toxdigitvalue (*str);
  if (logf)
    fprintf(logf, "START PARSE: digit=%d, res=%llu\n", digit, (unsigned long long) res);

  while (digit >= 0)
  {
    if (logf)
    {
      fprintf(logf, "ITER %d: char='%c' (0x%02x), digit=%d, res(before)=%llu, ptr=%p\n",
              iter,
              *str ? *str : '0', (unsigned char) *str,
              digit,
              (unsigned long long) res,
              (void *) str);
    }

    if ( (res < (UINT64_MAX / 16)) ||
         ((res == (UINT64_MAX / 16)) &&
          ( (uint64_t) digit <= (UINT64_MAX % 16)) ) )
    {
      res *= 16;
      res += (unsigned int) digit;
      if (logf)
        fprintf(logf, "  UPDATED res(after)=%llu\n", (unsigned long long) res);
    }
    else
    {
      if (logf)
      {
        fprintf(logf, "  OVERFLOW: res=%llu, digit=%d -> returning 0\n",
                (unsigned long long) res, digit);
        fclose(logf);
      }
      return 0;
    }

    /* Violation reproduction point: parameter pointer is incremented */
    str++;
    if (logf)
      fprintf(logf, "  ADVANCE PTR -> now str=%p, char='%c' (0x%02x)\n",
              (void *) str, *str ? *str : '0', (unsigned char) *str);

    digit = toxdigitvalue (*str);
    if (logf)
      fprintf(logf, "  next digit=%d\n", digit);

    iter++;
  }

  if (logf)
    fprintf(logf, "PARSE COMPLETE: consumed=%zu, res=%llu\n",
            (size_t) (str - start), (unsigned long long) res);

  if (str - start > 0)
  {
    *out_val = res;
    if (logf)
      fprintf(logf, "  ASSIGNED *out_val=%llu\n", (unsigned long long) res);
  }
  else
  {
    if (logf)
      fprintf(logf, "  NO DIGITS CONSUMED -> out_val not assigned\n");
  }

  if (logf)
    fclose(logf);

  return (size_t) (str - start);
}
