size_t
MHD_strx_to_uint64_ (const char *str,
                     uint64_t *out_val)
{
  const char *const start = str;
  uint64_t res;
  int digit;

  /* Probe: entry */
  {
    FILE *logf = fopen("/tmp/logs/122.txt", "a");
    if (logf)
    {
      fprintf(logf, "ENTER MHD_strx_to_uint64_: str=%p, start=%p, out_val=%p\n",
              (const void *) str, (const void *) start, (const void *) out_val);
      fclose(logf);
    }
  }

  if (! str || ! out_val)
  {
    FILE *logf = fopen("/tmp/logs/122.txt", "a");
    if (logf)
    {
      fprintf(logf, "EARLY RETURN: null parameter(s): str=%p, out_val=%p\n",
              (const void *) str, (const void *) out_val);
      fclose(logf);
    }
    return 0;
  }

  res = 0;
  digit = toxdigitvalue (*str);

  /* Log initial state before loop */
  {
    FILE *logf = fopen("/tmp/logs/122.txt", "a");
    if (logf)
    {
      int c = (str && *str) ? (unsigned char) *str : 0;
      fprintf(logf, "LOOP START: pos=%td, *str=0x%02x ('%c'), digit=%d, res=%llu\n",
              (ptrdiff_t) (str - start), c, (c >= 32 && c < 127) ? (char) c : '.', digit,
              (unsigned long long) res);
      fclose(logf);
    }
  }

  while (digit >= 0)
  {
    /* Log check for overflow possibility */
    {
      FILE *logf = fopen("/tmp/logs/122.txt", "a");
      if (logf)
      {
        fprintf(logf, "CHECK: pos=%td, res=%llu, digit=%d\n",
                (ptrdiff_t) (str - start), (unsigned long long) res, digit);
        fclose(logf);
      }
    }

    if ( (res < (UINT64_MAX / 16)) ||
         ((res == (UINT64_MAX / 16)) &&
          ( (uint64_t) digit <= (UINT64_MAX % 16)) ) )
    {
      /* Log before modifying res */
      {
        FILE *logf = fopen("/tmp/logs/122.txt", "a");
        if (logf)
        {
          fprintf(logf, "UPDATE RES: before res=%llu, multiply by 16 and add %d\n",
                  (unsigned long long) res, digit);
          fclose(logf);
        }
      }

      res *= 16;
      res += (unsigned int) digit;

      /* Log after modifying res */
      {
        FILE *logf = fopen("/tmp/logs/122.txt", "a");
        if (logf)
        {
          fprintf(logf, "UPDATED RES: after res=%llu\n",
                  (unsigned long long) res);
          fclose(logf);
        }
      }
    }
    else
    {
      /* Overflow path: log and return 0 as original code */
      FILE *logf = fopen("/tmp/logs/122.txt", "a");
      if (logf)
      {
        fprintf(logf, "OVERFLOW: pos=%td, res=%llu, digit=%d -> returning 0\n",
                (ptrdiff_t) (str - start), (unsigned long long) res, digit);
        fclose(logf);
      }
      return 0;
    }

    /* Log pointer modification (this is the parameter modification flagged by MISRA) */
    {
      FILE *logf = fopen("/tmp/logs/122.txt", "a");
      if (logf)
      {
        fprintf(logf, "MODIFY PARAM: str currently=%p (pos=%td) -> incrementing\n",
                (const void *) str, (ptrdiff_t) (str - start));
        fclose(logf);
      }
    }

    str++;

    /* Log after incrementing the parameter pointer */
    {
      FILE *logf = fopen("/tmp/logs/122.txt", "a");
      if (logf)
      {
        int c = (str && *str) ? (unsigned char) *str : 0;
        fprintf(logf, "AFTER INCREMENT: str=%p (pos=%td), next_char=0x%02x ('%c')\n",
                (const void *) str, (ptrdiff_t) (str - start), c,
                (c >= 32 && c < 127) ? (char) c : '.');
        fclose(logf);
      }
    }

    digit = toxdigitvalue (*str);

    /* Log next digit value */
    {
      FILE *logf = fopen("/tmp/logs/122.txt", "a");
      if (logf)
      {
        fprintf(logf, "NEXT DIGIT: pos=%td, digit=%d\n",
                (ptrdiff_t) (str - start), digit);
        fclose(logf);
      }
    }
  }

  /* Log completion of loop and potential assignment */
  {
    FILE *logf = fopen("/tmp/logs/122.txt", "a");
    if (logf)
    {
      fprintf(logf, "LOOP END: final pos=%td, res=%llu, will_assign=%d\n",
              (ptrdiff_t) (str - start), (unsigned long long) res, (int) ((str - start) > 0));
      fclose(logf);
    }
  }

  if (str - start > 0)
  {
    *out_val = res;

    FILE *logf = fopen("/tmp/logs/122.txt", "a");
    if (logf)
    {
      fprintf(logf, "ASSIGN OUT: out_val=%p <- %llu\n",
              (const void *) out_val, (unsigned long long) res);
      fclose(logf);
    }
  }

  /* Final log and return */
  {
    FILE *logf = fopen("/tmp/logs/122.txt", "a");
    if (logf)
    {
      fprintf(logf, "RETURN: length=%zu\n", (size_t) (str - start));
      fclose(logf);
    }
  }

  return (size_t) (str - start);
}
