size_t
MHD_strx_to_uint64_ (const char *str,
                     uint64_t *out_val)
{
  const char *const start = str;
  uint64_t res;
  int digit;
  FILE *logf = fopen("/tmp/logs/128.txt", "a");

  if (logf)
  {
    fprintf(logf, "MHD_strx_to_uint64_ entered: str=%p, out_val=%p\n",
            (const void *) str, (const void *) out_val);
  }

  if (! str || ! out_val)
  {
    if (logf)
    {
      fprintf(logf, "Invalid argument(s): str=%p, out_val=%p -> returning 0\n",
              (const void *) str, (const void *) out_val);
      fclose(logf);
    }
    return 0;
  }

  res = 0;
  digit = toxdigitvalue (*str);

  if (logf)
    fprintf(logf, "Initial state: *str=%c (0x%02x), digit=%d, res=%llu\n",
            (*str) ? *str : '0', (unsigned int) (unsigned char) *str,
            digit, (unsigned long long) res);

  while (digit >= 0)
  {
    /* Log the checks that determine overflow handling. */
    int check1 = (res < (UINT64_MAX / 16));
    int check2 = (res == (UINT64_MAX / 16));
    int check3 = ((uint64_t) digit <= (UINT64_MAX % 16));

    if (logf)
    {
      fprintf(logf,
              "Loop pos=%td char='%c'(0x%02x) digit=%d res=%llu check1(res<MAX/16)=%d check2(res==MAX/16)=%d check3(digit<=MAX%16)=%d MAX%%16=%llu\n",
              (ptrdiff_t) (str - start), (*str) ? *str : '0', (unsigned int) (unsigned char) *str,
              digit, (unsigned long long) res, check1, check2, check3,
              (unsigned long long) (UINT64_MAX % 16));
    }

    if ( (res < (UINT64_MAX / 16)) ||
         ((res == (UINT64_MAX / 16)) &&
          ( (uint64_t) digit <= (UINT64_MAX % 16)) ) )
    {
      if (logf)
        fprintf(logf, " -> accumulating: res_before=%llu, digit=%d\n",
                (unsigned long long) res, digit);

      res *= 16;
      res += (unsigned int) digit;

      if (logf)
        fprintf(logf, " -> accumulated: res_after=%llu\n",
                (unsigned long long) res);
    }
    else
    {
      if (logf)
      {
        fprintf(logf, " -> overflow detected, returning 0. res=%llu digit=%d\n",
                (unsigned long long) res, digit);
        fclose(logf);
      }
      return 0;
    }

    str++;
    digit = toxdigitvalue (*str);

    if (logf)
      fprintf(logf, " Next char=%c (0x%02x) next_digit=%d\n",
              (*str) ? *str : '0', (unsigned int) (unsigned char) *str, digit);
  }

  if (str - start > 0)
  {
    *out_val = res;
    if (logf)
      fprintf(logf, "Parsed length=%zu, out_val=%llu\n",
              (size_t) (str - start), (unsigned long long) res);
  }
  else
  {
    if (logf)
      fprintf(logf, "No hex digits parsed, returning 0 length.\n");
  }

  if (logf)
  {
    fprintf(logf, "Returning length=%zu\n", (size_t) (str - start));
    fclose(logf);
  }

  return (size_t) (str - start);
}
