size_t
MHD_strx_to_uint64_n_ (const char *str,
                       size_t maxlen,
                       uint64_t *out_val)
{
  size_t i;
  uint64_t res;
  int digit;
  /* Attempt to log control flow and key state into /tmp/logs/1028.txt. */
  {
    FILE *logf = fopen("/tmp/logs/1028.txt", "a");
    if (logf)
    {
      fprintf(logf, "MHD_strx_to_uint64_n_ entered: str=%p maxlen=%zu out_val=%p\n", (void *) str, maxlen, (void *) out_val);
      fclose(logf);
    }
  }

  if (! str || ! out_val)
    return 0;

  res = 0;
  i = 0;
  while (i < maxlen && (digit = toxdigitvalue (str[i])) >= 0)
  {
    /* Before testing overflow, log current state and values used in the check. */
    {
      FILE *logf = fopen("/tmp/logs/1028.txt", "a");
      if (logf)
      {
        uint64_t res_div = (UINT64_MAX / 16u);
        uint64_t res_mod = (UINT64_MAX % 16u);
        int check1 = (res > res_div) ? 1 : 0;
        int check2_part = (res == res_div) ? 1 : 0;
        int check2 = (check2_part && ((uint64_t) digit > res_mod)) ? 1 : 0;
        fprintf(logf,
                "iter=%zu char='%c' (0x%02x) digit=%d res_before=%llu res_div=%llu res_mod=%llu check1=%d (res>res_div) check2_part=%d ((digit>res_mod))=%d combined=%d\n",
                i,
                (str[i] >= 32 && str[i] < 127) ? str[i] : '?',
                (unsigned int) (unsigned char) str[i],
                digit,
                (unsigned long long) res,
                (unsigned long long) res_div,
                (unsigned long long) res_mod,
                check1,
                check2_part,
                (int) (((uint64_t) digit > res_mod) ? 1 : 0),
                check2);
        fclose(logf);
      }
    }

    if ( (res > (UINT64_MAX / 16)) ||
         ((res == (UINT64_MAX / 16)) &&
          ( (uint64_t) digit > (UINT64_MAX % 16)) ) )
    {
      /* Log the overflow decision before returning. */
      FILE *logf = fopen("/tmp/logs/1028.txt", "a");
      if (logf)
      {
        fprintf(logf, "overflow detected at iter=%zu res=%llu digit=%d -> returning 0\n", i, (unsigned long long) res, digit);
        fclose(logf);
      }
      return 0;
    }

    res *= 16;
    res += (unsigned int) digit;

    /* Log new accumulated result after the digit is added. */
    {
      FILE *logf = fopen("/tmp/logs/1028.txt", "a");
      if (logf)
      {
        fprintf(logf, "after add: iter=%zu res_after=%llu\n", i, (unsigned long long) res);
        fclose(logf);
      }
    }

    i++;
  }

  if (i)
  {
    *out_val = res;
    FILE *logf = fopen("/tmp/logs/1028.txt", "a");
    if (logf)
    {
      fprintf(logf, "parsed %zu digits final_value=%llu written to out_val=%p\n", i, (unsigned long long) res, (void *) out_val);
      fclose(logf);
    }
  }
  else
  {
    FILE *logf = fopen("/tmp/logs/1028.txt", "a");
    if (logf)
    {
      fprintf(logf, "no digits parsed, returning 0\n");
      fclose(logf);
    }
  }

  return i;
}
