size_t
MHD_uint16_to_str (uint16_t val,
                   char *buf,
                   size_t buf_size)
{
  char *chr;  /**< pointer to the current printed digit */
  /* The biggest printable number is 65535 */
  uint16_t divisor = UINT16_C (10000);
  int digit;
  FILE *log = fopen("/tmp/logs/712.txt", "a");

  if (log)
  {
    fprintf(log, "ENTER MHD_uint16_to_str: val=%u, buf=%p, buf_size=%zu\n",
            (unsigned) val, (void *) buf, buf_size);
  }

  chr = buf;
  digit = (int) (val / divisor);
  mhd_assert (digit < 10);

  if (log)
  {
    fprintf(log, "initial divisor=%u, digit=%d\n", (unsigned) divisor, digit);
  }

  /* Do not print leading zeros */
  while ((0 == digit) && (1 < divisor))
  {
    divisor /= 10;
    digit = (int) (val / divisor);
    mhd_assert (digit < 10);
    if (log)
    {
      fprintf(log, "skipping leading zero -> divisor=%u, digit=%d\n",
              (unsigned) divisor, digit);
    }
  }

  while (0 != buf_size)
  {
    if (log)
    {
      fprintf(log, "about to write digit '%c' (digit=%d) at buf_size=%zu, chr=%p\n",
              (char) (digit + '0'), digit, buf_size, (void *) chr);
    }

    *chr = (char) ((char) digit + '0');
    chr++;

    /* Violation scenario: modifying function parameter directly */
    buf_size--;

    if (log)
    {
      fprintf(log, "decremented buf_size, new buf_size=%zu\n", buf_size);
    }

    if (1 == divisor)
    {
      size_t ret = (size_t) (chr - buf);
      if (log)
      {
        fprintf(log, "final digit written, returning %zu\n", ret);
        fclose(log);
      }
      return ret;
    }

    val = (uint16_t) (val % divisor);
    divisor /= 10;
    digit = (int) (val / divisor);
    mhd_assert (digit < 10);

    if (log)
    {
      fprintf(log, "next loop: val=%u, divisor=%u, digit=%d, buf_size=%zu\n",
              (unsigned) val, (unsigned) divisor, digit, buf_size);
    }
  }

  if (log)
  {
    fprintf(log, "buffer too small, returning 0\n");
    fclose(log);
  }
  return 0; /* The buffer is too small */
}
