size_t
MHD_uint64_to_str (uint64_t val,
                   char *buf,
                   size_t buf_size)
{
  /* Logging probe: append control flow and state info to /tmp/logs/4152.txt */
  FILE *log = fopen("/tmp/logs/4152.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER MHD_uint64_to_str: val=%llu, buf=%p, buf_size=%zu\n",
            (unsigned long long) val, (void *) buf, buf_size);
  }

  char *chr;  /**< pointer to the current printed digit */
  /* The biggest printable number is 18446744073709551615 */
  uint64_t divisor = UINT64_C (10000000000000000000);
  int digit;

  chr = buf;
  digit = (int) (val / divisor);
  mhd_assert (digit < 10);

  if (log)
    fprintf(log, "initial divisor=%llu, digit=%d\n",
            (unsigned long long) divisor, digit);

  /* Do not print leading zeros */
  while ((0 == digit) && (1 < divisor))
  {
    divisor /= 10;
    digit = (int) (val / divisor);
    mhd_assert (digit < 10);
    if (log)
      fprintf(log, "skip leading zero -> divisor=%llu, digit=%d\n",
              (unsigned long long) divisor, digit);
  }

  while (0 != buf_size)
  {
    if (log)
      fprintf(log, "about to write digit '%c' at chr=%p (buf_size=%zu)\n",
              (char) (digit + '0'), (void *) chr, buf_size);

    *chr = (char) ((char) digit + '0');
    chr++;

    /* NOTE: this intentionally reproduces the original behavior where the
       function parameter 'buf_size' is decremented directly (buf_size--).
       This demonstrates the violation scenario for MISRA C 2023 Rule 17.8. */
    buf_size--;

    if (log)
      fprintf(log, "after decrement buf_size=%zu\n", buf_size);

    if (1 == divisor)
    {
      size_t ret = (size_t) (chr - buf);
      if (log)
        fprintf(log, "returning (end reached) ret=%zu\n", ret);
      if (log)
        fclose(log);
      return ret;
    }
    val %= divisor;
    divisor /= 10;
    digit = (int) (val / divisor);
    mhd_assert (digit < 10);
  }

  if (log)
    fprintf(log, "buffer too small, returning 0\n");
  if (log)
    fclose(log);
  return 0; /* The buffer is too small */
}
