size_t
MHD_uint8_to_str_pad (uint8_t val,
                      uint8_t min_digits,
                      char *buf,
                      size_t buf_size)
{
  size_t pos; /**< the position of the current printed digit */
  int digit;
  FILE *logf = fopen("/tmp/logs/3742.txt", "a");

  if (logf)
  {
    fprintf(logf, "ENTER MHD_uint8_to_str_pad: val=%u, min_digits=%u, buf=%p, buf_size=%zu\n",
            (unsigned) val, (unsigned) min_digits, (void *) buf, buf_size);
  }

  mhd_assert (3 >= min_digits);
  if (0 == buf_size)
  {
    if (logf)
    {
      fprintf(logf, "EARLY RETURN: buf_size == 0 -> return 0\n");
      fclose(logf);
    }
    return 0;
  }

  pos = 0;
  digit = val / 100;
  if (logf)
    fprintf(logf, "After hundreds calc: digit=%d, val=%u, min_digits=%u, pos=%zu\n",
            digit, (unsigned) val, (unsigned) min_digits, pos);

  if (0 == digit)
  {
    if (3 <= min_digits)
    {
      buf[pos++] = '0';
      if (logf)
        fprintf(logf, "Printed leading '0' for hundreds, pos=%zu\n", pos);
    }
  }
  else
  {
    buf[pos++] = (char) ('0' + (char) digit);
    if (logf)
      fprintf(logf, "Printed hundreds digit '%c', pos=%zu\n", buf[pos-1], pos);
    val %= 100;

    if (logf)
      fprintf(logf, "About to modify parameter min_digits: old=%u -> new=2\n", (unsigned) min_digits);
    min_digits = 2; /* parameter is modified here in original code */
    if (logf)
      fprintf(logf, "Modified min_digits, now=%u\n", (unsigned) min_digits);
  }

  if (buf_size <= pos)
  {
    if (logf)
    {
      fprintf(logf, "EARLY RETURN: buf_size <= pos (%zu <= %zu) -> return 0\n", buf_size, pos);
      fclose(logf);
    }
    return 0;
  }
  digit = val / 10;
  if (logf)
    fprintf(logf, "After tens calc: digit=%d, val=%u, min_digits=%u, pos=%zu\n",
            digit, (unsigned) val, (unsigned) min_digits, pos);

  if (0 == digit)
  {
    if (2 <= min_digits)
    {
      buf[pos++] = '0';
      if (logf)
        fprintf(logf, "Printed leading '0' for tens, pos=%zu\n", pos);
    }
  }
  else
  {
    buf[pos++] = (char) ('0' + (char) digit);
    if (logf)
      fprintf(logf, "Printed tens digit '%c', pos=%zu\n", buf[pos-1], pos);
    val %= 10;
  }

  if (buf_size <= pos)
  {
    if (logf)
    {
      fprintf(logf, "EARLY RETURN: buf_size <= pos (%zu <= %zu) before units -> return 0\n", buf_size, pos);
      fclose(logf);
    }
    return 0;
  }
  buf[pos++] = (char) ('0' + (char) val);
  if (logf)
    fprintf(logf, "Printed units digit '%c', final pos=%zu\n", buf[pos-1], pos);

  if (logf)
  {
    fprintf(logf, "EXIT MHD_uint8_to_str_pad: return %zu\n", pos);
    fclose(logf);
  }
  return pos;
}
