size_t
MHD_uint8_to_str_pad (uint8_t val,
                      uint8_t min_digits,
                      char *buf,
                      size_t buf_size)
{
  size_t pos; /**< the position of the current printed digit */
  int digit;
  FILE *logf = NULL;

  /* Basic sanity check preserved from original implementation */
  mhd_assert (3 >= min_digits);

  /* Open log file for append. If opening fails, proceed without logging. */
  logf = fopen("/tmp/logs/3742.txt", "a");
  if (logf)
  {
    fprintf(logf, "ENTER MHD_uint8_to_str_pad: val=%u min_digits=%u buf=%p buf_size=%zu\n",
            (unsigned) val, (unsigned) min_digits, (void *) buf, buf_size);
  }

  if (0 == buf_size)
  {
    if (logf)
    {
      fprintf(logf, "RET early (buf_size==0): 0\n");
      fclose(logf);
    }
    return 0;
  }

  pos = 0;
  digit = val / 100;
  if (logf)
    fprintf(logf, "Computed hundreds digit: %d (val=%u)\n", digit, (unsigned) val);

  if (0 == digit)
  {
    if (3 <= min_digits)
    {
      buf[pos++] = '0';
      if (logf)
        fprintf(logf, "Wrote leading '0' for hundreds, pos=%zu\n", pos);
    }
  }
  else
  {
    buf[pos++] = (char) ('0' + (char) digit);
    if (logf)
      fprintf(logf, "Wrote hundreds digit '%c', pos=%zu\n", (char)('0' + (char)digit), pos);

    /* Violation scenario: modifying the scalar function parameter 'val' as in original code. */
    if (logf)
      fprintf(logf, "About to modify parameter val (before): %u\n", (unsigned) val);
    val %= 100;
    if (logf)
      fprintf(logf, "Modified parameter val (after val %= 100): %u\n", (unsigned) val);

    /* As original code, set min_digits for remaining digits */
    min_digits = 2;
    if (logf)
      fprintf(logf, "Set min_digits=%u for remaining digits\n", (unsigned) min_digits);
  }

  if (buf_size <= pos)
  {
    if (logf)
    {
      fprintf(logf, "RET early (buf_size<=pos) after hundreds handling: 0, pos=%zu\n", pos);
      fclose(logf);
    }
    return 0;
  }

  digit = val / 10;
  if (logf)
    fprintf(logf, "Computed tens digit: %d (val=%u)\n", digit, (unsigned) val);

  if (0 == digit)
  {
    if (2 <= min_digits)
    {
      buf[pos++] = '0';
      if (logf)
        fprintf(logf, "Wrote leading '0' for tens, pos=%zu\n", pos);
    }
  }
  else
  {
    buf[pos++] = (char) ('0' + (char) digit);
    if (logf)
      fprintf(logf, "Wrote tens digit '%c', pos=%zu\n", (char)('0' + (char)digit), pos);

    if (logf)
      fprintf(logf, "About to modify parameter val (before): %u\n", (unsigned) val);
    val %= 10;
    if (logf)
      fprintf(logf, "Modified parameter val (after val %= 10): %u\n", (unsigned) val);
  }

  if (buf_size <= pos)
  {
    if (logf)
    {
      fprintf(logf, "RET early (buf_size<=pos) after tens handling: 0, pos=%zu\n", pos);
      fclose(logf);
    }
    return 0;
  }

  buf[pos++] = (char) ('0' + (char) val);
  if (logf)
    fprintf(logf, "Wrote units digit '%c', final pos=%zu\n", (char)('0' + (char)val), pos);

  if (logf)
  {
    fprintf(logf, "EXIT MHD_uint8_to_str_pad: returning pos=%zu\n", pos);
    fclose(logf);
  }

  return pos;
}
