static bool
add_response_entry_n (struct MHD_Response *response,
                      enum MHD_ValueKind kind,
                      const char *header,
                      size_t header_len,
                      const char *content,
                      size_t content_len)
{
    /* Log entry and parameters. Keep logging lightweight and avoid dereferencing
       header/content pointers to not change behavior in presence of NULL. */
    {
        FILE *log = fopen("/tmp/logs/923.txt", "a");
        if (log) {
            fprintf(log, "ENTER add_response_entry_n: response=%p kind=%d header=%p header_len=%zu content=%p content_len=%zu\n",
                    (void *)response, (int)kind, (const void *)header, header_len,
                    (const void *)content, content_len);
            fclose(log);
        }
    }

    if (NULL == response) {
        FILE *log = fopen("/tmp/logs/923.txt", "a");
        if (log) {
            fprintf(log, "EXIT add_response_entry_n: response is NULL -> return false\n");
            fclose(log);
        }
        return false;
    }
    if (0 == header_len) {
        FILE *log = fopen("/tmp/logs/923.txt", "a");
        if (log) {
            fprintf(log, "EXIT add_response_entry_n: header_len == 0 -> return false\n");
            fclose(log);
        }
        return false;
    }
    if (0 == content_len) {
        FILE *log = fopen("/tmp/logs/923.txt", "a");
        if (log) {
            fprintf(log, "EXIT add_response_entry_n: content_len == 0 -> return false\n");
            fclose(log);
        }
        return false;
    }

    /* Before calling memchr, log the operation and parameters. Do not attempt
       to read through header/content; just log the pointer and length so a
       reproducer can observe NULL with non-zero length leading to a crash. */
    {
        FILE *log = fopen("/tmp/logs/923.txt", "a");
        if (log) {
            fprintf(log, "CHECK memchr(header, '\t', header_len): header=%p header_len=%zu\n",
                    (const void *)header, header_len);
            fclose(log);
        }
    }
    if (NULL != memchr (header, '\t', header_len)) {
        FILE *log = fopen("/tmp/logs/923.txt", "a");
        if (log) {
            fprintf(log, "EXIT add_response_entry_n: header contains '\t' -> return false\n");
            fclose(log);
        }
        return false;
    }

    {
        FILE *log = fopen("/tmp/logs/923.txt", "a");
        if (log) {
            fprintf(log, "CHECK memchr(header, ' ', header_len): header=%p header_len=%zu\n",
                    (const void *)header, header_len);
            fclose(log);
        }
    }
    if (NULL != memchr (header, ' ', header_len)) {
        FILE *log = fopen("/tmp/logs/923.txt", "a");
        if (log) {
            fprintf(log, "EXIT add_response_entry_n: header contains ' ' -> return false\n");
            fclose(log);
        }
        return false;
    }

    {
        FILE *log = fopen("/tmp/logs/923.txt", "a");
        if (log) {
            fprintf(log, "CHECK memchr(header, '\r', header_len): header=%p header_len=%zu\n",
                    (const void *)header, header_len);
            fclose(log);
        }
    }
    if (NULL != memchr (header, '\r', header_len)) {
        FILE *log = fopen("/tmp/logs/923.txt", "a");
        if (log) {
            fprintf(log, "EXIT add_response_entry_n: header contains '\r' -> return false\n");
            fclose(log);
        }
        return false;
    }

    {
        FILE *log = fopen("/tmp/logs/923.txt", "a");
        if (log) {
            fprintf(log, "CHECK memchr(header, '\n', header_len): header=%p header_len=%zu\n",
                    (const void *)header, header_len);
            fclose(log);
        }
    }
    if (NULL != memchr (header, '\n', header_len)) {
        FILE *log = fopen("/tmp/logs/923.txt", "a");
        if (log) {
            fprintf(log, "EXIT add_response_entry_n: header contains '\n' -> return false\n");
            fclose(log);
        }
        return false;
    }

    /* Log checks on content before calling memchr. If content is NULL and
       content_len > 0 this will reproduce the original crash when memchr is
       called; the earlier log entries will provide evidence of the pointer
       and lengths passed in. */
    {
        FILE *log = fopen("/tmp/logs/923.txt", "a");
        if (log) {
            fprintf(log, "CHECK memchr(content, '\r', content_len): content=%p content_len=%zu\n",
                    (const void *)content, content_len);
            fclose(log);
        }
    }
    if (NULL != memchr (content, '\r', content_len)) {
        FILE *log = fopen("/tmp/logs/923.txt", "a");
        if (log) {
            fprintf(log, "EXIT add_response_entry_n: content contains '\r' -> return false\n");
            fclose(log);
        }
        return false;
    }

    {
        FILE *log = fopen("/tmp/logs/923.txt", "a");
        if (log) {
            fprintf(log, "CHECK memchr(content, '\n', content_len): content=%p content_len=%zu\n",
                    (const void *)content, content_len);
            fclose(log);
        }
    }
    if (NULL != memchr (content, '\n', content_len)) {
        FILE *log = fopen("/tmp/logs/923.txt", "a");
        if (log) {
            fprintf(log, "EXIT add_response_entry_n: content contains '\n' -> return false\n");
            fclose(log);
        }
        return false;
    }

    FILE *log = fopen("/tmp/logs/923.txt", "a");
    if (log) {
        fprintf(log, "PROCEED to MHD_add_response_entry_no_check_ -> calling underlying function\n");
        fclose(log);
    }

    return MHD_add_response_entry_no_check_ (response, kind, header, header_len,
                                             content, content_len);
}
