static bool
add_response_entry_n (struct MHD_Response *response,
                      enum MHD_ValueKind kind,
                      const char *header,
                      size_t header_len,
                      const char *content,
                      size_t content_len)
{
  FILE *logf = fopen("/tmp/logs/923.txt", "a");
  if (logf) {
    fprintf(logf, "enter add_response_entry_n: response=%p kind=%d header=%p header_len=%zu content=%p content_len=%zu\n",
            (void *)response, (int)kind, (void *)header, header_len, (void *)content, content_len);
    fflush(logf);
  }

  if (NULL == response) {
    if (logf) {
      fprintf(logf, "early return: response == NULL\n");
      fclose(logf);
    }
    return false;
  }
  if (0 == header_len) {
    if (logf) {
      fprintf(logf, "early return: header_len == 0\n");
      fclose(logf);
    }
    return false;
  }
  if (0 == content_len) {
    if (logf) {
      fprintf(logf, "early return: content_len == 0\n");
      fclose(logf);
    }
    return false;
  }

  /* Check header for forbidden characters, logging each check and result. */
  if (logf) fprintf(logf, "checking header bytes: header=%p len=%zu for '\\t'\n", (void *)header, header_len);
  const void *hdr_ret = memchr (header, '\t', header_len);
  if (hdr_ret != NULL) {
    if (logf) {
      fprintf(logf, "header contains '\\t' at %p\n", hdr_ret);
      fclose(logf);
    }
    return false;
  } else if (logf) {
    fprintf(logf, "header does not contain '\\t'\n");
  }

  if (logf) fprintf(logf, "checking header bytes for ' ' (space)\n");
  hdr_ret = memchr (header, ' ', header_len);
  if (hdr_ret != NULL) {
    if (logf) {
      fprintf(logf, "header contains ' ' at %p\n", hdr_ret);
      fclose(logf);
    }
    return false;
  } else if (logf) {
    fprintf(logf, "header does not contain ' ' (space)\n");
  }

  if (logf) fprintf(logf, "checking header bytes for '\\r'\n");
  hdr_ret = memchr (header, '\r', header_len);
  if (hdr_ret != NULL) {
    if (logf) {
      fprintf(logf, "header contains '\\r' at %p\n", hdr_ret);
      fclose(logf);
    }
    return false;
  } else if (logf) {
    fprintf(logf, "header does not contain '\\r'\n");
  }

  if (logf) fprintf(logf, "checking header bytes for '\\n'\n");
  hdr_ret = memchr (header, '\n', header_len);
  if (hdr_ret != NULL) {
    if (logf) {
      fprintf(logf, "header contains '\\n' at %p\n", hdr_ret);
      fclose(logf);
    }
    return false;
  } else if (logf) {
    fprintf(logf, "header does not contain '\\n'\n");
  }

  /* Check content for '\r' and '\n' with logs. Note: content may be NULL; logging prints pointer but does not dereference. */
  if (logf) fprintf(logf, "about to check content bytes for '\\r': content=%p len=%zu\n", (void *)content, content_len);
  const void *cont_ret = memchr (content, '\r', content_len);
  if (cont_ret != NULL) {
    if (logf) {
      fprintf(logf, "content contains '\\r' at %p\n", cont_ret);
      fclose(logf);
    }
    return false;
  } else if (logf) {
    fprintf(logf, "content does not contain '\\r'\n");
  }

  if (logf) fprintf(logf, "about to check content bytes for '\\n': content=%p len=%zu\n", (void *)content, content_len);
  cont_ret = memchr (content, '\n', content_len);
  if (cont_ret != NULL) {
    if (logf) {
      fprintf(logf, "content contains '\\n' at %p\n", cont_ret);
      fclose(logf);
    }
    return false;
  } else if (logf) {
    fprintf(logf, "content does not contain '\\n'\n");
  }

  if (logf) {
    fprintf(logf, "calling MHD_add_response_entry_no_check_ with response=%p kind=%d header=%p header_len=%zu content=%p content_len=%zu\n",
            (void *)response, (int)kind, (void *)header, header_len, (void *)content, content_len);
    fflush(logf);
  }

  bool result = MHD_add_response_entry_no_check_ (response, kind, header, header_len,
                                                   content, content_len);

  if (logf) {
    fprintf(logf, "MHD_add_response_entry_no_check_ returned %d\n", (int)result);
    fclose(logf);
  }

  return result;
}
